DECLARE
    v_src_part BYTEA;
    v_trg_part BYTEA;
    v_amount INTEGER;
BEGIN
    -- Checking input arguments
    IF (amount < 1 OR
        offset_1 < 1 OR
        offset_2 < 1)
    THEN
        RETURN NULL;
    END IF;

    -- NUMERIC -> INTEGER DBMS_LOB.LOBMAXSIZE workaround
    v_amount := CASE amount
                   WHEN aws_oracle_ext.dbms_lob$lobmaxsize()
                   THEN 2147483647 -- Maximum positive value for data type INTEGER
                   ELSE amount
                END;

    -- Constructing first (source) part for upcoming comparison
    v_src_part := substr(lob_1, floor(offset_1)::INTEGER, v_amount);

    -- Constructing second (target) part for upcoming comparison
    v_trg_part := substr(lob_2, floor(offset_2)::INTEGER, v_amount);

    -- Return -1 if the source piece is less than the second, and 1 if it is greater.
    RETURN CASE /* 0 if the comparison succeeds */
              WHEN (v_src_part = v_trg_part) THEN 0
              WHEN (v_src_part > v_trg_part) THEN 1
              ELSE -1
           END;
END;

DECLARE
    v_src_part TEXT;
    v_trg_part TEXT;
    v_amount INTEGER;
BEGIN
    -- Checking input arguments
    IF (amount < 1 OR
        offset_1 < 1 OR
        offset_2 < 1)
    THEN
        RETURN NULL;
    END IF;

    -- NUMERIC -> INTEGER DBMS_LOB.LOBMAXSIZE workaround
    v_amount := CASE amount
                   WHEN aws_oracle_ext.dbms_lob$lobmaxsize()
                   THEN 2147483647 -- Maximum positive value for data type INTEGER
                   ELSE amount
                END;

    -- Constructing first (source) part for upcoming comparison
    v_src_part := substr(lob_1, floor(offset_1)::INTEGER, v_amount);

    -- Constructing second (target) part for upcoming comparison
    v_trg_part := substr(lob_2, floor(offset_2)::INTEGER, v_amount);

    -- Return -1 if the source piece is less than the second, and 1 if it is greater.
    RETURN CASE /* 0 if the comparison succeeds */
              WHEN (v_src_part = v_trg_part) THEN 0
              WHEN (v_src_part > v_trg_part) THEN 1
              ELSE -1
           END;
END;

DECLARE
    v_loc_len INTEGER;
    v_pat_len INTEGER;
    v_pos_idx NUMERIC;
    v_offset INTEGER;
    v_nth_occur NUMERIC;
    v_obj_offset INTEGER;
    v_occur_num NUMERIC := 0;
BEGIN
    -- Determining pattern length (bytes)
    v_pat_len := octet_length(pattern);

    -- Checking input arguments
    IF (v_pat_len = 0 OR
        "offset" < 1 OR
        nth < 1)
    THEN
        RETURN NULL;
    END IF;

    -- Extracting integer part of offset
    v_obj_offset := floor("offset");

    -- Extracting integer part of occurrence
    v_nth_occur := floor(nth);

    -- Using the "easy way" approach by default
    IF (v_obj_offset = 1 AND v_nth_occur = 1)
    THEN
        RETURN position(pattern in lob_loc);
    ELSE
        -- Determining object length (bytes)
        v_loc_len := octet_length(lob_loc);

        -- Calculating the index of the first occurrence
        v_pos_idx := v_obj_offset - 1 + position(pattern in substr(lob_loc, v_obj_offset));

        WHILE (v_pos_idx BETWEEN 1 AND v_loc_len)
        LOOP
            v_occur_num := v_occur_num + 1;

            IF (v_occur_num = v_nth_occur)
            THEN
                RETURN v_pos_idx;
            END IF;

            v_offset := v_obj_offset + v_pos_idx + v_pat_len;

            -- Calculating the index of the next occurrence
            v_pos_idx := v_offset - 1 + position(pattern in substr(lob_loc, v_offset));
        END LOOP;

        RETURN 0; -- Zero, if there are no occurrences
    END IF;
END;

DECLARE
    v_lob_loc TEXT;
    v_pattern TEXT;
    v_loc_len INTEGER;
    v_pat_len INTEGER;
    v_pos_idx INTEGER;
    v_offset INTEGER;
    v_nth_occur NUMERIC;
    v_obj_offset INTEGER;
    v_occur_num NUMERIC := 0;
BEGIN
    -- Determining pattern length (characters)
    v_pat_len := char_length(pattern);

    -- Checking input arguments
    IF (v_pat_len = 0 OR
        "offset" < 1 OR
        nth < 1)
    THEN
        RETURN NULL;
    END IF;

    -- Extracting integer part of offset
    v_obj_offset := floor("offset");

    -- Extracting integer part of occurrence
    v_nth_occur := floor(nth);

    -- Replacing CR + LF to LF in lob_loc
    v_lob_loc := regexp_replace(lob_loc, '\r\n', chr(10), 'g');

    -- Replacing CR + LF to LF in pattern
    v_pattern := regexp_replace(pattern, '\r\n', chr(10), 'g');

    -- Using the "easy way" approach by default
    IF (v_obj_offset = 1 AND v_nth_occur = 1)
    THEN
        RETURN strpos(v_lob_loc, v_pattern);
    ELSE
        -- Determining object length (characters)
        v_loc_len := char_length(v_lob_loc);

        -- Calculating the index of the first occurrence
        v_pos_idx := v_obj_offset - 1 + strpos(substr(v_lob_loc, v_obj_offset), v_pattern);

        WHILE (v_pos_idx BETWEEN 1 AND v_loc_len)
        LOOP
            v_occur_num := v_occur_num + 1;

            IF (v_occur_num = v_nth_occur)
            THEN
                RETURN v_pos_idx;
            END IF;

            v_offset := v_obj_offset + v_pos_idx + v_pat_len;

            -- Calculating the index of the next occurrence
            v_pos_idx := v_offset - 1 + strpos(substr(v_lob_loc, v_offset), v_pattern);
        END LOOP;

        RETURN 0; -- Zero, if there are no occurrences
    END IF;
END;

DECLARE
    v_amount NUMERIC;
BEGIN
    -- Extracting integer part of amount
    v_amount := floor(amount);

    RETURN CASE
              WHEN (v_amount BETWEEN 1 AND 32767 AND
                    "offset" >= 1)
              THEN substr(lob_loc, floor("offset")::INTEGER, v_amount::INTEGER)
           END;
END;

DECLARE
    v_amount NUMERIC;
BEGIN
    -- Extracting integer part of amount
    v_amount := floor(amount);

    RETURN CASE
              WHEN (v_amount BETWEEN 1 AND 32767 AND
                    "offset" >= 1)
              THEN substr(lob_loc, floor("offset")::INTEGER, v_amount::INTEGER)
           END;
END;

BEGIN
    lockhandle := lower(lockname);
END;

declare
  l_unlock boolean := true;
  l_exit_code int;
BEGIN
    if aws_oracle_ext.get_package_variable('aws_oracle_ext', 'dbms_lock', concat('lockmode_',id))::INTEGER=6 /* lockmode: X_MODE = Exclusive */ then
      l_unlock := pg_advisory_unlock(id);
    else
      l_unlock := pg_advisory_unlock_shared(id);
    end if;

    l_exit_code := case when l_unlock then 0 -- Success
                        else 5 -- Illegal lock handle
                end;
    return l_exit_code;
END;

declare
  l_getlock boolean := true;
  l_result  int;
BEGIN
    /* lockmode is implemented only for values
     *   6 - Exclusive
     *   not 6 - Shared
     *
     * timeout is implemented only for values
     *   0 - no wait
     *   not 0 - wait untill free
     */
    perform aws_oracle_ext.set_package_variable('aws_oracle_ext', 'dbms_lock', concat('lockmode_', id), lockmode);

    case when not release_on_commit and lockmode = 6 and timeout = 0 then l_getlock := pg_try_advisory_lock(id);
         when not release_on_commit and lockmode = 6 and timeout != 0 then perform pg_advisory_lock(id);
         when not release_on_commit and lockmode != 6 and timeout = 0 then l_getlock := pg_try_advisory_lock_shared(id);
         when not release_on_commit and lockmode != 6 and timeout != 0 then perform pg_advisory_lock_shared(id);
         when release_on_commit and lockmode = 6 and timeout = 0 then l_getlock := pg_try_advisory_xact_lock(id);
         when release_on_commit and lockmode = 6 and timeout != 0 then perform pg_advisory_xact_lock(id);
         when release_on_commit and lockmode != 6 and timeout = 0 then l_getlock := pg_try_advisory_xact_lock_shared(id);
         when release_on_commit and lockmode != 6 and timeout != 0 then perform pg_advisory_xact_lock_shared(id);
         else l_getlock := false;
    end case;

    l_result := case when l_getlock then 0 -- Success
                     else 1 -- Timeout
                end;
    return l_result;
END;

BEGIN
  return decrypt(input, key, 'des-cbc/pad:none');
END;

BEGIN
  decrypted_data := decrypt(input, key, 'des-cbc/pad:none');
END;

BEGIN
  return encrypt(input, key, 'des-cbc/pad:none');
END;

BEGIN
  encrypted_data := encrypt(input, key, 'des-cbc/pad:none');
END;

BEGIN
  return crypt(seed, gen_salt('des'));
END;

BEGIN
  key := crypt(seed, gen_salt('des'));
END;

BEGIN
  return md5(input);
END;

BEGIN
  checksum := md5(input);
END;

BEGIN
  return md5(input_string);
END;

BEGIN
  checksum_string := md5(input_string);
END;

BEGIN
    PERFORM aws_oracle_ext.dbms_random$seed(p_seed);
END;

DECLARE
    v_u DOUBLE PRECISION;
    v_v DOUBLE PRECISION;
    v_s DOUBLE PRECISION;
BEGIN
    LOOP
        -- Range -1.0 <= u | v < 1.0
        v_u = random() * 2 - 1;
        v_v = random() * 2 - 1;
        v_s = power(v_u, 2) + power(v_v, 2);

        CONTINUE WHEN NOT (v_s != 0 AND v_s < 1);
        RETURN sqrt(-2 * ln(v_s) / v_s) * v_u;
    END LOOP;
END;

BEGIN
    RETURN round((random() * 4294967296 - 2147483648)::NUMERIC, 0);
END;

DECLARE
    v_seedsign INTEGER := sign(p_seed);
    v_seedstr VARCHAR := rpad(to_char(abs(p_seed), 'FM9999999999'), 15, '0');
BEGIN
    PERFORM setseed(to_number(concat('0.', v_seedstr), '9.FM999999999999999999') * v_seedsign);
END;

DECLARE
    v_seedstr TEXT := substring(translate(upper(md5(p_seed)), 'ABCDEF', '123456'), 1, 15);
BEGIN
    PERFORM setseed(to_number(concat('0.', v_seedstr), '9.FM999999999999999999'));
END;

BEGIN
    RETURN aws_oracle_ext.dbms_random$string(opt => opt,
                                             len => len::NUMERIC);
END;

DECLARE
    v_ccs TEXT;
    v_res_val TEXT := '';
BEGIN
    IF (char_length(opt) > 1)
    THEN
        -- The string type to be generated (opt) must be less than or equal to one character.
        RAISE USING MESSAGE := 'Numeric or value error: character string buffer too small.',
                    DETAIL := 'The string type to be generated (opt) exceeds one character.',
                    HINT := '-6502';
    ELSIF (len IS NULL)
    THEN
        -- The length of the returned string (len) must not be null.
        RAISE USING MESSAGE := 'Numeric or value error.',
                    DETAIL := 'The length of the returned string (len) must not be null.',
                    HINT := '-6502';
    ELSIF (len::INTEGER < 1)
    THEN
        RETURN null;
    END IF;

    v_ccs := -- A subset of candidate characters to use as a source for generating random strings.
        CASE lower(opt)
           WHEN 'u' THEN 'ABCDEFGHIJKLMNOPQRSTUVWXYZ' -- Upper case alpha characters only.
           WHEN 'l' THEN 'abcdefghijklmnopqrstuvwxyz' -- Lower case alpha characters only.
           WHEN 'a' THEN 'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ' -- Alpha characters only (mixed case).
           WHEN 'x' THEN '0123456789ABCDEFGHIJKLMNOPQRSTUVWXYZ' -- Any alpha-numeric characters (upper).
           WHEN 'p' THEN ' !"#$%&''()*+,-./0123456789:;<=>?@ABCDEFGHIJKLMNOPQRSTUVWXYZ[\]^_`abcdefghijklmnopqrstuvwxyz{|}~' -- Any printable char (ASCII subset).
           ELSE 'ABCDEFGHIJKLMNOPQRSTUVWXYZ' -- Upper case alpha characters only (default).
        END;

    -- Generating random string of the specified length.
    FOR i IN 1..least(len, 4000)
    LOOP
        v_res_val := v_res_val || substring(v_ccs from (ceil(random() * char_length(v_ccs)))::INTEGER for 1);
    END LOOP;

    RETURN v_res_val;
END;

BEGIN
    PERFORM setseed(random());
END;

BEGIN
    RETURN random() * (p_high - p_start) + p_start;
END;

declare
  l_namespace text;
  l_attribute text;
begin	
   l_namespace := aws_oracle_ext.format_identifier_case(namespace);
   l_attribute := aws_oracle_ext.format_identifier_case(attribute);

   if l_namespace is null then 
   		raise exception 'input value for argument namespace is not valid';    
   end if;

  if l_namespace = 'CLIENTCONTEXT' then
    raise exception 'CLIENTCONTEXT namespace will be cleared automatically in the end of the session. Can not be cleared manually';    
  end if;

  case aws_oracle_ext.get_context_type(l_namespace) 
    when 'ACCESSED GLOBALLY' then 
    	delete from aws_oracle_context.context_global cg
    	where cg.context_namespace = l_namespace
    	and coalesce(cg.cliend_id, '') = coalesce($2, '')
    	and cg.attribute_name = coalesce(l_attribute, cg.attribute_name);         		        	
    when 'ACCESSED LOCALLY' then 
    	delete from context_local where context_namespace = l_namespace and coalesce(attribute_name, '') = coalesce(l_attribute, '');
    else raise exception 'Unsupported context type';   
  end case;

end;

begin
  perform aws_oracle_ext.set_extpack_variable
    (
	  variable_name => 'CLIENT_IDENTIFIER', 
	  variable_value => null::text
	);
END;

BEGIN
  return exists (select 1 from information_schema.enabled_roles where role_name = rolename);
END;

BEGIN
  return ((select state from pg_stat_activity where pid = uniqueid) = 'active'::text);
END;

DECLARE
  l_key text;
  l_context_type text; 
  l_crypted text;
  l_interval text;
  l_namespace text;
  l_attribute text;
BEGIN
  if namespace is null or attribute is null then 
    raise exception 'invalid input values';  
  end if; 


  l_namespace := aws_oracle_ext.format_identifier_case(namespace);
  l_attribute := aws_oracle_ext.format_identifier_case(attribute);

  if l_namespace = 'CLIENTCONTEXT' then 
    perform  aws_oracle_ext.set_extpack_variable
                                (
                                 variable_name => l_namespace||'_'||l_attribute, 
                                 variable_value => value
                                );
  else 
   	perform aws_oracle_ext.init_context_struct();  
    l_key := aws_oracle_ext.get_context_param('key');  
    l_context_type := aws_oracle_ext.get_context_type(l_namespace);   
    l_crypted := crypt(value || ':' || l_key, gen_salt('bf'));   


   	case upper(l_context_type) 
	  	when 'ACCESSED LOCALLY' then 
	  		begin
				insert into context_local(
				  context_namespace, 
				  attribute_name, 
				  attribute_value, 
				  user_name, 
				  cliend_id, 
				  signature
				)
				values (
				  l_namespace, 
				  l_attribute, 
				  $3, 
				  $4, 
				  coalesce($5, aws_oracle_ext.sys_context_userenv('CLIENT_IDENTIFIER')), 
				  l_crypted
				) 
				on conflict (context_namespace, attribute_name) do 
				update set 
					attribute_value = excluded.attribute_value,
					signature = excluded.signature;
		  	end;
	  	when 'ACCESSED GLOBALLY' then	  
	  		begin

				delete from aws_oracle_context.context_global 
			    where backend_start <= now()::timestamp - (coalesce(aws_oracle_ext.get_context_param('auto_clean_interval'), '1 day'))::interval;

				insert into aws_oracle_context.context_global(
				  context_namespace, 
				  attribute_name, 
				  attribute_value, 
				  user_name, 
				  cliend_id, 
				  signature
				)
				values (
				  l_namespace, 
				  l_attribute, 
				  $3, 
				  $4, 
				  coalesce($5, aws_oracle_ext.sys_context_userenv('CLIENT_IDENTIFIER')), 
				  l_crypted
				) 
				on conflict (context_namespace, attribute_name, cliend_id) do 
				update set 
					attribute_value = excluded.attribute_value,
					signature = excluded.signature;
		  	end;  
	  	else 
			-- INITIALIZED EXTERNALLY, INITIALIZED GLOBALLY  	
	  		raise exception 'unsupported context type: %', l_context_type;
	  end case;    
  end if;

  exception 
    when no_data_found then raise exception 'Context % is not found', l_namespace;  
END;

begin
  perform  aws_oracle_ext.set_extpack_variable
   (
      variable_name => 'CLIENT_IDENTIFIER', 
      variable_value => client_id
   ); 
END;

begin
  perform  aws_oracle_ext.set_extpack_variable
   (
      variable_name => upper(param), 
      variable_value => val
   ); 


  if upper(param) = 'NLS_LANGUAGE' then

    perform  aws_oracle_ext.set_extpack_variable
     (
        variable_name => 'NLS_DATE_LANGUAGE', 
        variable_value => val
     );    


    perform  aws_oracle_ext.set_extpack_variable
     (
        variable_name => 'NLS_SORT', 
        variable_value => val
     );    

  end if;
END;

DECLARE
    v_var_pos INTEGER;
    v_var_info TEXT[];
    v_chr_val TEXT;
    v_num_val NUMERIC;
    v_dprec_val DOUBLE PRECISION;
    v_money_val MONEY;
    v_tstamp_val TIMESTAMP WITHOUT TIME ZONE;
    v_tstamptz_val TIMESTAMP WITH TIME ZONE;
    v_timetz_val TIME WITH TIME ZONE;
    v_interval_val INTERVAL;
    v_bool_val BOOLEAN;
    v_xml_val XML;
    v_bin_val BYTEA;
    v_var_size INTEGER;
    v_value_size INTEGER;
    v_is_fixed SMALLINT;
    v_sql_statement TEXT;
    v_cur_metadata JSONB;
    v_out_var_base_t TEXT;
    v_out_var_data_t TEXT;
    v_current_user_oid BIGINT;
BEGIN
    -- Checking that cursor is opened (exit if it is not)
    IF (NOT aws_oracle_ext.dbms_sql$is_open(p_cursor_id))
    THEN
        RAISE invalid_cursor_state;
    -- Variable name should not be empty or blank (null)
    ELSIF (NOT p_check_var_exists AND
           nullif(trim(p_var_name), '') IS NULL)
    THEN
        RAISE null_value_not_allowed;
    END IF;

    -- Obtaining current user OID
    v_current_user_oid := aws_oracle_ext.get_user_oid_by_name(current_user);

    -- Getting cursor metadata (JSON) object
    v_cur_metadata := nullif(current_setting(format('sct$dbms_sql.cursor_id_%s_metadata', p_cursor_id), true), '');

    /* Checking that effective user OID (current_user) of the caller on this cursor is the same
       as that of the caller of the most recent parse operation on this cursor.
       Actual for cursors opened with security level 1 or 2 */
    IF ((v_cur_metadata ->> 'security_level')::SMALLINT IN (1, 2) AND
        (v_cur_metadata ->> 'parse_user_oid')::BIGINT != v_current_user_oid)
    THEN
        RAISE invalid_role_specification;
    END IF;

    BEGIN
        -- Getting SQL-PL/pgSQL statement by cursor id
        SELECT sql_statement
          INTO v_sql_statement
          FROM dbms_sql$cursor
         WHERE cursor_id = p_cursor_id;
    EXCEPTION
        WHEN insufficient_privilege THEN
            -- Effective userid changed
            RAISE invalid_cursor_state;
    END;

    -- Checking whether statement parsed
    IF (v_sql_statement IS NULL) THEN
        RAISE sql_statement_not_yet_complete;
    END IF;

    -- Extracting variable info (label in statement code and general name) from supplied string
    v_var_info := regexp_matches(p_var_name, '^((?:\s*\$\d+\s*(?:[\|,;\-_])?)+)*\s*[\:|\|]?([[:alnum:]_]+)\s*$', 'gi');
    v_var_info[1] := regexp_replace(regexp_replace(v_var_info[1], '\s*[\|,;\-_]\s*', '|', 'g'), '^\s*|\s*[\|,;\-_]$', '', 'gi');

    /* Checking that variable, either by name or any of its markers,
       exists in the corresponding SQL statement */
    IF ((v_var_info[2] IS NULL OR
         v_sql_statement !~* coalesce(v_var_info[2], '')) AND
        coalesce(p_check_var_exists, true)) -- variable name
    THEN
        IF (v_var_info[1] IS NULL OR
            v_sql_statement !~* replace(concat('(', v_var_info[1], ')(?!\d)'), '$', '\$')) -- markers list
        THEN
            RAISE null_value_not_allowed;
        END IF;
    END IF;

    v_out_var_data_t := upper(pg_typeof(p_var_value)::TEXT);

    -- Determining base data type of passed variable
    v_out_var_base_t := aws_oracle_ext.get_base_type_by_dtype(v_out_var_data_t);

    CASE v_out_var_base_t
       WHEN 'TEXT' THEN
           v_chr_val := p_var_value::TEXT;
           v_var_size := coalesce(length(p_var_value::BYTEA, pg_client_encoding()), 0);
           v_value_size := coalesce(p_value_size, v_var_size);
       WHEN 'NUMERIC' THEN
           v_num_val := p_var_value::NUMERIC;
       WHEN 'DOUBLE PRECISION' THEN
           v_dprec_val := p_var_value::DOUBLE PRECISION;
       WHEN 'MONEY' THEN
           v_money_val := p_var_value;
       WHEN 'TIMESTAMP WITHOUT TIME ZONE' THEN
           IF (v_out_var_data_t = 'TIME WITHOUT TIME ZONE') THEN
               v_tstamp_val := to_char(p_var_value, '2001-01-01 hh24:mi:ss.us')::TIMESTAMP;
           ELSE
               v_tstamp_val := p_var_value;
           END IF;
       WHEN 'TIMESTAMP WITH TIME ZONE' THEN
           v_tstamptz_val := p_var_value;
       WHEN 'TIME WITH TIME ZONE' THEN
           v_timetz_val := p_var_value;
       WHEN 'INTERVAL' THEN
           v_interval_val := p_var_value;
       WHEN 'BOOLEAN' THEN
           v_bool_val := p_var_value;
       WHEN 'XML' THEN
           v_xml_val := p_var_value;
       WHEN 'BYTEA' THEN
           v_bin_val := p_var_value;
           v_var_size := coalesce(octet_length(p_var_value), 0);
           v_value_size := p_value_size;
       ELSE RETURN;
    END CASE;

    -- Determining next variable position
    SELECT coalesce(MAX(var_pos), 0) + 1
      INTO v_var_pos
      FROM dbms_sql$bind_variable
     WHERE cursor_id = p_cursor_id;

    -- Binding variable to cursor (identified by cursor_id)
    INSERT INTO dbms_sql$bind_variable (cursor_id,
                                        var_pos,
                                        var_name,
                                        var_label,
                                        data_type,
                                        base_type,
                                        var_size,
                                        value_size,
                                        is_fixed,
                                        chr_val,
                                        num_val,
                                        dprec_val,
                                        money_val,
                                        tstamp_val,
                                        tstamptz_val,
                                        timetz_val,
                                        interval_val,
                                        bool_val,
                                        xml_val,
                                        bin_val)
         VALUES (p_cursor_id,
                 v_var_pos,
                 v_var_info[2], -- Variable name
                 v_var_info[1], -- Variable label list
                 v_out_var_data_t,
                 v_out_var_base_t,
                 v_var_size,
                 v_value_size,
                 v_is_fixed,
                 v_chr_val,
                 v_num_val,
                 v_dprec_val,
                 v_money_val,
                 v_tstamp_val,
                 v_tstamptz_val,
                 v_timetz_val,
                 v_interval_val,
                 v_bool_val,
                 v_xml_val,
                 v_bin_val)
    ON CONFLICT (cursor_id, var_name)
    DO UPDATE SET var_label = v_var_info[1],
                  data_type = v_out_var_data_t,
                  base_type = v_out_var_base_t,
                  var_size = v_var_size,
                  value_size = v_value_size,
                  is_fixed = v_is_fixed,
                  chr_val = v_chr_val,
                  num_val = v_num_val,
                  dprec_val = v_dprec_val,
                  money_val = v_money_val,
                  tstamp_val = v_tstamp_val,
                  tstamptz_val = v_tstamptz_val,
                  timetz_val = v_timetz_val,
                  interval_val = v_interval_val,
                  bool_val = v_bool_val,
                  xml_val = v_xml_val,
                  bin_val = v_bin_val;
EXCEPTION
    -- Cursor is not opened or does not exist
    WHEN invalid_cursor_state THEN
        RAISE USING MESSAGE := 'DBMS_SQL access denied.',
                    DETAIL := 'DBMS_SQL access was denied due to security concerns.',
                    HINT := '-29471';

    -- Bind variable does not exist
    WHEN null_value_not_allowed THEN
        RAISE USING MESSAGE := 'Bind variable does not exist.',
                    DETAIL := 'Call for a bind variable that was not listed in the corresponding SQL statement.',
                    HINT := '-1006';

    -- No statement previously parsed
    WHEN sql_statement_not_yet_complete THEN
        RAISE USING MESSAGE := 'No statement parsed.',
                    DETAIL := 'Attempting to reference a cursor with the SQL statement associated not parsed.',
                    HINT := '-1003';

    -- Effective userid (OID) changed since cursor parse
    WHEN invalid_role_specification THEN
        RAISE USING MESSAGE := 'Effective userid is not the same as when cursor was parsed.',
                    DETAIL := 'The effective userid on this call to DBMS_SQL is not the same as that at the time the cursor was parsed.',
                    HINT := '-29470';
END;

DECLARE
    v_var_pos INTEGER;
    v_var_info TEXT[];
    v_chr_val TEXT;
    v_var_size INTEGER;
    v_value_size INTEGER;
    v_datetime_mask TEXT;
    v_out_var_data_t TEXT;
    v_sql_statement TEXT;
    v_cur_metadata JSONB;
    v_is_fixed SMALLINT := 1;
    v_current_user_oid BIGINT;
    v_bind_var_base_t TEXT := 'TEXT';
    v_bind_var_data_t TEXT := 'CHARACTER VARYING';
BEGIN
    -- Checking that cursor is opened (exit if it is not)
    IF (NOT aws_oracle_ext.dbms_sql$is_open(p_cursor_id))
    THEN
        RAISE invalid_cursor_state;
    -- Variable name should not be empty or blank (null)
    ELSIF (NOT p_check_var_exists AND
           nullif(trim(p_var_name), '') IS NULL)
    THEN
        RAISE null_value_not_allowed;
    END IF;

    -- Obtaining current user OID
    v_current_user_oid := aws_oracle_ext.get_user_oid_by_name(current_user);

    -- Getting cursor metadata (JSON) object
    v_cur_metadata := nullif(current_setting(format('sct$dbms_sql.cursor_id_%s_metadata', p_cursor_id), true), '');

    /* Checking that effective user OID (current_user) of the caller on this cursor is the same
       as that of the caller of the most recent parse operation on this cursor.
       Actual for cursors opened with security level 1 or 2 */
    IF ((v_cur_metadata ->> 'security_level')::SMALLINT IN (1, 2) AND
        (v_cur_metadata ->> 'parse_user_oid')::BIGINT != v_current_user_oid)
    THEN
        RAISE invalid_role_specification;
    END IF;

    BEGIN
        -- Getting SQL-PL/pgSQL statement by cursor id
        SELECT sql_statement
          INTO v_sql_statement
          FROM dbms_sql$cursor
         WHERE cursor_id = p_cursor_id;
    EXCEPTION
        WHEN insufficient_privilege THEN
            -- Effective userid changed
            RAISE invalid_cursor_state;
    END;

    -- Checking whether statement parsed
    IF (v_sql_statement IS NULL) THEN
        RAISE sql_statement_not_yet_complete;
    END IF;

    -- Extracting variable info (label in statement code and general name) from supplied string
    v_var_info := regexp_matches(p_var_name, '^((?:\s*\$\d+\s*(?:[\|,;\-_])?)+)*\s*[\:|\|]?([[:alnum:]_]+)\s*$', 'gi');
    v_var_info[1] := regexp_replace(regexp_replace(v_var_info[1], '\s*[\|,;\-_]\s*', '|', 'g'), '^\s*|\s*[\|,;\-_]$', '', 'gi');

    /* Checking that variable, either by name or any of its markers,
       exists in the corresponding SQL statement */
    IF ((v_var_info[2] IS NULL OR
         v_sql_statement !~* coalesce(v_var_info[2], '')) AND
        coalesce(p_check_var_exists, true)) -- variable name
    THEN
        IF (v_var_info[1] IS NULL OR
            v_sql_statement !~* replace(concat('(', v_var_info[1], ')(?!\d)'), '$', '\$')) -- markers list
        THEN
            RAISE null_value_not_allowed;
        END IF;
    END IF;

    v_out_var_data_t := upper(pg_typeof(p_var_value)::TEXT);

    -- Casting input value to a character string
    IF (v_out_var_data_t IN ('TEXT',
                             'CHARACTER',
                             'CHARACTER VARYING'))
    THEN
        v_bind_var_data_t := v_out_var_data_t;
        v_chr_val := p_var_value::TEXT;
        v_var_size := coalesce(length(p_var_value::BYTEA, pg_client_encoding()), 0);
    ELSIF (v_out_var_data_t IN ('XML',
                                'MONEY',
                                'REAL',
                                'BIGINT',
                                'INTEGER',
                                'NUMERIC',
                                'SMALLINT',
                                'INTERVAL',
                                'DOUBLE PRECISION',
                                'TIME WITH TIME ZONE'))
    THEN
        v_chr_val := p_var_value::TEXT;
    ELSIF (v_out_var_data_t = 'BOOLEAN')
    THEN
        v_chr_val := upper(p_var_value::TEXT);
    ELSIF (v_out_var_data_t IN ('DATE',
                                'TIME WITHOUT TIME ZONE',
                                'TIMESTAMP WITHOUT TIME ZONE',
                                'TIMESTAMP WITH TIME ZONE'))
    THEN
        -- Picking up the right datetime mask
        v_datetime_mask := CASE v_out_var_data_t
                              WHEN 'DATE' THEN 'dd-MON-yy'
                              WHEN 'TIME WITHOUT TIME ZONE' THEN 'hh24:mi:ss.us AM'
                              WHEN 'TIMESTAMP WITHOUT TIME ZONE' THEN 'dd-MON-yy hh24:mi:ss.us AM'
                              ELSE 'dd-MON-yy hh24:mi:ss.us AM OF:00'
                           END;

        v_chr_val := to_char(p_var_value, v_datetime_mask);
    ELSIF (v_out_var_data_t = 'BYTEA')
    THEN
        v_bind_var_data_t := 'TEXT';
        v_chr_val := upper(encode(p_var_value, 'hex'));
    ELSE RETURN;
    END IF;

    v_var_size := coalesce(v_var_size, coalesce(char_length(v_chr_val), 0));

    -- Determining next variable position
    SELECT coalesce(MAX(var_pos), 0) + 1
      INTO v_var_pos
      FROM dbms_sql$bind_variable
     WHERE cursor_id = p_cursor_id;

    -- Binding variable to cursor (identified by cursor_id)
    INSERT INTO dbms_sql$bind_variable (cursor_id,
                                        var_pos,
                                        var_name,
                                        var_label,
                                        data_type,
                                        base_type,
                                        var_size,
                                        value_size,
                                        is_fixed,
                                        chr_val)
         VALUES (p_cursor_id,
                 v_var_pos,
                 v_var_info[2], -- Variable name
                 v_var_info[1], -- Variable label list
                 v_bind_var_data_t,
                 v_bind_var_base_t,
                 v_var_size,
                 p_value_size,
                 v_is_fixed,
                 v_chr_val)
    ON CONFLICT (cursor_id, var_name)
    DO UPDATE SET var_label = v_var_info[1],
                  data_type = v_bind_var_data_t,
                  base_type = v_bind_var_base_t,
                  value_size = p_value_size,
                  is_fixed = v_is_fixed,
                  chr_val = v_chr_val;
EXCEPTION
    -- Cursor is not opened or does not exist
    WHEN invalid_cursor_state THEN
        RAISE USING MESSAGE := 'DBMS_SQL access denied.',
                    DETAIL := 'DBMS_SQL access was denied due to security concerns.',
                    HINT := '-29471';

    -- Bind variable does not exist
    WHEN null_value_not_allowed THEN
        RAISE USING MESSAGE := 'Bind variable does not exist.',
                    DETAIL := 'Call for a bind variable that was not listed in the corresponding SQL statement.',
                    HINT := '-1006';

    -- No statement previously parsed
    WHEN sql_statement_not_yet_complete THEN
        RAISE USING MESSAGE := 'No statement parsed.',
                    DETAIL := 'Attempting to reference a cursor with the SQL statement associated not parsed.',
                    HINT := '-1003';

    -- Effective userid (OID) changed since cursor parse
    WHEN invalid_role_specification THEN
        RAISE USING MESSAGE := 'Effective userid is not the same as when cursor was parsed.',
                    DETAIL := 'The effective userid on this call to DBMS_SQL is not the same as that at the time the cursor was parsed.',
                    HINT := '-29470';
END;

DECLARE
    v_cursor_id INTEGER := p_cursor_id;
BEGIN
    /* Close DBMS_SQL cursor only if it is opened.
       This prevents a secondary exception raising when trying
       to close DBMS_SQL cursor in EXCEPTION section.
    */
    IF (aws_oracle_ext.dbms_sql$is_open(v_cursor_id))
    THEN
        -- Deleting related data from all temp tables
        DELETE FROM dbms_sql$cursor
              WHERE cursor_id = v_cursor_id;

        -- Nulling cursor metadata (JSON) object
        PERFORM set_config(format('sct$dbms_sql.cursor_id_%s_metadata', v_cursor_id), null, false);

        -- Setting `last_affected_cursor_id` variable
        PERFORM set_config('sct$dbms_sql.last_affected_cursor_id', v_cursor_id::TEXT, false);

        -- Nulling `last_row_count` variable
        PERFORM set_config('sct$dbms_sql.last_row_count', null, false);
    END IF;

    p_cursor_id := null;
EXCEPTION
    -- Effective userid (OID) changed since solution initialization
    WHEN insufficient_privilege THEN
        RAISE USING MESSAGE := 'DBMS_SQL access denied.',
                    DETAIL := 'DBMS_SQL access was denied due to security concerns.',
                    HINT := '-29471';
END;

DECLARE
    v_chr_val TEXT;
    v_num_val NUMERIC;
    v_dprec_val DOUBLE PRECISION;
    v_money_val MONEY;
    v_tstamp_val TIMESTAMP WITHOUT TIME ZONE;
    v_tstamptz_val TIMESTAMP WITH TIME ZONE;
    v_timetz_val TIME WITH TIME ZONE;
    v_interval_val INTERVAL;
    v_bool_val BOOLEAN;
    v_xml_val XML;
    v_bin_val BYTEA;
    v_col_size INTEGER;
    v_defcol_data JSONB;
    v_curcol_data JSONB;
    v_cur_metadata JSONB;
    v_curcol_data_t TEXT;
    v_curcol_base_t TEXT;
    v_defcol_data_t TEXT;
    v_defcol_base_t TEXT;
    v_out_var_base_t TEXT;
    v_out_var_data_t TEXT;
    v_current_user_oid BIGINT;
BEGIN
    -- Checking that cursor is opened (exit if it is not)
    IF (NOT aws_oracle_ext.dbms_sql$is_open(p_cursor_id)) THEN
        RAISE invalid_cursor_state;
    END IF;

    -- Obtaining current user OID
    v_current_user_oid := aws_oracle_ext.get_user_oid_by_name(current_user);

    -- Getting cursor metadata (JSON) object
    v_cur_metadata := nullif(current_setting(format('sct$dbms_sql.cursor_id_%s_metadata', p_cursor_id), true), '');

    /* Checking that effective user OID (current_user) of the caller on this cursor is the same
       as that of the caller of the most recent parse operation on this cursor.
       Actual for cursors opened with security level 2 */
    IF ((v_cur_metadata ->> 'security_level')::SMALLINT = 2 AND
        (v_cur_metadata ->> 'parse_user_oid')::BIGINT != v_current_user_oid)
    THEN
        RAISE invalid_role_specification;
    END IF;

    -- Getting dynamic SQL statement defined and actual columns info (JSON)
    v_defcol_data := (v_cur_metadata #> format('{defcols_data, col_%s}', p_column_pos)::TEXT[]);
    v_curcol_data := (v_cur_metadata #> format('{curcols_data, col_%s}', p_column_pos)::TEXT[]);

    v_defcol_data_t := (v_defcol_data ->> 'data_type'); -- Column (defined) data type
    v_defcol_base_t := (v_defcol_data ->> 'base_type'); -- Column (defined) base data type
    v_curcol_data_t := (v_curcol_data ->> 'data_type'); -- Column (actual) data type
    v_curcol_base_t := (v_curcol_data ->> 'base_type'); -- Column (actual) base data type

    v_col_size := (v_defcol_data ->> 'col_size'); -- Column size in characters

    v_out_var_data_t := upper(pg_typeof(p_column_val)::TEXT);

    -- Determining base data type of passed variable
    v_out_var_base_t := aws_oracle_ext.get_base_type_by_dtype(v_out_var_data_t);

    -- Default vars states
    p_column_val := null;
    p_column_err := 0;
    p_actual_len := 0;
    p_typvar_len := 0;

    -- Column position should be declared in SELECT list
    IF (v_defcol_base_t IS NULL) THEN
        RAISE null_value_not_allowed;
    -- Check if OUT argument base type matches declared column base type (with some exclusions)
    ELSIF ((substring(v_out_var_base_t, 1, 14) != substring(v_defcol_base_t, 1, 14) AND -- TIMESTAMPs
            substring(v_out_var_base_t, 1, 5) != substring(v_defcol_base_t, 1, 5) AND -- TIMEs
            (substring(v_out_var_data_t, 1, 5) != 'TIME ' AND
             substring(v_defcol_data_t, 1, 10) != 'TIMESTAMP ') AND
            NOT (v_defcol_base_t IN ('NUMERIC',
                                     'DOUBLE PRECISION') AND
                 v_out_var_base_t IN ('NUMERIC',
                                      'DOUBLE PRECISION'))) OR
           (substring(v_defcol_data_t, 1, 5) = 'TIME ' AND
            (substring(v_out_var_data_t, 1, 10) = 'TIMESTAMP ' OR
             v_out_var_data_t = 'DATE')))
    THEN
        RAISE datatype_mismatch;
    -- Variable should not be declared using `dbms_sql$define_column_char`
    ELSIF ((v_defcol_data ->> 'is_fixed')::SMALLINT = 1) THEN
        RAISE datatype_mismatch;
    -- If current row not specified, then nothing to return
    ELSIF ((v_cur_metadata ->> 'curr_row') IS NULL) THEN
        RETURN;
    END IF;

    BEGIN
        /* Extracting row data from execution results
           table `dbms_sql$recordset`
           (by cursor id, row number, column position) */
        SELECT chr_val,
               num_val,
               dprec_val,
               money_val,
               tstamp_val,
               tstamptz_val,
               timetz_val,
               interval_val,
               bool_val,
               xml_val,
               bin_val
          INTO v_chr_val,
               v_num_val,
               v_dprec_val,
               v_money_val,
               v_tstamp_val,
               v_tstamptz_val,
               v_timetz_val,
               v_interval_val,
               v_bool_val,
               v_xml_val,
               v_bin_val
          FROM dbms_sql$recordset
         WHERE cursor_id = p_cursor_id
           AND rownum = (v_cur_metadata ->> 'curr_row')::INTEGER
           AND column_pos = p_column_pos;
    EXCEPTION
        WHEN insufficient_privilege THEN
            -- Effective userid changed
            RAISE invalid_cursor_state;
    END;

    -- Casting data type and copying value into the OUT argument
    IF (v_out_var_base_t = 'TEXT')
    THEN
        -- Actual length of the data (chars)
        IF (v_chr_val IS NOT NULL) THEN
            p_typvar_len :=
                CASE v_curcol_data_t
                   WHEN 'CHARACTER' THEN (v_curcol_data ->> 'chr_max_len')::INTEGER
                   ELSE char_length(v_chr_val)
                END;
        END IF;

        IF (v_col_size IS NULL) THEN
            p_column_val := v_chr_val;
        ELSE
            p_column_val := substring(v_chr_val, 1, v_col_size);

            IF (v_col_size != 0 AND v_col_size < p_typvar_len)
            THEN
                p_column_err := 1406;
            END IF;
        END IF;

        /* Internal representation (bytes).
           4 bytes + length of text */
        IF (v_chr_val IS NOT NULL) THEN
            p_actual_len :=
                CASE v_curcol_data_t
                   WHEN 'CHARACTER' THEN (v_curcol_data ->> 'chr_byte_len')::INTEGER
                   ELSE pg_column_size(v_chr_val)
                END;
        END IF;
    ELSIF (v_out_var_base_t IN ('NUMERIC',
                                'DOUBLE PRECISION'))
    THEN
        -- Determining data source column
        IF (v_curcol_base_t = 'NUMERIC') THEN
            p_column_val := v_num_val;
        ELSE
            p_column_val := v_dprec_val;
        END IF;

        -- Internal representation (bytes)
        IF (p_column_val IS NOT NULL) THEN
            p_actual_len :=
                CASE v_curcol_data_t
                   WHEN 'SMALLINT' THEN 2 -- 2 bytes
                   WHEN 'INTEGER' THEN 4 -- 4 bytes
                   WHEN 'BIGINT' THEN 8 -- 8 bytes
                   WHEN 'REAL' THEN 4 -- 4 bytes
                   WHEN 'DOUBLE PRECISION' THEN 8 -- 8 bytes
                   WHEN 'NUMERIC' THEN pg_column_size(p_column_val)
                END;
        END IF;

        /* Actual length of the data (bytes).
           Same as internal representation */
        p_typvar_len := p_actual_len;
    ELSIF (v_out_var_base_t = 'MONEY')
    THEN
        p_column_val := v_money_val;

        -- Internal representation, 8 bytes
        p_actual_len := CASE
                           WHEN p_column_val IS NOT NULL
                           THEN 8
                        END;

        /* Actual length of the data (bytes).
           Same as internal representation */
        p_typvar_len := p_actual_len;
    ELSIF (v_out_var_base_t IN ('TIMESTAMP WITHOUT TIME ZONE',
                                'TIMESTAMP WITH TIME ZONE'))
    THEN
        CASE v_curcol_base_t
           WHEN 'TIMESTAMP WITHOUT TIME ZONE' THEN
               p_column_val := v_tstamp_val;
           WHEN 'TIMESTAMP WITH TIME ZONE' THEN
               p_column_val := v_tstamptz_val;
           ELSE
               p_column_val := v_timetz_val;
        END CASE;

        -- Internal representation (bytes)
        p_actual_len := CASE
                           WHEN p_column_val IS NOT NULL
                           THEN CASE v_curcol_data_t
                                   WHEN 'DATE' THEN 4
                                   WHEN 'TIME WITH TIME ZONE' THEN 12
                                   ELSE 8
                                END
                        END;

        /* Actual length of the data (bytes).
           Same as internal representation */
        p_typvar_len := p_actual_len;
    ELSIF (v_out_var_base_t = 'TIME WITH TIME ZONE')
    THEN
        p_column_val := CASE v_curcol_base_t
                           WHEN 'TIMESTAMP WITHOUT TIME ZONE' THEN v_tstamp_val::TIME::TIMETZ
                           WHEN 'TIMESTAMP WITH TIME ZONE' THEN v_tstamptz_val::TIMETZ
                           ELSE v_timetz_val
                        END;

        -- Internal representation (bytes)
        p_actual_len := CASE
                           WHEN p_column_val IS NOT NULL
                           THEN CASE v_curcol_data_t
                                   WHEN 'DATE' THEN 4
                                   WHEN 'TIME WITH TIME ZONE' THEN 12
                                   ELSE 8
                                END
                        END;

        /* Actual length of the data (bytes).
           Same as internal representation */
        p_typvar_len := p_actual_len;
    ELSIF (v_out_var_base_t = 'INTERVAL')
    THEN
        p_column_val := v_interval_val;

        -- Internal representation, 16 bytes
        p_actual_len := CASE
                           WHEN p_column_val IS NOT NULL
                           THEN 16
                        END;

        /* Actual length of the data (bytes).
           Same as internal representation */
        p_typvar_len := p_actual_len;
    ELSIF (v_out_var_base_t = 'BOOLEAN')
    THEN
        p_column_val := v_bool_val;

        /* Internal representation (bytes).
           1 byte, for TRUE or FALSE value */
        p_actual_len := CASE
                           WHEN p_column_val IS NOT NULL
                           THEN 1
                        END;

        /* Actual length of the data (bytes).
           Same as internal representation */
        p_typvar_len := p_actual_len;
    ELSIF (v_out_var_base_t = 'XML')
    THEN
        p_column_val := v_xml_val;

        -- Internal representation (bytes)
        p_actual_len := pg_column_size(p_column_val);

        -- Actual length of the data (chars)
        p_typvar_len := char_length(p_column_val::TEXT);
    ELSIF (v_out_var_base_t = 'BYTEA')
    THEN
        p_column_val := CASE
                           WHEN v_col_size IS NULL THEN v_bin_val
                           ELSE substring(v_bin_val from 1 for v_col_size)
                        END;

        /* Internal representation (bytes).
           4 bytes + actual length of the data */
        p_actual_len := pg_column_size(v_bin_val);

        -- Actual length of the data (bytes)
        p_typvar_len := octet_length(v_bin_val);

        IF (v_col_size != 0 AND v_col_size < p_typvar_len)
        THEN
            p_column_err := 1406;
        END IF;
    END IF;

    IF (p_column_val IS NULL) THEN
        p_column_err := 1405;
    END IF;

    p_actual_len := coalesce(p_actual_len, 0);
    p_typvar_len := coalesce(p_typvar_len, 0);
EXCEPTION
    -- Cursor is not opened or does not exist
    WHEN invalid_cursor_state THEN
        RAISE USING MESSAGE := 'DBMS_SQL access denied.',
                    DETAIL := 'DBMS_SQL access was denied due to security concerns.',
                    HINT := '-29471';

    -- Column position is not declared in SELECT list
    WHEN null_value_not_allowed THEN
        RAISE USING MESSAGE := 'Column not in select list.',
                    DETAIL := 'A reference was made to a column not listed in the SELECT clause.',
                    HINT := '-1007';

    /* OUT argument base type does not match declared column base type.
       Possible column was declared through `dbms_sql$define_column_char` */
    WHEN datatype_mismatch THEN
        RAISE USING MESSAGE := 'Type of OUT argument does not match declared column type.',
                    DETAIL := 'Attempting to get the value of a column but the type of the given OUT argument is different from the type of column that was defined previously.',
                    HINT := '-6562';

    -- Effective userid (OID) changed since cursor parse
    WHEN invalid_role_specification THEN
        RAISE USING MESSAGE := 'Effective userid is not the same as when cursor was parsed.',
                    DETAIL := 'The effective userid on this call to DBMS_SQL is not the same as that at the time the cursor was parsed.',
                    HINT := '-29470';
END;

DECLARE
    v_cur_metadata JSONB;
    v_defcol_data JSONB;
    v_curcol_data JSONB;
    v_col_size INTEGER;
    v_chr_val TEXT;
    v_num_val NUMERIC;
    v_dprec_val DOUBLE PRECISION;
    v_money_val MONEY;
    v_tstamp_val TIMESTAMP WITHOUT TIME ZONE;
    v_tstamptz_val TIMESTAMP WITH TIME ZONE;
    v_timetz_val TIME WITH TIME ZONE;
    v_interval_val INTERVAL;
    v_bool_val BOOLEAN;
    v_xml_val XML;
    v_bin_val BYTEA;
    v_curcol_data_t TEXT;
    v_curcol_base_t TEXT;
    v_defcol_base_t TEXT;
    v_datetime_mask TEXT;
    v_out_var_size INTEGER;
    v_current_user_oid BIGINT;
    v_out_var_data_t TEXT := upper(pg_typeof(p_column_val)::TEXT);
BEGIN
    -- Checking that cursor is opened (exit if it is not)
    IF (NOT aws_oracle_ext.dbms_sql$is_open(p_cursor_id)) THEN
        RAISE invalid_cursor_state;
    END IF;

    -- Obtaining current user OID
    v_current_user_oid := aws_oracle_ext.get_user_oid_by_name(current_user);

    -- Getting cursor metadata (JSON) object
    v_cur_metadata := nullif(current_setting(format('sct$dbms_sql.cursor_id_%s_metadata', p_cursor_id), true), '');

    /* Checking that effective user OID (current_user) of the caller on this cursor is the same
       as that of the caller of the most recent parse operation on this cursor.
       Actual for cursors opened with security level 2 */
    IF ((v_cur_metadata ->> 'security_level')::SMALLINT = 2 AND
        (v_cur_metadata ->> 'parse_user_oid')::BIGINT != v_current_user_oid)
    THEN
        RAISE invalid_role_specification;
    END IF;

    -- Getting dynamic SQL statement defined and actual columns info (JSON)
    v_defcol_data := (v_cur_metadata #> format('{defcols_data, col_%s}', p_column_pos)::TEXT[]);
    v_curcol_data := (v_cur_metadata #> format('{curcols_data, col_%s}', p_column_pos)::TEXT[]);

    v_curcol_data_t := (v_curcol_data ->> 'data_type'); -- Column (actual) data type
    v_curcol_base_t := (v_curcol_data ->> 'base_type'); -- Column (actual) base data type
    v_defcol_base_t := (v_defcol_data ->> 'base_type'); -- Column (defined) base data type

    v_col_size := (v_defcol_data ->> 'col_size'); -- Column size in characters

    -- Default vars states
    p_column_err := 0;
    p_actual_len := 0;
    p_typvar_len := 0;

    -- Column position should be declared in SELECT list
    IF (v_defcol_base_t IS NULL) THEN
        RAISE null_value_not_allowed;
    -- Variable should not be declared using `dbms_sql$define_column`
    ELSIF (coalesce((v_defcol_data ->> 'is_fixed')::SMALLINT, 0) != 1) THEN
        RAISE most_specific_type_mismatch;
    -- If current row not specified, then nothing to return
    ELSIF ((v_cur_metadata ->> 'curr_row') IS NULL) THEN
        RETURN;
    END IF;

    BEGIN
        /* Extracting row data from execution results
           table `dbms_sql$recordset`
           (by cursor id, row number, column position) */
        SELECT chr_val,
               num_val,
               dprec_val,
               money_val,
               tstamp_val,
               tstamptz_val,
               timetz_val,
               interval_val,
               bool_val,
               xml_val,
               bin_val
          INTO v_chr_val,
               v_num_val,
               v_dprec_val,
               v_money_val,
               v_tstamp_val,
               v_tstamptz_val,
               v_timetz_val,
               v_interval_val,
               v_bool_val,
               v_xml_val,
               v_bin_val
          FROM dbms_sql$recordset
         WHERE cursor_id = p_cursor_id
           AND rownum = (v_cur_metadata ->> 'curr_row')::INTEGER
           AND column_pos = p_column_pos;
    EXCEPTION
        WHEN insufficient_privilege THEN
            -- Effective userid changed
            RAISE invalid_cursor_state;
    END;

    /* Casting column data to a character string.
       Copying value into the OUT argument */
    IF (v_curcol_data_t = 'CHARACTER' AND
        v_chr_val IS NOT NULL)
    THEN
        -- Internal representation (bytes)
        p_actual_len := (v_curcol_data ->> 'chr_byte_len');

        -- Actual length of the data (bytes)
        p_typvar_len := (v_curcol_data ->> 'chr_max_len');
    ELSIF (v_curcol_base_t = 'TEXT')
    THEN
        /* Internal representation (bytes).
           4 bytes + length of text */
        p_actual_len := pg_column_size(v_chr_val);

        -- Actual length of the data (chars)
        p_typvar_len := char_length(v_chr_val);
    ELSIF (v_curcol_base_t = 'NUMERIC')
    THEN
        v_chr_val := v_num_val::TEXT;

        -- Internal representation (bytes)
        IF (v_chr_val IS NOT NULL) THEN
            p_actual_len :=
                CASE v_curcol_data_t
                   WHEN 'SMALLINT' THEN 2 -- 2 bytes
                   WHEN 'INTEGER' THEN 4 -- 4 bytes
                   WHEN 'BIGINT' THEN 8 -- 8 bytes
                   WHEN 'NUMERIC' THEN pg_column_size(v_num_val)
                END;
        END IF;

        /* Actual length of the data (bytes).
           Same as internal representation */
        p_typvar_len := p_actual_len;
    ELSIF (v_curcol_base_t = 'DOUBLE PRECISION')
    THEN
        v_chr_val := v_dprec_val::TEXT;

        -- Internal representation (bytes)
        IF (v_chr_val IS NOT NULL) THEN
            p_actual_len :=
                CASE v_curcol_data_t
                   WHEN 'REAL' THEN 4 -- 4 bytes
                   WHEN 'DOUBLE PRECISION' THEN 8 -- 8 bytes
                END;
        END IF;

        /* Actual length of the data (bytes).
           Same as internal representation */
        p_typvar_len := p_actual_len;
    ELSIF (v_curcol_base_t = 'MONEY')
    THEN
        v_chr_val := v_money_val::TEXT;

        -- Internal representation, 8 bytes
        p_actual_len := CASE
                           WHEN v_chr_val IS NOT NULL
                           THEN 8
                        END;

        /* Actual length of the data (bytes).
           Same as internal representation */
        p_typvar_len := p_actual_len;
    ELSIF (v_curcol_base_t IN ('TIMESTAMP WITHOUT TIME ZONE',
                               'TIMESTAMP WITH TIME ZONE',
                               'TIME WITH TIME ZONE'))
    THEN
        -- Picking up the right datetime mask
        v_datetime_mask := CASE v_curcol_data_t
                              WHEN 'DATE' THEN 'dd-MON-yy'
                              WHEN 'TIME WITHOUT TIME ZONE' THEN 'hh24:mi:ss.us AM'
                              WHEN 'TIMESTAMP WITHOUT TIME ZONE' THEN 'dd-MON-yy hh24:mi:ss.us AM'
                              ELSE 'dd-MON-yy hh24:mi:ss.us AM OF:00'
                           END;

        v_chr_val := CASE v_curcol_base_t
                        WHEN 'TIMESTAMP WITHOUT TIME ZONE' THEN to_char(v_tstamp_val, v_datetime_mask)
                        WHEN 'TIME WITH TIME ZONE' THEN v_timetz_val::TEXT
                        ELSE to_char(v_tstamptz_val, v_datetime_mask)
                     END;

        /* Internal representation,
           4 bytes for DATE, 8 bytes for other types */
        p_actual_len := CASE
                           WHEN v_chr_val IS NOT NULL
                           THEN CASE v_curcol_data_t
                                   WHEN 'DATE' THEN 4
                                   WHEN 'TIME WITH TIME ZONE' THEN 12
                                   ELSE 8
                                END
                        END;

        /* Actual length of the data (bytes).
           Same as internal representation */
        p_typvar_len := p_actual_len;
    ELSIF (v_curcol_base_t = 'INTERVAL')
    THEN
        v_chr_val := v_interval_val::TEXT;

        -- Internal representation, 16 bytes
        p_actual_len := CASE
                           WHEN v_chr_val IS NOT NULL
                           THEN 16
                        END;

        /* Actual length of the data (bytes).
           Same as internal representation */
        p_typvar_len := p_actual_len;
    ELSIF (v_curcol_base_t = 'BOOLEAN')
    THEN
        v_chr_val := upper(v_bool_val::TEXT);

        /* Internal representation (bytes).
           1 byte, for TRUE or FALSE value */
        p_actual_len := CASE
                           WHEN v_chr_val IS NOT NULL
                           THEN 1
                        END;

        /* Actual length of the data (bytes).
           Same as internal representation */
        p_typvar_len := p_actual_len;
    ELSIF (v_curcol_base_t = 'XML')
    THEN
        v_chr_val := v_xml_val::TEXT;

        -- Internal representation (bytes)
        p_actual_len := pg_column_size(v_xml_val);

        -- Actual length of the data (chars)
        p_typvar_len := char_length(v_chr_val);
    ELSIF (v_curcol_base_t = 'BYTEA')
    THEN
        v_chr_val := upper(encode(v_bin_val, 'hex'));

        /* Internal representation (bytes).
           4 bytes + actual length of the data */
        p_actual_len := pg_column_size(v_bin_val);

        -- Actual length of the data (bytes)
        p_typvar_len := octet_length(v_bin_val);
    END IF;

    IF (v_col_size != 0 AND
        v_col_size < char_length(v_chr_val))
    THEN
        p_column_err := 1406;
    END IF;

    v_chr_val := CASE
                    WHEN v_col_size IS NULL THEN v_chr_val
                    ELSE substring(v_chr_val, 1, v_col_size)
                 END;

    -- Copying value into the OUT argument
    IF (v_out_var_data_t IN ('TEXT',
                             'CHARACTER',
                             'CHARACTER VARYING'))
    THEN
        v_out_var_size := coalesce(length(p_column_val::BYTEA, pg_client_encoding()), 0);

        IF (v_out_var_size != 0 AND
            v_out_var_size < p_actual_len)
        THEN
            RAISE string_data_length_mismatch;
        END IF;

        p_column_val := v_chr_val;
    ELSIF (v_out_var_data_t IN ('BIGINT',
                                'INTEGER',
                                'NUMERIC',
                                'SMALLINT'))
    THEN
        p_column_val := v_chr_val::NUMERIC;
    ELSIF (v_out_var_data_t IN ('REAL',
                                'DOUBLE PRECISION'))
    THEN
        p_column_val := v_chr_val::DOUBLE PRECISION;
    ELSIF (v_out_var_data_t = 'MONEY')
    THEN
        p_column_val := v_chr_val::MONEY;
    ELSIF (v_out_var_data_t = 'DATE')
    THEN
        IF (char_length(v_chr_val) < 8) THEN
            RAISE invalid_datetime_format;
        END IF;

        p_column_val := to_date(v_chr_val, 'dd-MON-yy');
    ELSIF (v_out_var_data_t IN ('TIME WITHOUT TIME ZONE',
                                'TIME WITH TIME ZONE'))
    THEN
        v_chr_val := regexp_replace(v_chr_val, '\s*[AP]M', '', 'i');

        IF (char_length(v_chr_val) < 8) THEN
            RAISE invalid_datetime_format;
        END IF;

        IF (v_out_var_data_t = 'TIME WITHOUT TIME ZONE') THEN
            p_column_val := v_chr_val::TIME;
        ELSE
            p_column_val := v_chr_val::TIMETZ;
        END IF;
    ELSIF (v_out_var_data_t = 'TIMESTAMP WITHOUT TIME ZONE')
    THEN
        v_chr_val := regexp_replace(v_chr_val, '\s*[AP]M', '', 'i');

        IF (char_length(v_chr_val) < 17) THEN
            RAISE invalid_datetime_format;
        END IF;

        p_column_val := v_chr_val::TIMESTAMP;
    ELSIF (v_out_var_data_t = 'TIMESTAMP WITH TIME ZONE')
    THEN
        v_chr_val := regexp_replace(v_chr_val, '\s*[AP]M', '', 'i');

        IF (char_length(v_chr_val) < 17) THEN
            RAISE invalid_datetime_format;
        END IF;

        p_column_val := to_timestamp(v_chr_val, 'dd-MON-yy hh24:mi:ss.us');
    ELSIF (v_out_var_data_t = 'INTERVAL')
    THEN
        p_column_val := v_chr_val::INTERVAL;
    ELSIF (v_out_var_data_t = 'BOOLEAN')
    THEN
        p_column_val := v_chr_val::BOOLEAN;
    ELSIF (v_out_var_data_t = 'BYTEA') THEN
        BEGIN
            p_column_val := decode(v_chr_val, 'hex');
        EXCEPTION
            WHEN OTHERS THEN
            RAISE invalid_binary_representation;
        END;
    ELSIF (v_out_var_data_t = 'XML') THEN
        BEGIN
            p_column_val := v_chr_val::XML;
        EXCEPTION
            WHEN OTHERS THEN
            RAISE invalid_xml_document;
        END;
    ELSE
        p_column_val := NULL;
    END IF;

    IF (v_chr_val IS NULL) THEN
        p_column_err := 1405;
    END IF;

    p_actual_len := coalesce(p_actual_len, 0);
    p_typvar_len := coalesce(p_typvar_len, 0);
EXCEPTION
    -- Cursor is not opened or does not exist
    WHEN invalid_cursor_state THEN
        RAISE USING MESSAGE := 'DBMS_SQL access denied.',
                    DETAIL := 'DBMS_SQL access was denied due to security concerns.',
                    HINT := '-29471';

    -- Column position is not declared in SELECT list
    WHEN null_value_not_allowed THEN
        RAISE USING MESSAGE := 'Column not in select list.',
                    DETAIL := 'A reference was made to a column not listed in the SELECT clause.',
                    HINT := '-1007';

    /* OUT argument base type does not match declared column base type.
       Possible column was declared through `dbms_sql$define_column` */
    WHEN most_specific_type_mismatch THEN
        RAISE USING MESSAGE := 'Type of OUT argument does not match declared column type.',
                    DETAIL := 'Attempting to get the value of a column but the type of the given OUT argument is different from the type of column that was defined previously.',
                    HINT := '-6562';

    -- Length of OUT argument is too small
    WHEN string_data_length_mismatch THEN
        RAISE USING MESSAGE := 'Character string buffer too small.',
                    DETAIL := 'Length of OUT argument is too small to store bind variable value.',
                    HINT := '-6502';

    -- Effective userid (OID) changed since cursor parse
    WHEN invalid_role_specification THEN
        RAISE USING MESSAGE := 'Effective userid is not the same as when cursor was parsed.',
                    DETAIL := 'The effective userid on this call to DBMS_SQL is not the same as that at the time the cursor was parsed.',
                    HINT := '-29470';

    -- Possible data type conversion error (char to numeric\money)
    WHEN invalid_text_representation THEN
        RAISE USING MESSAGE := 'Error during column value conversion (from char to numeric\money).',
                    DETAIL := format('Value %s is not correct for conversion to numeric\money.',
                                     quote_literal(v_chr_val));

    -- Possible data type conversion error (char to datetime\interval)
    WHEN invalid_datetime_format THEN
        RAISE USING MESSAGE := 'Error during column value conversion (from char to datetime\interval).',
                    DETAIL := format('Value %s is not correct for conversion to datetime\interval.',
                                     quote_literal(v_chr_val));

    -- Possible data type conversion error (char to binary data)
    WHEN invalid_binary_representation THEN
        RAISE USING MESSAGE := 'Error during column value conversion (from char to binary data).',
                    DETAIL := format('Value %s is not correct for conversion to binary data.',
                                     quote_literal(v_chr_val));

    -- Possible data type conversion error (char to XML document)
    WHEN invalid_xml_document THEN
        RAISE USING MESSAGE := 'Error during column value conversion (from char to XML document).',
                    DETAIL := format('Value %s is not correct for conversion to XML document.',
                                     quote_literal(v_chr_val));
END;

DECLARE
    v_chr_val TEXT;
    v_chr_binval BYTEA;
    v_defcol_data JSONB;
    v_cur_metadata JSONB;
    v_defcol_base_t TEXT;
    v_current_user_oid BIGINT;
    v_out_var_base_t TEXT := 'TEXT';
BEGIN
    -- Checking that cursor is opened (exit if it is not)
    IF (NOT aws_oracle_ext.dbms_sql$is_open(p_cursor_id)) THEN
        RAISE invalid_cursor_state;
    -- Length of the segment to retrieve (in bytes) should be 1 or above
    ELSIF (coalesce(p_byte_len, 0) = 0) THEN
        RAISE zero_length_character_string;
    END IF;

    -- Obtaining current user OID
    v_current_user_oid := aws_oracle_ext.get_user_oid_by_name(current_user);

    -- Getting cursor metadata (JSON) object
    v_cur_metadata := nullif(current_setting(format('sct$dbms_sql.cursor_id_%s_metadata', p_cursor_id), true), '');

    /* Checking that effective user OID (current_user) of the caller on this cursor is the same
       as that of the caller of the most recent parse operation on this cursor.
       Actual for cursors opened with security level 2 */
    IF ((v_cur_metadata ->> 'security_level')::SMALLINT = 2 AND
        (v_cur_metadata ->> 'parse_user_oid')::BIGINT != v_current_user_oid)
    THEN
        RAISE invalid_role_specification;
    END IF;

    -- Getting dynamic SQL statement defined columns info (JSON)
    v_defcol_data := (v_cur_metadata #> format('{defcols_data, col_%s}', p_column_pos)::TEXT[]);

    v_defcol_base_t := (v_defcol_data ->> 'base_type'); -- Column base data type

    -- Check if data is previously fetched from cursor
    IF ((v_cur_metadata ->> 'curr_row') IS NULL) THEN
        RAISE null_value_not_allowed;
    -- Column position should be declared in SELECT list
    ELSIF (v_defcol_base_t IS NULL) THEN
        RAISE too_many_columns;
    -- Check if OUT argument base type matches declared column base type
    ELSIF (v_out_var_base_t != substring(v_defcol_base_t, 1, 14)) THEN
        RAISE datatype_mismatch;
    -- Column should not be declared using `dbms_sql$define_column_char`
    ELSIF ((v_defcol_data ->> 'is_fixed')::SMALLINT = 1) THEN
        RAISE datatype_mismatch;
    END IF;

    BEGIN
        /* Extracting row data from execution results
           table `dbms_sql$recordset`
           (by cursor id, row number, column position) */
        SELECT chr_val
          INTO v_chr_val
          FROM dbms_sql$recordset
         WHERE cursor_id = p_cursor_id
           AND rownum = (v_cur_metadata ->> 'curr_row')::INTEGER
           AND column_pos = p_column_pos;
    EXCEPTION
        WHEN insufficient_privilege THEN
            -- Effective userid changed
            RAISE invalid_cursor_state;
    END;

    -- Extracting data from binary string and copying it into the OUT argument
    v_chr_binval := substring(v_chr_val::BYTEA from (p_offset + 1) for p_byte_len);
    p_column_val := convert_from(v_chr_binval, pg_client_encoding());

    /* Internal representation (bytes).
       4 bytes + actual length of the data */
    p_length_val := coalesce(pg_column_size(v_chr_binval), 0);

    -- Actual length of the data (bytes)
    p_typvar_len := coalesce(octet_length(v_chr_binval), 0);
EXCEPTION
    -- Cursor is not opened or does not exist
    WHEN invalid_cursor_state THEN
        RAISE USING MESSAGE := 'DBMS_SQL access denied.',
                    DETAIL := 'DBMS_SQL access was denied due to security concerns.',
                    HINT := '-29471';

    -- Length of the segment to retrieve (in bytes) is zero or less
    WHEN zero_length_character_string THEN
        RAISE USING MESSAGE := 'Numeric or value error.',
                    DETAIL := 'Length of the segment to retrieve (in bytes) must be 1 or above.',
                    HINT := '-6502';

    -- Resulting data was not previously fetched from cursor
    WHEN null_value_not_allowed THEN
        RAISE USING MESSAGE := 'This function can be called only after a fetch.',
                    DETAIL := 'The function was called before the fetch operation on cursor.',
                    HINT := '-1016';

    -- Column position is not declared in SELECT list
    WHEN too_many_columns THEN
        RAISE USING MESSAGE := 'Column not in select list.',
                    DETAIL := 'A reference was made to a column not listed in the SELECT clause.',
                    HINT := '-1007';

    /* OUT argument base type does not match declared column base type.
       Possible column was declared through `dbms_sql$define_column_char` */
    WHEN datatype_mismatch THEN
        RAISE USING MESSAGE := 'Type of OUT argument does not match declared column type.',
                    DETAIL := 'Attempting to get the value of a column but the type of the given OUT argument is different from the type of column that was defined previously.',
                    HINT := '-6562';

    -- Effective userid (OID) changed since cursor parse
    WHEN invalid_role_specification THEN
        RAISE USING MESSAGE := 'Effective userid is not the same as when cursor was parsed.',
                    DETAIL := 'The effective userid on this call to DBMS_SQL is not the same as that at the time the cursor was parsed.',
                    HINT := '-29470';
END;

DECLARE
    v_cur_metadata JSONB;
    v_defcols_data JSONB;
    v_col_size INTEGER;
    v_colvar_len INTEGER;
    v_defcol_base_t TEXT;
    v_current_user_oid BIGINT;
    v_defcol_data_t TEXT := upper(pg_typeof(p_column_var)::TEXT);
BEGIN
    -- Checking that cursor is opened
    IF (NOT aws_oracle_ext.dbms_sql$is_open(p_cursor_id)) THEN
        RAISE invalid_cursor_state;
    END IF;

    -- Obtaining current user OID
    v_current_user_oid := aws_oracle_ext.get_user_oid_by_name(current_user);

    -- Getting cursor metadata (JSON) object
    v_cur_metadata := nullif(current_setting(format('sct$dbms_sql.cursor_id_%s_metadata', p_cursor_id), true), '');

    /* Checking that effective user OID (current_user) of the caller on this cursor is the same
       as that of the caller of the most recent parse operation on this cursor.
       Actual for cursors opened with security level 2 */
    IF ((v_cur_metadata ->> 'security_level')::SMALLINT = 2 AND
        (v_cur_metadata ->> 'parse_user_oid')::BIGINT != v_current_user_oid)
    THEN
        RAISE invalid_role_specification;
    END IF;

    -- Column position must be within the range 1..cols_count
    IF (p_column_pos NOT BETWEEN 1 AND (v_cur_metadata ->> 'cols_count')::SMALLINT) THEN
        RAISE check_violation;
    END IF;

    -- Determining base data type of passed variable
    v_defcol_base_t := aws_oracle_ext.get_base_type_by_dtype(v_defcol_data_t);

    IF (v_defcol_base_t = 'TEXT')
    THEN
        v_colvar_len := coalesce(length(p_column_var::BYTEA, pg_client_encoding()), 0);
        v_col_size := CASE
                         WHEN p_col_size IS NOT NULL THEN p_col_size
                         WHEN v_colvar_len > 0 THEN v_colvar_len
                      END;
    ELSIF (v_defcol_base_t = 'BYTEA')
    THEN
        v_col_size := p_col_size;
    END IF;

    BEGIN
        -- Saving defined columns data into temp table
        INSERT INTO dbms_sql$define_column (cursor_id,
                                            column_pos,
                                            data_type,
                                            base_type,
                                            col_size,
                                            is_fixed)
             VALUES (p_cursor_id,
                     p_column_pos,
                     v_defcol_data_t,
                     v_defcol_base_t,
                     v_col_size,
                     null)
        ON CONFLICT (cursor_id, column_pos)
        DO UPDATE SET data_type = v_defcol_data_t,
                      base_type = v_defcol_base_t,
                      col_size = v_col_size,
                      is_fixed = null;
    EXCEPTION
        WHEN insufficient_privilege THEN
            -- Effective userid changed
            RAISE invalid_cursor_state;
    END;

    -- Serializing defined columns data into JSON object
    SELECT jsonb_object_agg(concat('col_', defcol_data.column_pos),
                            defcol_data.column_data)
      INTO v_defcols_data
      FROM (SELECT defcol3.column_pos,
                   (SELECT to_jsonb(defcol2.*)
                      FROM (SELECT defcol1.data_type,
                                   defcol1.base_type,
                                   defcol1.col_size,
                                   defcol1.is_fixed
                              FROM dbms_sql$define_column AS defcol1
                             WHERE defcol1.cursor_id = defcol3.cursor_id
                               AND defcol1.column_pos = defcol3.column_pos
                           ) AS defcol2
                   ) AS column_data
              FROM dbms_sql$define_column AS defcol3
             WHERE defcol3.cursor_id = p_cursor_id
           ) AS defcol_data;

    -- Setting "defcols_data" (JSON) cursor property
    v_cur_metadata := jsonb_set(v_cur_metadata, '{defcols_data}', v_defcols_data);

    -- Saving cursor metadata (JSON) object
    PERFORM set_config(format('sct$dbms_sql.cursor_id_%s_metadata', p_cursor_id),
                       v_cur_metadata::TEXT,
                       false);
EXCEPTION
    -- Cursor is not opened or does not exist
    WHEN invalid_cursor_state THEN
        RAISE USING MESSAGE := 'DBMS_SQL access denied.',
                    DETAIL := 'DBMS_SQL access was denied due to security concerns.',
                    HINT := '-29471';

    -- Column position lies outside of range 1..cols_count
    WHEN check_violation THEN
        RAISE USING MESSAGE := 'Column not in select list.',
                    DETAIL := 'Reference to a column not listed in the SELECT clause.',
                    HINT := '-1007';

    -- Effective userid (OID) changed since cursor parse
    WHEN invalid_role_specification THEN
        RAISE USING MESSAGE := 'Effective userid is not the same as when cursor was parsed.',
                    DETAIL := 'The effective userid on this call to DBMS_SQL is not the same as that at the time the cursor was parsed.',
                    HINT := '-29470';
END;

DECLARE v_cur_metadata JSONB; v_defcols_data JSONB; v_is_fixed SMALLINT := 1; v_current_user_oid BIGINT; v_defcol_base_t TEXT := 'TEXT'; BEGIN -- Checking that cursor is opened IF (NOT aws_oracle_ext.dbms_sql$is_open(p_cursor_id)) THEN RAISE invalid_cursor_state; END IF;
-- Obtaining current user OID v_current_user_oid := aws_oracle_ext.get_user_oid_by_name(current_user);
-- Getting cursor metadata (JSON) object v_cur_metadata := nullif(current_setting(format('sct$dbms_sql.cursor_id_%s_metadata', p_cursor_id), true), '');
/* Checking that effective user OID (current_user) of the caller on this cursor is the same as that of the caller of the most recent parse operation on this cursor. Actual for cursors opened with security level 2 */ IF ((v_cur_metadata ->> 'security_level')::SMALLINT = 2 AND (v_cur_metadata ->> 'parse_user_oid')::BIGINT != v_current_user_oid) THEN RAISE invalid_role_specification; END IF;
-- Column position must be within the range 1..cols_count IF (p_column_pos NOT BETWEEN 1 AND (v_cur_metadata ->> 'cols_count')::SMALLINT) THEN RAISE check_violation; END IF;
BEGIN -- Saving defined columns data into temp table INSERT INTO dbms_sql$define_column (cursor_id, column_pos, data_type, base_type, col_size, is_fixed) VALUES (p_cursor_id, p_column_pos, v_defcol_base_t, v_defcol_base_t, p_col_size, v_is_fixed) ON CONFLICT (cursor_id, column_pos) DO UPDATE SET data_type = v_defcol_base_t, base_type = v_defcol_base_t, col_size = p_col_size, is_fixed = v_is_fixed; EXCEPTION WHEN insufficient_privilege THEN -- Effective userid changed RAISE invalid_cursor_state; END;
-- Serializing defined columns data into JSON object SELECT jsonb_object_agg(concat('col_', defcol_data.column_pos), defcol_data.column_data) INTO v_defcols_data FROM (SELECT defcol3.column_pos, (SELECT to_jsonb(defcol2.*) FROM (SELECT defcol1.data_type, defcol1.base_type, defcol1.col_size, defcol1.is_fixed FROM dbms_sql$define_column AS defcol1 WHERE defcol1.cursor_id = defcol3.cursor_id AND defcol1.column_pos = defcol3.column_pos ) AS defcol2 ) AS column_data FROM dbms_sql$define_column AS defcol3 WHERE defcol3.cursor_id = p_cursor_id ) AS defcol_data;
-- Setting "defcols_data" (JSON) cursor property v_cur_metadata := jsonb_set(v_cur_metadata, '{defcols_data}', v_defcols_data);
-- Saving cursor metadata (JSON) object PERFORM set_config(format('sct$dbms_sql.cursor_id_%s_metadata', p_cursor_id), v_cur_metadata::TEXT, false); EXCEPTION -- Cursor is not opened or does not exist WHEN invalid_cursor_state THEN RAISE USING MESSAGE := 'DBMS_SQL access denied.', DETAIL := 'DBMS_SQL access was denied due to security concerns.', HINT := '-29471';
-- Column position lies outside of range 1..cols_count WHEN check_violation THEN RAISE USING MESSAGE := 'Column not in select list.', DETAIL := 'Reference to a column not listed in the SELECT clause.', HINT := '-1007';
-- Effective userid (OID) changed since cursor parse WHEN invalid_role_specification THEN RAISE USING MESSAGE := 'Effective userid is not the same as when cursor was parsed.', DETAIL := 'The effective userid on this call to DBMS_SQL is not the same as that at the time the cursor was parsed.', HINT := '-29470'; END;

BEGIN
    PERFORM aws_oracle_ext.dbms_sql$define_column(p_cursor_id,
                                                  p_column_pos,
                                                  ''::TEXT);
END;

DECLARE v_cur_metadata JSONB; v_curcols_data JSONB; v_colscount_block TEXT; v_colstype_block TEXT; v_prepdynsql_block TEXT; v_sql_statement TEXT; v_cols_count SMALLINT; v_cases_list TEXT; v_rows_count INTEGER; v_vars_sync_sql TEXT; v_col_aliases1 TEXT; v_col_aliases2 TEXT; v_coldtype_cases TEXT; v_into_clause TEXT; v_bind_var RECORD; v_bindvar_val TEXT; v_bindvar_col TEXT; v_bind_varstr TEXT; v_datatypes_rec RECORD; v_err_message TEXT; v_err_stack TEXT; v_defcols_hash TEXT; v_sql_cmd_type TEXT; v_sql_function_code SMALLINT; v_colstype_table_name TEXT; v_current_user_oid BIGINT; v_debug_mode BOOLEAN := 0; -- {1:ON, 0:OFF}. If set to "ON", engine logs all dynamically generated scripts into `dbms_sql$cursor` table BEGIN -- Getting cursor metadata (JSON) object v_cur_metadata := nullif(current_setting(format('sct$dbms_sql.cursor_id_%s_metadata', p_cursor_id), true), '');
/* If cursor was transformed from a REFCURSOR than it could not be executed */ IF ((v_cur_metadata ->> 'from_refcursor')::BOOLEAN) THEN RAISE invalid_cursor_definition; -- Checking that cursor is opened (exit if it is not) ELSIF (NOT aws_oracle_ext.dbms_sql$is_open(p_cursor_id)) THEN RAISE invalid_cursor_state; END IF;
-- Obtaining current user OID v_current_user_oid := aws_oracle_ext.get_user_oid_by_name(current_user);
/* Checking that effective user OID (current_user) of the caller on this cursor is the same as that of the caller of the most recent parse operation on this cursor. Actual for cursors opened with security level 1 and 2 */ IF ((v_cur_metadata ->> 'security_level')::SMALLINT IN (1, 2) AND (v_cur_metadata ->> 'parse_user_oid')::BIGINT != v_current_user_oid) THEN RAISE invalid_role_specification; END IF;
-- Getting dynamic SQL function code from cursor metadata (JSON) v_sql_function_code := (v_cur_metadata ->> 'sql_func_code');
-- Determining SQL command type from cursor metadata (JSON) v_sql_cmd_type := (v_cur_metadata ->> 'sql_cmd_type');
BEGIN -- Getting SQL-PL/pgSQL statement by cursor id SELECT rtrim(sql_statement, ';') INTO v_sql_statement FROM dbms_sql$cursor WHERE cursor_id = p_cursor_id; EXCEPTION WHEN insufficient_privilege THEN -- Effective userid changed RAISE invalid_cursor_state; END;
/* Associated SQL statement (PL/pgSQL blobk) should not be empty (NULL) or invalid (not recognized) */ IF (coalesce(v_sql_statement, '') = '' OR v_sql_cmd_type IS NULL) THEN -- ORA-01003: no statement parsed RAISE null_value_not_allowed; END IF;
-- Conditional execution by SQL command type IF (v_sql_cmd_type = 'DML') THEN /* Wrapper to resolve an issue with SELECT-INTO statement, where INTO clause supplied at the end of the statement string */ IF (v_sql_statement ~* '^(WITH\s+.*\s+AS\s+\(.*\s*)?SELECT\s+.*\s+INTO\s+([\$\:][[:alnum:]_$#\.]+(,\s*)?)+\s*$' AND v_sql_function_code NOT IN (3, -- INSERT 5, -- UPDATE 9)) -- DELETE THEN -- Extracting "INTO" clause (and related "$1, $2, $n" markers) from the end of the statement string v_into_clause := aws_oracle_ext.regexp_substr(v_sql_statement, '(\s*INTO\s*([\$\:][[:alnum:]_]+(,\s*)?)+)\s*$', 1, 1, 'i');
-- Removing "INTO" clause from the end of the statement string v_sql_statement := replace(v_sql_statement, v_into_clause, '');
-- Replacing "INTO" with "INTO STRICT" in order to emulate NO_DATA_FOUND & TOO_MANY_ROWS logic v_into_clause := regexp_replace(v_into_clause, 'INTO', 'INTO STRICT', 'gi');
-- Inserting "INTO" clause (and related "$1, $2, $n" markers) before "FROM" clause v_sql_statement := overlay(v_sql_statement placing concat(' ', ltrim(v_into_clause), ' ') from position('FROM' in upper(v_sql_statement)) - 1 for 1); ELSIF (v_sql_statement ~* 'RETURNING' AND v_sql_function_code IN (3, -- INSERT 5, -- UPDATE 9)) -- DELETE THEN -- Replacing "RETURNING col_name; INTO" with "RETURNING col_name INTO" v_sql_statement := regexp_replace(v_sql_statement, '(?:;)\s*INTO', ' INTO', 'gi'); END IF;
/* Binding local variables (instead of placeholders) associated with dynamic SQL query (PL/pgSQL block) */ FOR v_bind_var IN (SELECT * FROM dbms_sql$bind_variable WHERE cursor_id = p_cursor_id ORDER BY var_pos) LOOP CASE v_bind_var.base_type WHEN 'TEXT' THEN v_bindvar_col := 'chr_val'; v_bindvar_val := CASE WHEN v_bind_var.chr_val IS NOT NULL THEN quote_literal(CASE v_bind_var.data_type WHEN 'CHARACTER' THEN rpad(v_bind_var.chr_val, v_bind_var.value_size, ' ') ELSE substring(v_bind_var.chr_val, 1, v_bind_var.value_size) END) ELSE null END; WHEN 'NUMERIC' THEN v_bindvar_col := 'num_val'; v_bindvar_val := CASE WHEN v_bind_var.num_val IS NOT NULL THEN v_bind_var.num_val::TEXT ELSE null END; WHEN 'DOUBLE PRECISION' THEN v_bindvar_col := 'dprec_val'; v_bindvar_val := CASE WHEN v_bind_var.dprec_val IS NOT NULL THEN v_bind_var.dprec_val::TEXT ELSE null END; WHEN 'MONEY' THEN v_bindvar_col := 'money_val'; v_bindvar_val := CASE WHEN v_bind_var.money_val IS NOT NULL THEN format('MONEY ''%s''', v_bind_var.money_val::TEXT) ELSE null END; WHEN 'TIMESTAMP WITHOUT TIME ZONE' THEN v_bindvar_col := 'tstamp_val'; v_bindvar_val := CASE WHEN v_bind_var.tstamp_val IS NOT NULL THEN CASE v_bind_var.data_type WHEN 'DATE' THEN format('DATE ''%s''', to_char(v_bind_var.tstamp_val, 'yyyy-mm-dd')) WHEN 'TIME WITHOUT TIME ZONE' THEN format('TIME ''%s''', to_char(v_bind_var.tstamp_val, 'hh24:mi:ss.us')) ELSE format('TIMESTAMP ''%s''', to_char(v_bind_var.tstamp_val, 'yyyy-mm-dd hh24:mi:ss.us')) END ELSE null END; WHEN 'TIMESTAMP WITH TIME ZONE' THEN v_bindvar_col := 'tstamptz_val'; v_bindvar_val := CASE WHEN v_bind_var.tstamptz_val IS NOT NULL THEN format('TIMESTAMPTZ ''%s''', to_char(v_bind_var.tstamptz_val, 'yyyy-mm-dd hh24:mi:ss.usOF')) ELSE null END; WHEN 'TIME WITH TIME ZONE' THEN v_bindvar_col := 'timetz_val'; v_bindvar_val := CASE WHEN v_bind_var.timetz_val IS NOT NULL THEN format('TIMETZ ''%s''', v_bind_var.timetz_val::TEXT) ELSE null END; WHEN 'INTERVAL' THEN v_bindvar_col := 'interval_val'; v_bindvar_val := CASE WHEN v_bind_var.interval_val IS NOT NULL THEN format('INTERVAL ''%s''', v_bind_var.interval_val::TEXT) ELSE null END; WHEN 'BOOLEAN' THEN v_bindvar_col := 'bool_val'; v_bindvar_val := CASE WHEN v_bind_var.bool_val IS NOT NULL THEN upper(v_bind_var.bool_val::TEXT) ELSE null END; WHEN 'XML' THEN v_bindvar_col := 'xml_val'; v_bindvar_val := CASE WHEN v_bind_var.xml_val IS NOT NULL THEN quote_literal(v_bind_var.xml_val) ELSE null END; WHEN 'BYTEA' THEN v_bindvar_col := 'bin_val'; v_bindvar_val := CASE WHEN v_bind_var.bin_val IS NOT NULL THEN format('decode(%s, ''hex'')', quote_literal(encode(v_bind_var.bin_val, 'hex'))) ELSE null END; ELSE CONTINUE; END CASE;
-- Constructing dynamic block variables declaration v_bind_varstr := concat_ws(chr(10), v_bind_varstr, format(' v_%s %s%s', -- Variable declaration mask v_bind_var.var_name, -- Variable name CASE v_bind_var.data_type WHEN 'CHARACTER' THEN 'CHARACTER VARYING' ELSE v_bind_var.data_type END, -- Variable data type CASE -- Variable default value WHEN v_bindvar_val IS NULL THEN ';' ELSE format(' := %s;', v_bindvar_val) END));
-- Replacing all bind ":var_label" labels with proper `outer_block.v_var_name` vars v_sql_statement := regexp_replace(v_sql_statement, ':' || v_bind_var.var_name, 'outer_block.v_' || v_bind_var.var_name, 'gi');
/* Replacing all bind "$1[|,;-_]$2[|,;-_]$n" markers with the corresponding `outer_block.v_var_name` variable */ IF (v_bind_var.var_label IS NOT NULL) THEN v_sql_statement := regexp_replace(v_sql_statement, replace(concat('(', v_bind_var.var_label, ')(?!\d)'), '$', '\$'), 'outer_block.v_' || v_bind_var.var_name, 'gi'); END IF;
-- Generating bind variable (stored in `dbms_sql$bind_variable`) update scripts v_vars_sync_sql := concat_ws(chr(10), v_vars_sync_sql, '', format('-- Updating "%s" bind variable value', v_bind_var.var_name), 'UPDATE dbms_sql$bind_variable', format(' SET %s = %s', v_bindvar_col, CASE WHEN v_bind_var.data_type = 'TIME WITHOUT TIME ZONE' THEN format('to_char(outer_block.v_%s, ''2001-01-01 hh24:mi:ss.us'')::TIMESTAMP', v_bind_var.var_name) ELSE 'outer_block.v_' || v_bind_var.var_name END), ' WHERE cursor_id = outer_block.v_cursor_id', format(' AND var_name = %s;', quote_literal(v_bind_var.var_name))); END LOOP;
-- Getting dynamic SQL statement expected columns count v_cols_count := (v_cur_metadata ->> 'cols_count')::INTEGER;
IF (v_cols_count IS NULL AND -- Columns count variable is NULL (v_cur_metadata ->> 'colinfo_refresh')::SMALLINT = 1) -- Columns info needs to be refreshed THEN IF (v_sql_function_code = 4) -- SELECT THEN -- Determining the number of columns in dynamic SQL statement v_colscount_block := concat_ws(chr(10), 'DO $dbms_sql$', '<<outer_block>>', 'DECLARE', ' v_cur_metadata JSONB;', ' v_dynsql_row RECORD;', ' v_cols_count SMALLINT;', format(' v_cursor_id INTEGER := %s;', p_cursor_id), v_bind_varstr, -- Cursor vars declaration 'BEGIN', '-- Obtaining dynamic query structure', 'SELECT src.*', ' INTO outer_block.v_dynsql_row', ' FROM (', '--------------- Dynamic SQL statement ---------------', v_sql_statement, '--------------- Dynamic SQL statement ---------------', ' ) AS src', ' WHERE 1 = 2;', '', '-- Calculating dymamic sql statement columns count', 'SELECT COUNT(1)', ' INTO outer_block.v_cols_count', ' FROM json_object_keys(row_to_json(outer_block.v_dynsql_row));', '', '-- Getting cursor metadata (JSON) object', 'outer_block.v_cur_metadata :=', ' nullif(current_setting(format(''sct$dbms_sql.cursor_id_%s_metadata'',', ' outer_block.v_cursor_id), true), '''');', '', 'outer_block.v_cur_metadata := -- Setting "cols_count" (JSON) cursor property', ' jsonb_set(outer_block.v_cur_metadata, ''{cols_count}'', to_jsonb(outer_block.v_cols_count));', '', '-- Saving cursor metadata (JSON) object', 'PERFORM set_config(format(''sct$dbms_sql.cursor_id_%s_metadata'', outer_block.v_cursor_id),', ' outer_block.v_cur_metadata::TEXT, -- cursor metadata object', ' false);', 'END; $dbms_sql$');
EXECUTE v_colscount_block; -- Will be saved into `dbms_sql$cursor` ("colscount_block" column) if "v_debug_mode" set to "ON" (1)
-- Getting updated cursor metadata (JSON) object v_cur_metadata := nullif(current_setting(format('sct$dbms_sql.cursor_id_%s_metadata', p_cursor_id), true), '');
-- Getting dynamic SQL statement updated columns count v_cols_count := (v_cur_metadata ->> 'cols_count')::INTEGER; ELSE v_cols_count := 0; END IF; END IF;
/* Dynsql execution SELECT (v_cols_count > 0, results in rowset) or PL/pgSQL anonymous block (v_cols_count = 0 or is NULL) */ IF (v_cols_count > 0) THEN -- Generating list of consecutive column aliases (col_1, col_2, ...) SELECT string_agg(concat(repeat(' ', 10), 'col_', colnum), concat(',', chr(10))), string_agg(concat(repeat(' ', 28), 'col_', colnum), concat(',', chr(10))) INTO v_col_aliases1, v_col_aliases2 FROM generate_series(1, v_cols_count) AS colnum;
-- Refresh cursor columns data type info only if SQL statement was changed IF ((v_cur_metadata ->> 'colinfo_refresh')::SMALLINT = 1) THEN -- Generating unique temp table name v_colstype_table_name := format('dynsql_colstype_%s', lower(substring((SELECT string_agg(md5(random()::TEXT), '') FROM generate_series(1, ceil(14 / 32.)::INTEGER) ), 1, 14)));
-- Determining data type of columns in dynamic SQL statement v_colstype_block := concat_ws(chr(10), 'DO $dbms_sql$', '<<outer_block>>', 'DECLARE', format(' v_cursor_id INTEGER := %s;', p_cursor_id), v_bind_varstr, -- Cursor vars declaration 'BEGIN', '-- Deleting previous dynamic sql execution results', 'DELETE FROM dbms_sql$recordset', ' WHERE cursor_id = outer_block.v_cursor_id;', '', '-- Deleting previous dynamic sql columns info', 'DELETE FROM dbms_sql$cursor_column', ' WHERE cursor_id = outer_block.v_cursor_id;', '', '-- Creating temp table in order to reflect the columns structure', 'BEGIN', format('CREATE TEMP TABLE %s', v_colstype_table_name), 'WITH (OIDS = false)', 'ON COMMIT DROP', 'AS', 'SELECT src.* FROM (', '--------------- Dynamic SQL statement ---------------', v_sql_statement, -- Dynamic SQL statement where placeholders replaced with v_block vars '--------------- Dynamic SQL statement ---------------', ') AS src', 'WITH NO DATA;', -- Obtaining only dynamic query structure '', '/* Using column aliases instead of names', ' in case if any duplicate columns found */', 'EXCEPTION', 'WHEN duplicate_column THEN', '', format('CREATE TEMP TABLE %s', v_colstype_table_name), 'WITH (OIDS = false)', 'ON COMMIT DROP', 'AS', 'SELECT src.* FROM (', '--------------- Dynamic SQL statement ---------------', v_sql_statement, -- Dynamic SQL statement where placeholders replaced with v_block vars '--------------- Dynamic SQL statement ---------------', format(') AS src (%s)', trim(v_col_aliases1)), -- Consecutive column aliases list 'WITH NO DATA;', -- Get only dynamic query structure 'END;', '', concat_ws(chr(10), '-- Saving detailed column data (name, type, precision, etc.)', 'INSERT INTO dbms_sql$cursor_column (cursor_id,', ' column_pos,', ' col_name,', ' data_type,', ' base_type,', ' is_nullable,', ' chr_max_len,', ' chr_byte_len,', ' num_precision,', ' num_scale,', ' tstamp_precision,', ' interval_type,', ' interval_precision)', 'SELECT outer_block.v_cursor_id,', ' ordinal_position,', ' column_name,', ' upper(data_type),', ' aws_oracle_ext.get_base_type_by_dtype(upper(data_type)),', ' CASE is_nullable', ' WHEN ''YES'' THEN true', ' ELSE false', ' END,', ' character_maximum_length,', ' character_octet_length,', ' numeric_precision,', ' numeric_scale,', ' datetime_precision,', ' interval_type,', ' interval_precision', ' FROM information_schema.columns'), format(' WHERE table_name = ''%s''', v_colstype_table_name), ' ORDER BY ordinal_position ASC;', '', '-- Dropping temp table since its not needed anymore', format('DROP TABLE %s;', v_colstype_table_name), 'END; $dbms_sql$');
EXECUTE v_colstype_block; -- Will be saved into `dbms_sql$cursor` ("colstype_block" column) if "v_debug_mode" set to "ON" (1)
-- Serializing dynamic statement columns data into JSON object SELECT jsonb_object_agg(concat('col_', curcol_data.column_pos), curcol_data.column_data) INTO v_curcols_data FROM (SELECT curcol3.column_pos, (SELECT to_jsonb(curcol2.*) FROM (SELECT curcol1.data_type, curcol1.base_type, curcol1.chr_max_len, curcol1.chr_byte_len FROM dbms_sql$cursor_column AS curcol1 WHERE curcol1.cursor_id = curcol3.cursor_id AND curcol1.column_pos = curcol3.column_pos ) AS curcol2 ) AS column_data FROM dbms_sql$cursor_column AS curcol3 WHERE curcol3.cursor_id = p_cursor_id ) AS curcol_data;
-- Setting "curcols_data" (JSON) cursor property v_cur_metadata := jsonb_set(v_cur_metadata, '{curcols_data}', v_curcols_data);
-- Setting "colinfo_refresh" (JSON) cursor property v_cur_metadata := jsonb_set(v_cur_metadata, '{colinfo_refresh}', to_jsonb(0)); END IF;
-- Getting defined columns data hash v_defcols_hash := md5(v_cur_metadata ->> 'defcols_data');
IF (v_defcols_hash IS NOT NULL AND v_defcols_hash != coalesce(v_cur_metadata ->> 'defcols_hash', '')) THEN -- Setting "defcols_hash" (JSON) cursor property v_cur_metadata := jsonb_set(v_cur_metadata, '{defcols_hash}', to_jsonb(v_defcols_hash));
/* Checking for correspondence between actual and declared column base data types. Oracle ORA-00932: INCONSISTENT_TYPE error is raised if the type of the given OUT parameter value is different from the actual type of the value.
This type was given when the column was defined by calling `dbms_sql$define_column` procedure. */ FOR v_datatypes_rec IN (SELECT curcol.column_pos, curcol.data_type AS cur_data_type, curcol.base_type AS cur_base_type, defcol.data_type AS def_data_type, defcol.base_type AS def_base_type FROM dbms_sql$cursor_column AS curcol INNER JOIN dbms_sql$define_column AS defcol ON (defcol.cursor_id = curcol.cursor_id AND defcol.column_pos = curcol.column_pos AND (defcol.base_type != curcol.base_type OR (substring(curcol.data_type, 1, 5) = 'TIME ' AND defcol.base_type = 'TIMESTAMP WITHOUT TIME ZONE') OR (substring(defcol.data_type, 1, 5) = 'TIME ' AND curcol.base_type = 'TIMESTAMP WITHOUT TIME ZONE')) AND coalesce(defcol.is_fixed, 0) != 1) WHERE curcol.cursor_id = p_cursor_id) LOOP /* Next data types are not implicitly convertible: - TIMESTAMP WITHOUT TIME ZONE to TIMESTAMP WITH TIME ZONE (and vice versa) - TIME WITHOUT TIME ZONE to TIME WITH TIME ZONE (and vice versa) - NUMERIC to DOUBLE PRECISION (and vice versa)
Thus, they must not generate an INCONSISTENT_TYPE error since they could be explicitly casted to each other with `dbms_sql$column_value` function */ CONTINUE WHEN substring(v_datatypes_rec.cur_data_type, 1, 14) = substring(v_datatypes_rec.def_data_type, 1, 14) OR -- TIMESTAMPs substring(v_datatypes_rec.cur_data_type, 1, 5) = substring(v_datatypes_rec.def_data_type, 1, 5) OR -- TIMEs (v_datatypes_rec.cur_base_type IN ('NUMERIC', 'DOUBLE PRECISION') AND v_datatypes_rec.def_base_type IN ('NUMERIC', 'DOUBLE PRECISION')) OR ((v_datatypes_rec.cur_data_type = 'DATE' AND v_datatypes_rec.def_base_type = 'TIMESTAMP WITH TIME ZONE') OR (v_datatypes_rec.def_data_type = 'DATE' AND v_datatypes_rec.cur_base_type = 'TIMESTAMP WITH TIME ZONE'));
-- Inconsistent datatypes (Oracle error 'ORA-00932') RAISE datatype_mismatch; END LOOP; END IF;
/* Constructing CASEs to distribute dynamic query execution results through INSERT-SELECT into `dbms_sql$recordset` table */ WITH dtypes_order AS (SELECT 'TEXT' AS base_type, 1 AS dtype_order UNION ALL SELECT 'NUMERIC', 2 UNION ALL SELECT 'DOUBLE PRECISION', 3 UNION ALL SELECT 'MONEY', 4 UNION ALL SELECT 'TIMESTAMP WITHOUT TIME ZONE', 5 UNION ALL SELECT 'TIMESTAMP WITH TIME ZONE', 6 UNION ALL SELECT 'TIME WITH TIME ZONE', 7 UNION ALL SELECT 'INTERVAL', 8 UNION ALL SELECT 'BOOLEAN', 9 UNION ALL SELECT 'XML', 10 UNION ALL SELECT 'BYTEA', 11), -- Attaching current columns data curcols_data AS (SELECT base_type, array_agg(concat_ws(':', column_pos, data_type)) AS colpos_list FROM dbms_sql$cursor_column WHERE cursor_id = p_cursor_id GROUP BY base_type)
SELECT string_agg( coalesce(' CASE colpos' || chr(10) || (SELECT string_agg(format(' WHEN %s THEN %s', colpos, CASE dtype WHEN 'TIME WITHOUT TIME ZONE' -- List of types that require special conversion THEN format('to_char(col_%s, ''2001-01-01 hh24:mi:ss.us'')::TIMESTAMP', colpos) ELSE 'col_' || colpos END), chr(10)) FROM (SELECT substring(colpos, '^(\d+):?') AS colpos, substring(colpos, ':(.+)$') AS dtype FROM unnest(cdt.colpos_list) AS colpos) AS cols) || chr(10) || ' END', ' null'), ',' || chr(10) ORDER BY dto.dtype_order ASC) INTO v_coldtype_cases FROM dtypes_order dto LEFT JOIN curcols_data cdt ON (cdt.base_type = dto.base_type);
/* Constructing INSERT-SELECT (into `dbms_sql$recordset` table), required to distribute dynamic query execution results */ v_sql_statement := concat_ws(chr(10), '-- Deleting previous dynamic SQL execution results', 'DELETE FROM dbms_sql$recordset', ' WHERE cursor_id = outer_block.v_cursor_id;', '', '/* Execution of dynamic SQL statement and inserting its', ' resulting data into temp table dbms_sql$recordset */', 'INSERT INTO dbms_sql$recordset (cursor_id,', ' rownum,', ' column_pos,', ' chr_val,', ' num_val,', ' dprec_val,', ' money_val,', ' tstamp_val,', ' tstamptz_val,', ' timetz_val,', ' interval_val,', ' bool_val,', ' xml_val,', ' bin_val)', 'SELECT outer_block.v_cursor_id,', ' rownum,', ' colpos,', v_coldtype_cases, -- Coltypes distribution CASEs ' FROM (SELECT row_number() over (order by 1) AS rownum,', ' generate_series(1, outer_block.v_cols_count) AS colpos,', ' dynsql.*', ' FROM (', '--------------- Dynamic SQL statement ---------------', v_sql_statement, -- Dynamic SQL statement '--------------- Dynamic SQL statement ---------------', format(' ) AS dynsql (%s)', trim(v_col_aliases2)), -- List of consecutive column aliases ' ) AS dynsql2;'); END IF;
-- Removing "$$" and "LANGUAGE" markers in order to embed block into <<outer_block>> IF (v_sql_function_code = 34) -- PL/SQL (PL/pgSQL) block THEN -- Removing "$$" and "LANGUAGE" markers from the start of the statement v_sql_statement := regexp_replace(v_sql_statement, '(^\s*DO(:?\s+LANGUAGE\s+[[:alnum:]]+\s+)?\s*\$(\w*)?\$\s*(:?<<.+>>)?\s*)', concat(substring(v_sql_statement, '<<.+>>'), chr(10)), 'gi');
-- Removing "$$" and "LANGUAGE" markers from the end of the statement v_sql_statement := regexp_replace(v_sql_statement, '(END\s*(;)?\s*\$(\w*)?\$\s*(:?\s+LANGUAGE\s+[[:alnum:]]+)?\s*$)', 'END;', 'gi');
-- Removing any unnecessary blank strings (spaces) from the beginning and the end of SQL statement v_sql_statement := regexp_replace(v_sql_statement, '^\s*(?=\S*)|(?<=\S*)\s*$', '', 'gi'); END IF;
-- Saving cursor metadata (JSON) object PERFORM set_config(format('sct$dbms_sql.cursor_id_%s_metadata', p_cursor_id), v_cur_metadata::TEXT, -- cursor metadata object false);
IF (v_sql_function_code = 27) -- EXPLAIN THEN -- Creating special `explain_plan` bind variable to store query EXPLAIN plan PERFORM aws_oracle_ext.dbms_sql$bind_variable(p_cursor_id, 'explain_plan', ''::TEXT, p_check_var_exists => false);
-- Dynamic EXPLAIN plan execution wrapper v_prepdynsql_block := concat_ws(chr(10), 'DO $dbms_sql$', '<<outer_block>>', 'DECLARE', ' v_exp_plan TEXT;', ' v_exp_plan_arr TEXT[];', ' r_explain_plan RECORD;', format(' v_cursor_id INTEGER := %s;', p_cursor_id), v_bind_varstr, -- Cursor vars declaration 'BEGIN', 'FOR r_explain_plan IN', '----------- Dynamic EXPLAIN plan statement -----------', v_sql_statement, -- Dynamic EXPLAIN statement, placeholders replaced with v_block vars '----------- Dynamic EXPLAIN plan statement -----------', 'LOOP', ' v_exp_plan_arr := array_append(v_exp_plan_arr, r_explain_plan."QUERY PLAN");', 'END LOOP;', '', '-- Updating "explain_plan" bind variable value', 'UPDATE dbms_sql$bind_variable', ' SET chr_val = array_to_string(v_exp_plan_arr, chr(10))', ' WHERE cursor_id = outer_block.v_cursor_id', ' AND var_name = ''explain_plan'';', 'END; $dbms_sql$'); ELSE -- Dynamic PL/pgSQL block (or DML with INTO clause) execution wrapper v_prepdynsql_block := concat_ws(chr(10), 'DO $dbms_sql$', '<<outer_block>>', 'DECLARE', ' v_cur_metadata JSONB;', ' v_rows_count INTEGER;', format(' v_cols_count SMALLINT := %s;', v_cols_count), format(' v_cursor_id INTEGER := %s;', p_cursor_id), v_bind_varstr, -- Cursor vars declaration 'BEGIN', '------- Dynamic PL/pgSQL block (SQL statement) -------', concat(v_sql_statement, -- Dynamic SQL or PL/pgSQL statement, placeholders replaced with v_block vars CASE WHEN v_sql_statement !~ ';$' THEN ';' END), '------- Dynamic PL/pgSQL block (SQL statement) -------', '', 'GET DIAGNOSTICS outer_block.v_rows_count := ROW_COUNT;', -- Count of rows affected by dynamic query execution '', '-- Getting cursor metadata (JSON) object', 'outer_block.v_cur_metadata :=', ' nullif(current_setting(format(''sct$dbms_sql.cursor_id_%s_metadata'',', ' outer_block.v_cursor_id), true), '''');', '', '-- Setting "rows_count" (JSON) cursor property', 'outer_block.v_cur_metadata :=', ' jsonb_set(outer_block.v_cur_metadata,', ' ''{rows_count}'',', ' CASE outer_block.v_cols_count', ' WHEN 0 THEN to_jsonb(outer_block.v_rows_count)', ' ELSE to_jsonb(outer_block.v_rows_count / outer_block.v_cols_count)', ' END);', '', '-- Setting "curr_row" (JSON) cursor property', 'outer_block.v_cur_metadata := jsonb_set(outer_block.v_cur_metadata, ''{curr_row}'', JSONB ''null'');', '', '-- Saving cursor metadata (JSON) object', 'PERFORM set_config(format(''sct$dbms_sql.cursor_id_%s_metadata'', outer_block.v_cursor_id),', ' outer_block.v_cur_metadata::TEXT, -- cursor metadata object', ' false);', v_vars_sync_sql, -- Update `dbms_sql$bind_variable` table with new values for binded vars 'END; $dbms_sql$'); END IF;
EXECUTE v_prepdynsql_block; -- Will be saved into `dbms_sql$cursor` ("prepdynsql_block" column) if "v_debug_mode" set to "ON" (1)
/* Logging of all dynamically generated scripts into `dbms_sql$cursor` if "v_debug_mode" set to "ON" (1) */ IF (v_debug_mode) THEN UPDATE dbms_sql$cursor SET colscount_block = coalesce(v_colscount_block, colscount_block), colstype_block = coalesce(v_colstype_block, colstype_block), prepdynsql_block = v_prepdynsql_block WHERE cursor_id = p_cursor_id; END IF; ELSIF (v_sql_cmd_type = 'SCL') THEN -- Skipping DDL, DCL, TCL, SCS EXECUTE v_sql_statement; END IF;
-- Setting `last_affected_cursor_id` variable PERFORM set_config('sct$dbms_sql.last_affected_cursor_id', p_cursor_id::TEXT, false);
-- Nulling `last_row_count` variable PERFORM set_config('sct$dbms_sql.last_row_count', null, false);
-- Getting updated cursor metadata (JSON) object v_cur_metadata := nullif(current_setting(format('sct$dbms_sql.cursor_id_%s_metadata', p_cursor_id), true), '');
RETURN CASE v_sql_function_code WHEN 4 -- SELECT THEN 0 -- Executing a SQL SELECT query returns 0 rows ELSE coalesce((v_cur_metadata ->> 'rows_count')::NUMERIC, 0) END; EXCEPTION -- Cursor is not opened or does not exist WHEN invalid_cursor_state THEN RAISE USING MESSAGE := 'DBMS_SQL access denied.', DETAIL := 'DBMS_SQL access was denied due to security concerns.', HINT := '-29471';
-- Trying to execute a REFCURSOR-sourced cursor WHEN invalid_cursor_definition THEN RAISE USING MESSAGE := 'Invalid cursor.', DETAIL := 'Can not repeatedly execute a REFCURSOR-sourced cursor.', HINT := '-1001';
-- Associated SQL statement is empty (NULL) or invalid WHEN null_value_not_allowed THEN RAISE USING MESSAGE := 'No statement parsed.', DETAIL := 'No valid SQL statement associated with DBMS_SQL cursor.', HINT := '-1003';
-- Effective userid (OID) changed since cursor parse WHEN invalid_role_specification THEN RAISE USING MESSAGE := 'Effective userid is not the same as when cursor was parsed.', DETAIL := 'The effective userid on this call to DBMS_SQL is not the same as that at the time the cursor was parsed.', HINT := '-29470';
-- Actual and declared base data types of a column do not match WHEN datatype_mismatch THEN RAISE USING MESSAGE := format('Inconsistent datatypes: expected %s got %s.', v_datatypes_rec.def_data_type, v_datatypes_rec.cur_data_type), DETAIL := format('Actual and declared data types for column in position %s do not match.', v_datatypes_rec.column_pos), HINT := '-932';
-- Undefined parameter in dynamic SELECT statement WHEN undefined_parameter THEN IF (v_sql_function_code = 4) -- SELECT THEN -- Not all variables bound RAISE USING MESSAGE := 'Not all variables bound.', DETAIL := 'SQL statement (PL/pgSQL block) containing bind variables was attempted to execute without all variables bound.', HINT := '-1008'; ELSE RAISE; END IF;
-- Some syntax error occurred WHEN syntax_error THEN GET STACKED DIAGNOSTICS v_err_message = MESSAGE_TEXT, v_err_stack = PG_EXCEPTION_CONTEXT;
IF ((v_err_message ~* '^syntax error at or near ":|\$\d+"$' OR v_err_message ~* '^"\$\d+" is not a known variable$') AND v_sql_cmd_type IN ('DML', 'SCL')) THEN IF (v_sql_cmd_type = 'DML') THEN -- Not all variables bound RAISE USING MESSAGE := 'Not all variables bound.', DETAIL := 'SQL statement (PL/pgSQL block) containing bind variables was attempted to execute without all variables bound.', HINT := '-1008'; ELSIF (v_sql_cmd_type = 'SCL') THEN -- Bind variables not allowed for SCL RAISE USING MESSAGE := 'Bind variables not allowed for SCL operations.', DETAIL := 'Bind variables are not allowed for session control language operations.', HINT := '-1027'; END IF; ELSIF (v_sql_function_code = 27) -- EXPLAIN THEN -- Invalid EXPLAIN statement RAISE USING MESSAGE := 'EXPLAIN statement is not valid.', DETAIL := 'EXPLAIN statement contains syntax or execution errors.', HINT := 'Correct the statement and try to perform `dbms_sql$execute` procedure again.'; ELSE RAISE USING MESSAGE := 'Syntax error has occured while trying to execute dynamic SQL statement.', DETAIL := concat(v_err_message, '.'), HINT := concat_ws(chr(10), '(Call Stack)', v_err_stack); END IF; END;

DECLARE
    v_cur_metadata JSONB;
    v_rows_count INTEGER;
    v_rows_fetched INTEGER;
BEGIN
    -- Executing a statement associated with the given cursor
    PERFORM aws_oracle_ext.dbms_sql$execute(p_cursor_id);

    -- Getting cursor metadata (JSON) object
    v_cur_metadata := nullif(current_setting(format('sct$dbms_sql.cursor_id_%s_metadata', p_cursor_id), true), '');

    -- Getting the count of rows of a statement after it has been executed
    v_rows_count := coalesce((v_cur_metadata ->> 'rows_count')::INTEGER, 0);

    -- Fetching a row from the given cursor
    v_rows_fetched := aws_oracle_ext.dbms_sql$fetch_rows(p_cursor_id);

    /* Raise an exception if the number of rows actually matching the query differs from 1.
       This behaviour is actual if "p_exact_match" param passed value is TRUE.
       -----------------------------------------------------------------------
       "ORA-01403: no data found" - if `v_rows_count` value is NULL or zero.
       "ORA-01422: exact fetch returns more than requested number of rows" - if `v_rows_count` value is more than one.
    */
    IF (p_exact_match)
    THEN
        IF (v_rows_count > 1)
        THEN
            -- ORA-01422
            RAISE too_many_rows;
        ELSIF (coalesce(v_rows_count, 0) = 0)
        THEN
            -- ORA-01403
            RAISE no_data_found;
        END IF;
    END IF;

    RETURN v_rows_fetched;
EXCEPTION
    -- Cursor fetch returns more than one row
    WHEN too_many_rows THEN
        RAISE USING MESSAGE := 'Exact fetch returns more than requested number of rows.',
                    DETAIL := 'The number specified in exact fetch is less than the rows returned.',
                    HINT := '-1422';

    -- Fetch from cursor results in zero rows
    WHEN no_data_found THEN
        RAISE USING MESSAGE := 'No data found.',
                    DETAIL := 'Fetch from cursor results in zero rows.',
                    HINT := '-1403';
END;

DECLARE
    v_cur_metadata JSONB;
    v_curr_row INTEGER;
    v_rows_count INTEGER;
    v_datatypes_rec RECORD;
    v_defcols_hash VARCHAR;
    v_last_row_count INTEGER;
    v_defcols_prevhash VARCHAR;
    v_current_user_oid BIGINT;
    v_rows_fetched INTEGER := 0;
BEGIN
    -- Checking that cursor is opened (exit if it is not)
    IF (NOT aws_oracle_ext.dbms_sql$is_open(p_cursor_id)) THEN
        RAISE invalid_cursor_state;
    END IF;

    -- Getting cursor metadata (JSON) object
    v_cur_metadata := nullif(current_setting(format('sct$dbms_sql.cursor_id_%s_metadata', p_cursor_id), true), '');

    -- Obtaining current user OID
    v_current_user_oid := aws_oracle_ext.get_user_oid_by_name(current_user);

    /* Checking that effective user OID (current_user) of the caller on this cursor is the same
       as that of the caller of the most recent parse operation on this cursor.
       Actual for cursors opened with security level 2 */
    IF ((v_cur_metadata ->> 'security_level')::SMALLINT = 2 AND
        (v_cur_metadata ->> 'parse_user_oid')::BIGINT != v_current_user_oid)
    THEN
        RAISE invalid_role_specification;
    END IF;

    /* Additional check and operations if cursor
       was transformed from a REFCURSOR */
    IF ((v_cur_metadata ->> 'from_refcursor')::BOOLEAN) THEN
        -- Getting defined columns data hash
        v_defcols_hash := md5(v_cur_metadata ->> 'defcols_data');

        IF (v_defcols_hash IS NOT NULL AND
            v_defcols_hash != coalesce(v_cur_metadata ->> 'defcols_hash', ''))
        THEN
            -- Setting `defcols_hash` (JSON) cursor property
            v_cur_metadata := jsonb_set(v_cur_metadata, '{defcols_hash}', to_jsonb(v_defcols_hash));

            BEGIN
                /* Checking for correspondence between actual and declared column base data types
                   Oracle ORA-00932: INCONSISTENT_TYPE error is raised if the type of the given OUT parameter value
                   is different from the actual type of the value.
                   This type was the given type when the column was defined by calling `dbms_sql$define_column`.
                */
                FOR v_datatypes_rec IN
                (SELECT curcol.column_pos,
                        curcol.data_type AS cur_data_type,
                        defcol.data_type AS def_data_type
                   FROM dbms_sql$cursor_column AS curcol
                        INNER JOIN dbms_sql$define_column AS defcol ON (defcol.cursor_id = curcol.cursor_id AND
                                                                        defcol.column_pos = curcol.column_pos AND
                                                                        (defcol.base_type != curcol.base_type OR
                                                                         defcol.data_type IN ('TIMESTAMP WITHOUT TIME ZONE',
                                                                                              'TIMESTAMP WITH TIME ZONE') AND
                                                                         curcol.data_type IN ('TIME WITHOUT TIME ZONE',
                                                                                              'TIME WITH TIME ZONE')) AND
                                                                        coalesce(defcol.is_fixed, 0) != 1)
                  WHERE curcol.cursor_id = p_cursor_id)
                LOOP
                    -- Data types TIMESTAMP WITHOUT TIME ZONE and TIMESTAMP WITH TIME ZONE are interconvertible, thus they should not generate an error
                    CONTINUE WHEN substring(v_datatypes_rec.cur_data_type, 1, 14) = substring(v_datatypes_rec.def_data_type, 1, 14);

                    RAISE datatype_mismatch;
                END LOOP;
            EXCEPTION
                WHEN insufficient_privilege THEN
                    -- Effective userid changed
                    RAISE invalid_cursor_state;
            END;
        END IF;

        -- Getting `last_row_count` variable value
        v_last_row_count := nullif(current_setting('sct$dbms_sql.last_row_count', true), '');

        -- Setting `last_row_count` variable
        PERFORM set_config('sct$dbms_sql.last_row_count', (v_last_row_count + 1)::TEXT, false);
    END IF;

    -- Perform fetching while there are rows to fetch
    IF (coalesce((v_cur_metadata ->> 'curr_row')::INTEGER, 0) <= (v_cur_metadata ->> 'rows_count')::INTEGER)
    THEN
        -- Fetch: 1 {curr_row < rows_count}, 0 {curr_row = rows_count}
        v_rows_fetched := (coalesce((v_cur_metadata ->> 'curr_row')::INTEGER, 0) < (v_cur_metadata ->> 'rows_count')::INTEGER)::INTEGER;

        -- Setting `curr_row` (JSON) cursor property
        v_cur_metadata := jsonb_set(v_cur_metadata, '{curr_row}', to_jsonb(coalesce((v_cur_metadata ->> 'curr_row')::INTEGER, 0) + 1));
    ELSE
        -- ORA-01002: fetch out of sequence
        RAISE null_value_not_allowed;
    END IF;

    -- Saving cursor metadata (JSON) object
    PERFORM set_config(format('sct$dbms_sql.cursor_id_%s_metadata', p_cursor_id),
                       v_cur_metadata::TEXT,
                       false);

    RETURN v_rows_fetched;
EXCEPTION
    -- Cursor is not opened or does not exist
    WHEN invalid_cursor_state THEN
        RAISE USING MESSAGE := 'DBMS_SQL access denied.',
                    DETAIL := 'DBMS_SQL access was denied due to security concerns.',
                    HINT := '-29471';

    -- Actual and declared base data types of a column do not match
    WHEN datatype_mismatch THEN
        RAISE USING MESSAGE := format('Inconsistent datatypes: expected %s got %s.',
                                      v_datatypes_rec.def_data_type,
                                      v_datatypes_rec.cur_data_type),
                    DETAIL := format('Actual and declared data types for column in position %s do not match.',
                                     v_datatypes_rec.column_pos),
                    HINT := '-932';

    -- Fetch from invalid cursor
    WHEN null_value_not_allowed THEN
        RAISE USING MESSAGE := 'Fetch out of sequence.',
                    DETAIL := 'A fetch has been attempted from a cursor which is no longer valid.',
                    HINT := '-1002';

    -- Effective userid (OID) changed since cursor parse
    WHEN invalid_role_specification THEN
        RAISE USING MESSAGE := 'Effective userid is not the same as when cursor was parsed.',
                    DETAIL := 'The effective userid on this call to DBMS_SQL is not the same as that at the time the cursor was parsed.',
                    HINT := '-29470';
END;

BEGIN -- Creating all necessary temp tables if they're not created before IF (NOT coalesce(nullif(current_setting('sct$dbms_sql.temp_tables_init', true), ''), 'false')::BOOLEAN) THEN -- Explicit tables dropping, to remove existing ones DROP TABLE IF EXISTS dbms_sql$cursor_column CASCADE;
DROP TABLE IF EXISTS dbms_sql$cursor CASCADE;
DROP TABLE IF EXISTS dbms_sql$define_column;
DROP TABLE IF EXISTS dbms_sql$bind_variable;
DROP TABLE IF EXISTS dbms_sql$recordset;
-- Temp table for storing cursor data (id, SQL statement etc.) CREATE TEMP TABLE dbms_sql$cursor ( cursor_id INTEGER, sql_statement TEXT, colscount_block TEXT, colstype_block TEXT, prepdynsql_block TEXT, CONSTRAINT pk_cursor PRIMARY KEY (cursor_id) ) WITH (OIDS = false) ON COMMIT PRESERVE ROWS;
-- Temp table for storing defined columns data (position, data type etc.) CREATE TEMP TABLE dbms_sql$define_column ( cursor_id INTEGER, column_pos INTEGER, data_type VARCHAR, base_type VARCHAR, col_size INTEGER, is_fixed SMALLINT, CONSTRAINT pk_def_column PRIMARY KEY (cursor_id, column_pos), CONSTRAINT fk_defcol_cursor FOREIGN KEY (cursor_id) REFERENCES dbms_sql$cursor (cursor_id) ON DELETE CASCADE ) WITH (OIDS = false) ON COMMIT PRESERVE ROWS;
-- Temp table for storing actual columns data (position, data type etc.) CREATE TEMP TABLE dbms_sql$cursor_column ( cursor_id INTEGER, column_pos INTEGER, col_name VARCHAR, data_type VARCHAR, base_type VARCHAR, is_nullable BOOLEAN, chr_max_len INTEGER, chr_byte_len INTEGER, num_precision SMALLINT, num_scale SMALLINT, tstamp_precision SMALLINT, interval_type VARCHAR, interval_precision SMALLINT, CONSTRAINT pk_cur_column PRIMARY KEY (cursor_id, column_pos), CONSTRAINT fk_curcol_cursor FOREIGN KEY (cursor_id) REFERENCES dbms_sql$cursor (cursor_id) ON DELETE CASCADE ) WITH (OIDS = false) ON COMMIT PRESERVE ROWS;
-- Temp table for storing binded variables data (name, data type, value etc.) CREATE TEMP TABLE dbms_sql$bind_variable ( cursor_id INTEGER, var_pos INTEGER, var_name VARCHAR, var_label VARCHAR, data_type VARCHAR, base_type VARCHAR, var_size INTEGER, value_size INTEGER, is_fixed SMALLINT, chr_val TEXT, num_val NUMERIC, dprec_val DOUBLE PRECISION, money_val MONEY, tstamp_val TIMESTAMP WITHOUT TIME ZONE, tstamptz_val TIMESTAMP WITH TIME ZONE, timetz_val TIME WITH TIME ZONE, interval_val INTERVAL, bool_val BOOLEAN, xml_val XML, bin_val BYTEA, CONSTRAINT pk_bind_var PRIMARY KEY (cursor_id, var_name), CONSTRAINT fk_bindvar_cur FOREIGN KEY (cursor_id) REFERENCES dbms_sql$cursor (cursor_id) ON DELETE CASCADE ) WITH (OIDS = false) ON COMMIT PRESERVE ROWS;
-- Temp table for storing dynamic statement execution results (parsed SQL record sets) CREATE TEMP TABLE dbms_sql$recordset ( cursor_id INTEGER, rownum INTEGER, column_pos INTEGER, chr_val TEXT, num_val NUMERIC, dprec_val DOUBLE PRECISION, money_val MONEY, tstamp_val TIMESTAMP WITHOUT TIME ZONE, tstamptz_val TIMESTAMP WITH TIME ZONE, timetz_val TIME WITH TIME ZONE, interval_val INTERVAL, bool_val BOOLEAN, xml_val XML, bin_val BYTEA, CONSTRAINT pk_record_set PRIMARY KEY (cursor_id, rownum, column_pos), CONSTRAINT fk_recset_cursor FOREIGN KEY (cursor_id) REFERENCES dbms_sql$cursor (cursor_id) ON DELETE CASCADE, CONSTRAINT fk_recset_curcol FOREIGN KEY (cursor_id, column_pos) REFERENCES dbms_sql$cursor_column (cursor_id, column_pos) ) WITH (OIDS = false) ON COMMIT PRESERVE ROWS;
-- Marking package "dbms_sql" as initialized PERFORM set_config('sct$dbms_sql.temp_tables_init', 'true', false); END IF; EXCEPTION -- Effective userid (OID) changed WHEN insufficient_privilege THEN RAISE USING MESSAGE := 'DBMS_SQL access denied.', DETAIL := 'DBMS_SQL access was denied due to security concerns.', HINT := '-29471'; END;

BEGIN
    -- Checking if cursor metadata (JSON) exists, thus cursor is opened
    RETURN nullif(current_setting(format('sct$dbms_sql.cursor_id_%s_metadata', p_cursor_id), true), '') IS NOT NULL;
END;

DECLARE
    v_field_name VARCHAR;
    v_cur_metadata JSONB;
    v_last_row_count INTEGER;
    v_sql_function_code SMALLINT;
    v_last_affected_cursor_id INTEGER;
BEGIN
    /* Getting the most recently changed cursor id,
       affected by DBMS_SQL procs or funcs:

       OPEN, CLOSE
       EXECUTE, EXECUTE_AND_FETCH
       TO_CURSOR_NUMBER, TO_REFCURSOR
    */
    v_last_affected_cursor_id := nullif(current_setting('sct$dbms_sql.last_affected_cursor_id', true), '');

    -- Getting `last_row_count` variable value
    v_last_row_count := nullif(current_setting('sct$dbms_sql.last_row_count', true), '');

    -- Getting cursor metadata (JSON) object
    v_cur_metadata := nullif(current_setting(format('sct$dbms_sql.cursor_id_%s_metadata',
                                                    v_last_affected_cursor_id), true), '');

    -- Getting dynamic SQL function code from cursor metadata (JSON)
    v_sql_function_code := (v_cur_metadata ->> 'sql_func_code');

    -- func_code 4 for SELECT
    v_field_name := CASE v_sql_function_code
                       WHEN 4 THEN 'curr_row'
                       ELSE 'rows_count'
                    END;

    RETURN coalesce(v_last_row_count,
                    coalesce((v_cur_metadata ->> v_field_name)::NUMERIC, 0));
END;

DECLARE
    v_cursor_id INTEGER;
    v_sql_statement TEXT;
    v_cur_metadata JSONB;
    v_actual_cursor_id INTEGER;
    v_function_code INTEGER := 0;
    v_last_parsed_cur_id INTEGER;
    v_last_opened_cur_id INTEGER;
    v_last_cursor_func_code NUMERIC;
BEGIN
    -- Getting the most recently parsed cursor id
    v_last_parsed_cur_id := nullif(current_setting('sct$dbms_sql.last_parsed_cursor_id', true), '');

    -- Getting the most recently opened cursor id
    v_last_opened_cur_id := coalesce(nullif(current_setting('sct$dbms_sql.last_opened_cursor_id', true), '')::INTEGER, 0);

    -- Recently opened cursor id is preferred over the last parsed one
    v_actual_cursor_id := greatest(v_last_opened_cur_id, v_last_parsed_cur_id);

    -- Getting last parsed (opened) cursor metadata (JSON) object
    v_cur_metadata := nullif(current_setting(format('sct$dbms_sql.cursor_id_%s_metadata', v_actual_cursor_id), true), '');

    -- Getting `last_cursor_func_code` variable
    v_last_cursor_func_code := nullif(current_setting('sct$dbms_sql.last_cursor_func_code', true), '');

    IF (p_cursor_id IS NULL AND
        v_cur_metadata IS NULL AND
        v_last_cursor_func_code IS NOT NULL)
    THEN
        RETURN v_last_cursor_func_code;
    ELSE
        v_cursor_id := coalesce(p_cursor_id, v_actual_cursor_id);
    END IF;

    /* If the cursor is not opened,
       exit the function with code 0 */
    IF (NOT aws_oracle_ext.dbms_sql$is_open(v_cursor_id))
    THEN
        RETURN v_function_code;
    END IF;

    -- Getting SQL-PL/pgSQL statement by cursor id
    SELECT sql_statement
      INTO v_sql_statement
      FROM dbms_sql$cursor
     WHERE cursor_id = v_cursor_id;

    /* See list of possible function codes here:
       https://docstore.mik.ua/orelly/oracle/bipack/ch02_03.htm
    */
    IF (v_sql_statement ~* '^\s*(CREATE\s+(?:(?:GLOBAL\s+)|(?:LOCAL\s+))?TEMPORARY\s+TABLE\s+)|(CREATE\s+(?:(?:GLOBAL\s+)|(?:LOCAL\s+))?TEMP\s+TABLE\s+)|(CREATE\s+(?:UNLOGGED\s+)?TABLE\s+)')
    THEN -- CREATE TABLE
        v_function_code := 1;
    ELSIF (v_sql_statement ~* '^\s*(SET\s+ROLE)\s+')
    THEN -- SET ROLE
        v_function_code := 2;
    ELSIF (v_sql_statement ~* '^\s*INSERT\s+INTO\s+')
    THEN -- INSERT INTO
        v_function_code := 3;
    ELSIF (v_sql_statement ~* '^\s*(WITH\s+.*\s+AS\s+\(.*\s*)?SELECT\s+.*\s+FROM\s+' AND
           v_sql_statement !~* '^\s*(WITH\s+.*\s+AS\s+\(.*\s*)?SELECT\s+.*\s+INTO\s+([\$\:][[:alnum:]_$#\.]+(,\s*)?)+\s*;?\s*$')
    THEN -- [CTE ]SELECT
        v_function_code := 4;
    ELSIF (v_sql_statement ~* '^\s*UPDATE\s+.*\s+SET\s+')
    THEN -- UPDATE
        v_function_code := 5;
    ELSIF (v_sql_statement ~* '^\s*MERGE\s+INTO\s+')
    THEN -- MERGE
        v_function_code := 189;
    ELSIF (v_sql_statement ~* '^\s*(DROP\s+ROLE)\s+')
    THEN -- DROP ROLE
        v_function_code := 6;
    ELSIF (v_sql_statement ~* '^\s*(DROP\s+VIEW)\s+')
    THEN -- DROP VIEW
        v_function_code := 7;
    ELSIF (v_sql_statement ~* '^\s*(DROP\s+TABLE\s+)')
    THEN -- DROP TABLE
        v_function_code := 8;
    ELSIF (v_sql_statement ~* '^\s*(DELETE)\s+')
    THEN -- DELETE
        v_function_code := 9;
    ELSIF (v_sql_statement ~* '^\s*(CREATE\s+(?:OR\s+REPLACE\s+)?(?:TEMP\s+)?(?:TEMPORARY\s+)?VIEW)\s+')
    THEN -- CREATE (OR REPLACE) (TEMPORARY | TEMP) VIEW
        v_function_code := 10;
    ELSIF (v_sql_statement ~* '^\s*(DROP\s+USER)\s+|(DROP\s+SCHEMA)\s+')
    THEN -- DROP USER
        v_function_code := 11;
    ELSIF (v_sql_statement ~* '^\s*(CREATE\s+ROLE)\s+')
    THEN -- CREATE ROLE
        v_function_code := 12;
    ELSIF (v_sql_statement ~* '^\s*(CREATE\s+(?:TEMPORARY\s+)?SEQUENCE)|(CREATE\s+(?:TEMP\s+)?SEQUENCE)\s+')
    THEN -- CREATE (TEMPORARY | TEMP) SEQUENCE
        v_function_code := 13;
    ELSIF (v_sql_statement ~* '^\s*(ALTER\s+SEQUENCE)\s+')
    THEN -- ALTER SEQUENCE
        v_function_code := 14;
    ELSIF (v_sql_statement ~* '^\s*(DROP\s+SEQUENCE)\s+')
    THEN -- DROP SEQUENCE
        v_function_code := 16;
    ELSIF (v_sql_statement ~* '^\s*(CREATE\s+CLUSTER)\s+')
    THEN -- CREATE CLUSTER
        v_function_code := 18;
    ELSIF (v_sql_statement ~* '^\s*CREATE\s+(USER|SCHEMA)\s+')
    THEN -- CREATE USER
        v_function_code := 19;
    ELSIF (v_sql_statement ~* '^\s*(CREATE\s+(?:UNIQUE\s+)?INDEX)|(CREATE\s+(?:BITMAP\s+)?INDEX)\s+')
    THEN -- CREATE INDEX
        v_function_code := 20;
    ELSIF (v_sql_statement ~* '^\s*(DROP\s+INDEX)\s+')
    THEN -- DROP INDEX
        v_function_code := 21;
    ELSIF (v_sql_statement ~* '^\s*(DROP\s+CLUSTER)\s+')
    THEN -- DROP CLUSTER
        v_function_code := 22;
    ELSIF (v_sql_statement ~* '^\s*(CREATE\s+(?:OR\s+REPLACE\s+)?PROCEDURE)\s+')
    THEN -- CREATE (OR REPLACE) PROCEDURE
        v_function_code := 24;
    ELSIF (v_sql_statement ~* '^\s*(ALTER\s+PROCEDURE)\s+')
    THEN -- ALTER PROCEDURE
        v_function_code := 25;
    ELSIF (v_sql_statement ~* '^\s*(ALTER\s+TABLE\s+)')
    THEN -- ALTER TABLE
        v_function_code := 26;
    ELSIF (v_sql_statement ~* '^\s*EXPLAIN\s+')
    THEN -- EXPLAIN
        v_function_code := 27;
    ELSIF (v_sql_statement ~* '^\s*(GRANT)\s+')
    THEN -- GRANT
        v_function_code := 28;
    ELSIF (v_sql_statement ~* '^\s*(REVOKE)\s+')
    THEN -- REVOKE
        v_function_code := 29;
    ELSIF (v_sql_statement ~* '^\s*(CREATE\s+(?:OR\s+REPLACE\s+)?(?:PUBLIC\s+)?SYNONYM)\s+')
    THEN -- CREATE (OR REPLACE) (PUBLIC) SYNONYM
        v_function_code := 30;
    ELSIF (v_sql_statement ~* '^\s*(DROP\s+(?:PUBLIC\s+)?SYNONYM)\s+')
    THEN -- DROP (PUBLIC) SYNONYM
        v_function_code := 31;
    ELSIF (v_sql_statement ~* '^\s*(ALTER\s+SYSTEM)\s+')
    THEN -- ALTER SYSTEM
        v_function_code := 32;
    ELSIF (v_sql_statement ~* '^\s*(SET\s+TRANSACTION)\s+')
    THEN -- SET TRANSACTION
        v_function_code := 33;
    ELSIF (v_sql_statement ~* '^\s*SET\s+CONSTRAINT[S]?\s+')
    THEN -- SET CONSTRAINTS
        v_function_code := 90;
    ELSIF (v_sql_statement ~* '(^\s*(?:<<.+>>)?\s*(BEGIN|DECLARE)\s+)|(^\s*DO(:?\s+LANGUAGE\s+[[:alpha:]]+\s+)?\s*\$([[:alnum:]_]*)?\$\s*(:<<.+>>)?\s*)' OR
           v_sql_statement ~* '^\s*(WITH\s+.*\s+AS\s+\(.*\s*)?SELECT\s+.*\s+INTO\s+([\$\:][[:alnum:]_$#\.]+(,\s*)?)+\s*;?\s*$')
    THEN -- PL/SQL EXECUTE
        v_function_code := 34;
    ELSIF (v_sql_statement ~* '^\s*LOCK\s+')
    THEN -- LOCK
        v_function_code := 35;
    ELSIF (v_sql_statement ~* '^\s*RENAME\s+')
    THEN -- RENAME
        v_function_code := 37;
    ELSIF (v_sql_statement ~* '^\s*(COMMENT\s+ON)\s+')
    THEN -- COMMENT
        v_function_code := 38;
    ELSIF (v_sql_statement ~* '^\s*AUDIT\s+')
    THEN -- AUDIT
        v_function_code := 39;
    ELSIF (v_sql_statement ~* '^\s*NOAUDIT\s+')
    THEN -- NOAUDIT
        v_function_code := 40;
    ELSIF (v_sql_statement ~* '^\s*(ALTER\s+INDEX)\s+')
    THEN -- ALTER INDEX
        v_function_code := 41;
    ELSIF (v_sql_statement ~* '^\s*(CREATE\s+DATABASE)\s+')
    THEN -- CREATE DATABASE
        v_function_code := 44;
    ELSIF (v_sql_statement ~* '^\s*(ALTER\s+DATABASE)\s+')
    THEN -- ALTER DATABASE
        v_function_code := 45;
    ELSIF (v_sql_statement ~* '^\s*(CREATE\s+(?:PUBLIC\s+)?ROLLBACK\s+SEGMENT)\s+')
    THEN -- CREATE (OR REPLACE) (PUBLIC) ROLLBACK SEGMENT
        v_function_code := 46;
    ELSIF (v_sql_statement ~* '^\s*(ALTER\s+ROLLBACK\s+SEGMENT)\s+')
    THEN -- ALTER ROLLBACK SEGMENT
        v_function_code := 47;
    ELSIF (v_sql_statement ~* '^\s*(DROP\s+ROLLBACK\s+SEGMENT)\s+')
    THEN -- DROP ROLLBACK SEGMENT
        v_function_code := 48;
    ELSIF (v_sql_statement ~* '^\s*(CREATE\s+(?:(?:SMALLFILE\s+)|(?:BIGFILE\s+)|(?:UNDO\s+)|(?:TEMPORARY\s+))?TABLESPACE)\s+')
    THEN -- CREATE (SMALLFILE | BIGFILE | UNDO | TEMPORARY) TABLESPACE
        v_function_code := 49;
    ELSIF (v_sql_statement ~* '^\s*(ALTER\s+TABLESPACE)\s+')
    THEN -- ALTER TABLESPACE
        v_function_code := 50;
    ELSIF (v_sql_statement ~* '^\s*(DROP\s+TABLESPACE)\s+')
    THEN -- DROP TABLESPACE
        v_function_code := 51;
    ELSIF (v_sql_statement ~* '^\s*ALTER\s+SESSION\s+|^\s*RESET\s+' OR
           (v_sql_statement ~* '^\s*SET\s+[[:alnum:]_$#]+\s+' AND v_sql_statement !~* '^\s*SET\s+LOCAL\s+'))
    THEN -- ALTER SESSION
        v_function_code := 52;
    ELSIF (v_sql_statement ~* '^\s*(ALTER\s+USER)\s+|(ALTER\s+SCHEMA)\s+')
    THEN -- ALTER USER
        v_function_code := 53;
    ELSIF (v_sql_statement ~* '^\s*COMMIT(?:\s+WORK)?\s*$')
    THEN -- COMMIT[ WORK]
        v_function_code := 54;
    ELSIF (v_sql_statement ~* '^\s*ROLLBACK(?:\s+WORK)?\s*$')
    THEN -- ROLLBACK[ WORK]
        v_function_code := 55;
    ELSIF (v_sql_statement ~* '^\s*SAVEPOINT\s+')
    THEN -- SAVEPOINT
        v_function_code := 56;
    ELSIF (v_sql_statement ~* '^\s*(CREATE\s+CONTROLFILE)\s+')
    THEN -- CREATE CONTROL FILE
        v_function_code := 57;
    ELSIF (v_sql_statement ~* '^\s*(CREATE\s+(?:OR\s+REPLACE\s+)?(?:CONSTRAINT\s+)?TRIGGER)\s+')
    THEN -- CREATE TRIGGER
        v_function_code := 59;
    ELSIF (v_sql_statement ~* '^\s*(ALTER\s+TRIGGER)\s+')
    THEN -- ALTER TRIGGER
        v_function_code := 60;
    ELSIF (v_sql_statement ~* '^\s*(DROP\s+TRIGGER)\s+')
    THEN -- DROP TRIGGER
        v_function_code := 61;
    ELSIF (v_sql_statement ~* '^\s*ANALYZE\s+TABLE\s+')
    THEN -- ANALYZE TABLE
        v_function_code := 62;
    ELSIF (v_sql_statement ~* '^\s*ANALYZE\s+INDEX\s+')
    THEN -- ANALYZE INDEX
        v_function_code := 63;
    ELSIF (v_sql_statement ~* '^\s*ANALYZE\s+CLUSTER\s+')
    THEN -- ANALYZE CLUSTER
        v_function_code := 64;
    ELSIF (v_sql_statement ~* '^\s*(CREATE\s+PROFILE)\s+')
    THEN -- CREATE PROFILE
        v_function_code := 65;
    ELSIF (v_sql_statement ~* '^\s*(DROP\s+PROFILE)\s+')
    THEN -- DROP PROFILE
        v_function_code := 66;
    ELSIF (v_sql_statement ~* '^\s*(ALTER\s+PROFILE)\s+')
    THEN -- ALTER PROFILE
        v_function_code := 67;
    ELSIF (v_sql_statement ~* '^\s*(DROP\s+PROCEDURE)\s+')
    THEN -- DROP PROCEDURE
        v_function_code := 68;
    ELSIF (v_sql_statement ~* '^\s*(ALTER\s+RESOURCE\s+COST)\s+')
    THEN -- ALTER RESOURCE COST
        v_function_code := 70;
    ELSIF (v_sql_statement ~* '^\s*(CREATE\s+(MATERIALIZED\s+VIEW|SNAPSHOT)\s+LOG\s+ON)\s+')
    THEN -- CREATE SNAPSHOT LOG
        v_function_code := 71;
    ELSIF (v_sql_statement ~* '^\s*(ALTER\s+(MATERIALIZED\s+VIEW|SNAPSHOT)\s+LOG\s+ON)\s+')
    THEN -- ALTER SNAPSHOT LOG
        v_function_code := 72;
    ELSIF (v_sql_statement ~* '^\s*(DROP\s+(MATERIALIZED\s+VIEW|SNAPSHOT)\s+LOG\s+ON)\s+')
    THEN -- DROP SNAPSHOT LOG
        v_function_code := 73;
    ELSIF (v_sql_statement ~* '^\s*(CREATE\s+(MATERIALIZED\s+VIEW|SNAPSHOT))\s+')
    THEN -- CREATE SNAPSHOT
        v_function_code := 74;
    ELSIF (v_sql_statement ~* '^\s*(ALTER\s+(MATERIALIZED\s+VIEW|SNAPSHOT))\s+')
    THEN -- ALTER SNAPSHOT
        v_function_code := 75;
    ELSIF (v_sql_statement ~* '^\s*(DROP\s+(MATERIALIZED\s+VIEW|SNAPSHOT))\s+')
    THEN -- DROP SNAPSHOT
        v_function_code := 76;
    ELSIF (v_sql_statement ~* '^\s*(CREATE\s+(?:OR\s+REPLACE\s+)?TYPE)\s+(?!BODY)')
    THEN -- CREATE[ OR REPLACE] TYPE
        v_function_code := 77;
    ELSIF (v_sql_statement ~* '^\s*(DROP\s+TYPE)\s+(?!BODY)')
    THEN -- DROP TYPE
        v_function_code := 78;
    ELSIF (v_sql_statement ~* '^\s*(ALTER\s+ROLE)\s+')
    THEN -- ALTER ROLE
        v_function_code := 79;
    ELSIF (v_sql_statement ~* '^\s*(ALTER\s+TYPE)\s+' AND v_sql_statement !~* 'BODY')
    THEN -- ALTER TYPE
        v_function_code := 80;
    ELSIF (v_sql_statement ~* '^\s*(CREATE\s+(?:OR\s+REPLACE\s+)?TYPE\s+BODY)\s+')
    THEN -- CREATE[ OR REPLACE] TYPE BODY
        v_function_code := 81;
    ELSIF (v_sql_statement ~* '^\s*(ALTER\s+TYPE\s+(?:.)*\s+COMPILE\s+(?:DEBUG\s+)?BODY)')
    THEN -- ALTER TYPE COMPILE (DEBUG) BODY
        v_function_code := 82;
    ELSIF (v_sql_statement ~* '^\s*(DROP\s+TYPE\s+BODY)\s+')
    THEN -- DROP TYPE BODY
        v_function_code := 83;
    ELSIF (v_sql_statement ~* '^\s*(DROP\s+LIBRARY)\s+')
    THEN -- DROP LIBRARY
        v_function_code := 84;
    ELSIF (v_sql_statement ~* '^\s*(TRUNCATE\s+TABLE)\s+')
    THEN -- TRUNCATE TABLE
        v_function_code := 85;
    ELSIF (v_sql_statement ~* '^\s*(TRUNCATE\s+CLUSTER)\s+')
    THEN -- TRUNCATE CLUSTER
        v_function_code := 86;
    ELSIF (v_sql_statement ~* '^\s*(CREATE\s+BITMAPFILE)\s+')
    THEN -- CREATE BITMAPFILE
        v_function_code := 87;
    ELSIF (v_sql_statement ~* '^\s*(ALTER\s+VIEW)\s+')
    THEN -- ALTER VIEW
        v_function_code := 88;
    ELSIF (v_sql_statement ~* '^\s*(DROP\s+BITMAPFILE)\s+')
    THEN -- DROP BITMAPFILE
        v_function_code := 89;
    ELSIF (v_sql_statement ~* '^\s*(CREATE\s+(?:OR\s+REPLACE\s+)?FUNCTION\s+(?:.)*(RETURN|RETURNS))\s+')
    THEN -- CREATE (OR REPLACE) FUNCTION
        v_function_code := 91;
    ELSIF (v_sql_statement ~* '^\s*(ALTER\s+FUNCTION)\s+')
    THEN -- ALTER FUNCTION
        v_function_code := 92;
    ELSIF (v_sql_statement ~* '^\s*(DROP\s+FUNCTION)\s+')
    THEN -- DROP FUNCTION
        v_function_code := 93;
    ELSIF (v_sql_statement ~* '^\s*(CREATE\s+(?:OR\s+REPLACE\s+)?PACKAGE)\s+' AND v_sql_statement !~* 'BODY')
    THEN -- CREATE (OR REPLACE) PACKAGE
        v_function_code := 94;
    ELSIF (v_sql_statement ~* '^\s*(ALTER\s+PACKAGE)\s+' AND v_sql_statement !~* 'BODY')
    THEN -- ALTER PACKAGE
        v_function_code := 95;
    ELSIF (v_sql_statement ~* '^\s*(DROP\s+PACKAGE)\s+' AND v_sql_statement !~* 'BODY')
    THEN -- DROP PACKAGE
        v_function_code := 96;
    ELSIF (v_sql_statement ~* '^\s*(CREATE\s+(?:OR\s+REPLACE\s+)?PACKAGE\s+BODY)\s+')
    THEN -- CREATE (OR REPLACE) PACKAGE BODY
        v_function_code := 97;
    ELSIF (v_sql_statement ~* '^\s*(ALTER\s+PACKAGE)\s+' AND v_sql_statement ~* 'BODY')
    THEN -- ALTER PACKAGE BODY
        v_function_code := 98;
    ELSIF (v_sql_statement ~* '^\s*(DROP\s+PACKAGE\s+BODY)\s+')
    THEN -- DROP PACKAGE BODY
        v_function_code := 99;
    ELSIF (v_sql_statement ~* '^\s*(CREATE\s+(?:OR\s+REPLACE\s+)?DIRECTORY)\s+')
    THEN -- CREATE (OR REPLACE) DIRECTORY
        v_function_code := 157;
    ELSIF (v_sql_statement ~* '^\s*(DROP\s+DIRECTORY)\s+')
    THEN -- DROP DIRECTORY
        v_function_code := 158;
    ELSIF (v_sql_statement ~* '^\s*(CREATE\s+(?:OR\s+REPLACE\s+)?LIBRARY)\s+')
    THEN -- CREATE (OR REPLACE) LIBRARY
        v_function_code := 159;
    ELSIF (v_sql_statement ~* '^\s*CALL\s+')
    THEN -- CALL METHOD
        v_function_code := 170;
    ELSIF (v_sql_statement ~* '^\s*PURGE\s+RECYCLEBIN\s*[;]?\s*$')
    THEN -- PURGE RECYCLEBIN
        v_function_code := 197;
    ELSIF (v_sql_statement ~* '^\s*PURGE\s+DBA_RECYCLEBIN\s*[;]?\s*$')
    THEN -- PURGE DBA_RECYCLEBIN
        v_function_code := 198;
    ELSIF (v_sql_statement ~* '^\s*PURGE\s+TABLESPACE\s+')
    THEN -- PURGE TABLESPACE
        v_function_code := 199;
    ELSIF (v_sql_statement ~* '^\s*PURGE\s+TABLE\s+')
    THEN -- PURGE TABLE
        v_function_code := 200;
    ELSIF (v_sql_statement ~* '^\s*PURGE\s+INDEX\s+')
    THEN -- PURGE INDEX
        v_function_code := 201;
    ELSIF (v_sql_statement ~* '^\s*FLASHBACK\s+.*\s+BEFORE\s+DROP')
    THEN -- UNDROP (FLASHBACK)
        v_function_code := 202;
    ELSIF (v_sql_statement ~* '^\s*DROP\s+DATABASE\s*[;]?\s*')
    THEN -- DROP DATABASE
        v_function_code := 203;
    ELSIF (v_sql_statement ~* '^\s*CREATE\s+(?:OR\s+REPLACE\s+)?ATTRIBUTE\s+DIMENSION\s+')
    THEN -- CREATE[ OR REPLACE] ATTRIBUTE DIMENSION
        v_function_code := 243;
    ELSIF (v_sql_statement ~* '^\s*ALTER\s+ATTRIBUTE\s+DIMENSION\s+')
    THEN -- ALTER ATTRIBUTE DIMENSION
        v_function_code := 244;
    ELSIF (v_sql_statement ~* '^\s*DROP\s+ATTRIBUTE\s+DIMENSION\s+')
    THEN -- DROP ATTRIBUTE DIMENSION
        v_function_code := 245;
    ELSIF (v_sql_statement ~* '^\s*CREATE\s+(?:OR\s+REPLACE\s+)?HIERARCHY\s+')
    THEN -- CREATE[ OR REPLACE] HIERARCHY
        v_function_code := 246;
    ELSIF (v_sql_statement ~* '^\s*ALTER\s+HIERARCHY\s+')
    THEN -- ALTER HIERARCHY
        v_function_code := 247;
    ELSIF (v_sql_statement ~* '^\s*DROP\s+HIERARCHY\s+')
    THEN -- DROP HIERARCHY
        v_function_code := 248;
    ELSIF (v_sql_statement ~* '^\s*CREATE\s+(?:OR\s+REPLACE\s+)?ANALYTIC\s+VIEW\s+')
    THEN -- CREATE[ OR REPLACE] ANALYTIC VIEW
        v_function_code := 249;
    ELSIF (v_sql_statement ~* '^\s*ALTER\s+ANALYTIC\s+VIEW\s+')
    THEN -- ALTER ANALYTIC VIEW
        v_function_code := 250;
    ELSIF (v_sql_statement ~* '^\s*DROP\s+ANALYTIC\s+VIEW\s+')
    THEN -- DROP ANALYTIC VIEW
        v_function_code := 251;
    END IF;

    IF (p_cursor_id IS NULL)
    THEN
        -- Setting `last_cursor_func_code` variable
        PERFORM set_config('sct$dbms_sql.last_cursor_func_code', v_function_code::TEXT, false);
    END IF;

    RETURN v_function_code;
EXCEPTION
    -- Effective userid (OID) changed since cursor parse
    WHEN insufficient_privilege THEN
        RAISE USING MESSAGE := 'DBMS_SQL access denied.',
                    DETAIL := 'DBMS_SQL access was denied due to security concerns.',
                    HINT := '-29471';
END;

DECLARE
    v_cursor_id INTEGER;
    v_security_level NUMERIC;
BEGIN
    -- Security level is 1 when passed NULL
    v_security_level := coalesce(floor(p_security_level), 1);

    -- Checking that security level lies into valid range
    IF (v_security_level NOT BETWEEN 0 AND 2)
    THEN
        RAISE numeric_value_out_of_range;
    END IF;

    -- Checking security level for allowed values
    IF (v_security_level = 0)
    THEN
        RAISE invalid_parameter_value;
    END IF;

    -- Initializing solution temp tables
    PERFORM aws_oracle_ext.dbms_sql$init();

    -- Generating unpredictable, randomized, cursor number
    WHILE (v_cursor_id IS NULL)
    LOOP
        BEGIN
            INSERT INTO dbms_sql$cursor (cursor_id)
                 VALUES (abs(aws_oracle_ext.dbms_random$random()))
              RETURNING cursor_id
                   INTO v_cursor_id;
        EXCEPTION
            WHEN insufficient_privilege THEN
                -- Effective userid changed
                RAISE invalid_role_specification;
            WHEN unique_violation THEN
                NULL;
        END;
    END LOOP;

    -- Saving cursor metadata (JSON) object
    PERFORM set_config(format('sct$dbms_sql.cursor_id_%s_metadata', v_cursor_id),
                       jsonb_build_object('cursor_id', v_cursor_id,
                                          'security_level', v_security_level,
                                          'from_refcursor', false)::TEXT,
                       false);

    -- Nulling `last_cursor_func_code` variable
    PERFORM set_config('sct$dbms_sql.last_cursor_func_code', null, false);

    -- Setting `last_opened_cursor_id` variable
    PERFORM set_config('sct$dbms_sql.last_opened_cursor_id', v_cursor_id::TEXT, false);

    -- Setting `last_affected_cursor_id` variable
    PERFORM set_config('sct$dbms_sql.last_affected_cursor_id', v_cursor_id::TEXT, false);

    -- Nulling `last_row_count` variable
    PERFORM set_config('sct$dbms_sql.last_row_count', null, false);

    RETURN v_cursor_id;
EXCEPTION
    -- Incorrect security level value
    WHEN numeric_value_out_of_range THEN
        RAISE USING MESSAGE := 'Security level is outside the valid range of 0 to 2.',
                    DETAIL := 'An invalid security level value was specified.',
                    HINT := '-29472';

    -- Disabled security level value
    WHEN invalid_parameter_value THEN
        RAISE USING MESSAGE := 'Security level of 0 is not allowed.',
                    DETAIL := 'This level of security is disabled by default.',
                    HINT := '-29474';

    -- Effective userid (OID) changed since solution initialization
    WHEN invalid_role_specification THEN
        RAISE USING MESSAGE := 'DBMS_SQL access denied.',
                    DETAIL := 'DBMS_SQL access was denied due to security concerns.',
                    HINT := '-29471';
END;

DECLARE
    v_err_message TEXT;
    v_sql_cmd_type TEXT;
    v_cur_metadata JSONB;
    v_sql_statement TEXT;
    v_statement_hash TEXT;
    v_current_user_oid BIGINT;
    v_sql_function_code SMALLINT;
BEGIN
    -- Checking that cursor is opened (exit if it is not)
    IF (NOT aws_oracle_ext.dbms_sql$is_open(p_cursor_id))
    THEN
        RAISE invalid_cursor_state;
    ELSIF (coalesce(p_sql_statement, '') = '')
    THEN
        -- ORA-06561: given statement is not supported by package DBMS_SQL
        RAISE sql_statement_not_yet_complete;
    END IF;

    -- Calculating unique statement hash
    v_statement_hash := coalesce(md5(p_sql_statement), '');

    -- Getting cursor metadata (JSON) object
    v_cur_metadata := nullif(current_setting(format('sct$dbms_sql.cursor_id_%s_metadata', p_cursor_id), true), '');

    -- Getting dynamic SQL function code from cursor metadata (JSON)
    v_sql_function_code := (v_cur_metadata ->> 'sql_func_code');

    -- Determining SQL command type from cursor metadata (JSON)
    v_sql_cmd_type := (v_cur_metadata ->> 'sql_cmd_type');

    -- Setting SELECT statement columns count (if passed in)
    v_cur_metadata := jsonb_set(v_cur_metadata, '{cols_count}', coalesce(to_jsonb(p_cols_count), JSONB 'null'));

    -- If cursor (cursor_id) was transformed from a REFCURSOR then remove "from_refcursor" flag
    IF ((v_cur_metadata ->> 'from_refcursor')::BOOLEAN)
    THEN
        -- Setting "from_refcursor" cursor property to FALSE
        v_cur_metadata := jsonb_set(v_cur_metadata, '{from_refcursor}', JSONB 'false');
    END IF;

    -- Updating cursor info refresh flags if statement is a new one
    IF (v_statement_hash <> coalesce(v_cur_metadata ->> 'statement_hash', ''))
    THEN
        -- Setting "statement_hash" (JSON) cursor property
        v_cur_metadata := jsonb_set(v_cur_metadata, '{statement_hash}', to_jsonb(v_statement_hash));

        -- Setting "colinfo_refresh" (JSON) cursor property
        v_cur_metadata := jsonb_set(v_cur_metadata, '{colinfo_refresh}', to_jsonb(1));

        /* Removing comments from SQL statement (both single and multi-line),
           validating multi-line comment opening and closure tags */
        v_sql_statement := aws_oracle_ext.strip_sql_comments(p_sql_statement,
                                                             p_adv_quote_tags => ARRAY['$q$','$Q$'], -- Advanced quoting marker list
                                                             p_validate_multiline => true);
        BEGIN
            -- Associating SQL statement with cursor id
            UPDATE dbms_sql$cursor
               SET sql_statement = v_sql_statement
             WHERE cursor_id = p_cursor_id;
        EXCEPTION
            WHEN insufficient_privilege THEN
                -- Effective userid changed
                RAISE invalid_cursor_state;
        END;

        -- Getting dynamic SQL function code
        v_sql_function_code := aws_oracle_ext.dbms_sql$last_sql_function_code(p_cursor_id);

        -- Determining SQL command type (DDL, DML, DCL, TCL, SCL, SCS) by function code
        v_sql_cmd_type := aws_oracle_ext.get_cmd_type_by_sql_func_code(v_sql_function_code);

        -- Checking SQL statement for correctness
        IF (v_sql_cmd_type IS NULL)
        THEN
            -- ORA-00900: invalid SQL statement
            RAISE invalid_sql_statement_name;
        END IF;
    END IF;

    /* DDL and DCL statements are executed when they are parsed
       and the function DBMS_SQL.EXECUTE need not be used on them.

       Command type execution map:
       ---------------------------
       DDL - PARSE (DBMS_SQL.PARSE)
       DML - EXECUTE (DBMS_SQL.EXECUTE)
       DCL - PARSE
       TCL - EXECUTE
       SCL - EXECUTE
       SCS - EXECUTE
    */
    IF (v_sql_cmd_type IN ('DDL', 'DCL'))
    THEN
        BEGIN
            EXECUTE v_sql_statement;
        EXCEPTION
            WHEN undefined_parameter THEN
                -- ORA-01027: bind variables not allowed for DDL / DCL
                RAISE invalid_prepared_statement_definition;
            WHEN syntax_error THEN
                GET STACKED DIAGNOSTICS v_err_message = MESSAGE_TEXT;

                -- ORA-01027: bind variables not allowed for DDL / DCL
                IF (v_err_message = 'syntax error at or near ":"')
                THEN
                    RAISE invalid_prepared_statement_definition;
                ELSE
                    -- SQL statement is not valid (with desc.)
                    RAISE invalid_sql_statement_name;
                END IF;
        END;
    END IF;

    -- Setting `last_parsed_cursor_id` variable value
    PERFORM set_config('sct$dbms_sql.last_parsed_cursor_id', p_cursor_id::TEXT, false);

    -- Setting `sql_func_code` (JSON) cursor property
    v_cur_metadata := jsonb_set(v_cur_metadata, '{sql_func_code}', to_jsonb(v_sql_function_code));

    -- Setting `sql_cmd_type` (JSON) cursor property
    v_cur_metadata := jsonb_set(v_cur_metadata, '{sql_cmd_type}', to_jsonb(v_sql_cmd_type));

    -- Obtaining current user OID
    v_current_user_oid := aws_oracle_ext.get_user_oid_by_name(current_user);

    -- Setting `parse_role_oid` (JSON) cursor property
    v_cur_metadata := jsonb_set(v_cur_metadata, '{parse_user_oid}', to_jsonb(v_current_user_oid));

    -- Saving cursor metadata (JSON) object
    PERFORM set_config(format('sct$dbms_sql.cursor_id_%s_metadata', p_cursor_id),
                       v_cur_metadata::TEXT, -- cursor metadata object
                       false);
EXCEPTION
    -- Cursor is not opened or does not exist
    WHEN invalid_cursor_state THEN
        RAISE USING MESSAGE := 'DBMS_SQL access denied.',
                    DETAIL := 'DBMS_SQL access was denied due to security concerns.',
                    HINT := '-29471';

    -- SQL statement is empty
    WHEN sql_statement_not_yet_complete THEN
        RAISE USING MESSAGE := 'Given statement is not supported by package DBMS_SQL.',
                    DETAIL := 'Attempting to parse an unsupported SQL statement.',
                    HINT := '-6561';

    -- SQL statement is not valid
    WHEN invalid_sql_statement_name THEN
        RAISE USING MESSAGE := 'Invalid SQL statement.',
                    DETAIL := 'The statement is not recognized as a valid SQL statement or PL/pgSQL block.',
                    HINT := '-900';

    -- Bind variables not allowed for DDL / DCL
    WHEN invalid_prepared_statement_definition THEN
        RAISE USING MESSAGE := 'Bind variables not allowed for data definition operations.',
                    DETAIL := 'Bind variables are not allowed for both DDL and DCL operations.',
                    HINT := '-1027';
END;

DECLARE v_cur_metadata JSONB; v_curcols_data JSONB; v_cursor_id INTEGER; v_sql_statement TEXT; v_cols_count SMALLINT; v_col_names1 TEXT; v_col_names2 TEXT; v_col_aliases TEXT; v_colstype_block TEXT; v_prepdynsql_block TEXT; v_coldtype_cases TEXT; v_err_message TEXT; v_err_stack TEXT; v_refcur_rec RECORD; v_refcur_rec_json JSONB; v_is_scrollable BOOLEAN; v_last_row_count INTEGER; v_colstype_table_name TEXT; v_debug_mode SMALLINT := 0; -- {1:ON, 0:OFF}. If set to "ON", engine logs all dynamically generated scripts into `dbms_sql$cursor` table BEGIN -- Extracting REFRUCSOR metadata BEGIN SELECT statement, is_scrollable INTO STRICT v_sql_statement, v_is_scrollable FROM pg_catalog.pg_cursors WHERE name = p_refcursor::TEXT; EXCEPTION WHEN OTHERS THEN -- ORA-01001: invalid cursor RAISE invalid_cursor_state; END;
-- Getting `last_row_count` variable value v_last_row_count := nullif(current_setting('sct$dbms_sql.last_row_count', true), '');
/* Fetching row into record from REFCURSOR in order to determine columns count */ IF (v_is_scrollable) THEN FETCH NEXT FROM p_refcursor INTO v_refcur_rec;
MOVE BACKWARD FROM p_refcursor; ELSE FETCH FIRST FROM p_refcursor INTO v_refcur_rec; END IF;
-- Getting REFCURSOR columns count SELECT COUNT(1) INTO v_cols_count FROM json_object_keys(row_to_json(v_refcur_rec));
-- Opening new DBMS_SQL cursor for data storage v_cursor_id := aws_oracle_ext.dbms_sql$open_cursor();
-- Assigning SQL statement and exact columns count to DBMS_SQL cursor PERFORM aws_oracle_ext.dbms_sql$parse(v_cursor_id, v_sql_statement, v_cols_count);
-- Getting cursor metadata (JSON) object v_cur_metadata := nullif(current_setting(format('sct$dbms_sql.cursor_id_%s_metadata', v_cursor_id), true), '');
-- Generating list of column names SELECT string_agg(format('outer_block.v_refcur_rec."%s"', colname), concat(',', chr(10), repeat(' ', 7))), string_agg(format(' outer_block.v_refcur_rec."%s"', colname), concat(',', chr(10))) INTO v_col_names1, v_col_names2 FROM json_object_keys(row_to_json(v_refcur_rec)) AS colname;
-- Generating unique temp table name v_colstype_table_name := format('dynsql_colstype_%s', lower(substring((SELECT string_agg(md5(random()::TEXT), '') FROM generate_series(1, ceil(14 / 32.)::INTEGER) ), 1, 14)));
-- Determining REFCURSOR column data types v_colstype_block := concat_ws(chr(10), 'DO $dbms_sql$', '<<outer_block>>', 'DECLARE', ' v_refcur_rec RECORD;', format(' v_cursor_id INTEGER := %s;', v_cursor_id), format(' v_is_scrollable BOOLEAN := %s;', CASE WHEN v_is_scrollable THEN 'true' ELSE 'false' END), format(' v_refcursor REFCURSOR := %s;', quote_literal(p_refcursor)), 'BEGIN', '-- Fetching row into RECORD variable', 'IF (outer_block.v_is_scrollable) THEN', ' FETCH NEXT', ' FROM outer_block.v_refcursor', ' INTO outer_block.v_refcur_rec;', '', ' MOVE BACKWARD', ' FROM outer_block.v_refcursor;', 'ELSE', ' FETCH FIRST', ' FROM outer_block.v_refcursor', ' INTO outer_block.v_refcur_rec;', 'END IF;', '', '-- Creating temp table in order to reflect the columns structure', format('CREATE TEMP TABLE %s', v_colstype_table_name), 'WITH (OIDS = false)', 'ON COMMIT DROP', 'AS', concat('SELECT ', v_col_names1), 'WITH NO DATA;', '', concat_ws(chr(10), '-- Saving detailed column data (name, type, precision, etc.)', 'INSERT INTO dbms_sql$cursor_column (cursor_id,', ' column_pos,', ' col_name,', ' data_type,', ' base_type,', ' is_nullable,', ' chr_max_len,', ' chr_byte_len,', ' num_precision,', ' num_scale,', ' tstamp_precision,', ' interval_type,', ' interval_precision)', 'SELECT outer_block.v_cursor_id,', ' ordinal_position,', ' column_name,', ' upper(data_type),', ' aws_oracle_ext.get_base_type_by_dtype(upper(data_type)),', ' CASE is_nullable', ' WHEN ''YES'' THEN TRUE', ' ELSE FALSE', ' END,', ' character_maximum_length,', ' character_octet_length,', ' numeric_precision,', ' numeric_scale,', ' datetime_precision,', ' interval_type,', ' interval_precision', ' FROM information_schema.columns'), format(' WHERE table_name = ''%s''', v_colstype_table_name), ' ORDER BY ordinal_position ASC;', '', '-- Dropping temp table since its not needed anymore', format('DROP TABLE %s;', v_colstype_table_name), 'END; $dbms_sql$');
EXECUTE v_colstype_block; -- Will be saved into `dbms_sql$cursor` table if "v_debug_mode" is set to "ON" (1)
-- Serializing dynamic statement columns data into JSON object SELECT jsonb_object_agg(concat('col_', curcol_data.column_pos), curcol_data.column_data) INTO v_curcols_data FROM (SELECT curcol3.column_pos, (SELECT to_jsonb(curcol2.*) FROM (SELECT curcol1.data_type, curcol1.base_type, curcol1.chr_max_len, curcol1.chr_byte_len FROM dbms_sql$cursor_column AS curcol1 WHERE curcol1.cursor_id = curcol3.cursor_id AND curcol1.column_pos = curcol3.column_pos ) AS curcol2 ) AS column_data FROM dbms_sql$cursor_column AS curcol3 WHERE curcol3.cursor_id = v_cursor_id ) AS curcol_data;
-- Setting `curcols_data` (JSON) cursor property v_cur_metadata := jsonb_set(v_cur_metadata, '{curcols_data}', v_curcols_data);
-- Setting `from_refcursor` (JSON) cursor property v_cur_metadata := jsonb_set(v_cur_metadata, '{from_refcursor}', JSONB 'true');
-- Saving cursor metadata (JSON) object PERFORM set_config(format('sct$dbms_sql.cursor_id_%s_metadata', v_cursor_id), v_cur_metadata::TEXT, -- cursor metadata object FALSE);
/* Constructing CASEs to distribute dynamic query execution results through INSERT-SELECT into `dbms_sql$recordset` table */ WITH dtypes_order AS (SELECT 'TEXT' AS base_type, 1 AS dtype_order UNION ALL SELECT 'NUMERIC', 2 UNION ALL SELECT 'DOUBLE PRECISION', 3 UNION ALL SELECT 'MONEY', 4 UNION ALL SELECT 'TIMESTAMP WITHOUT TIME ZONE', 5 UNION ALL SELECT 'TIMESTAMP WITH TIME ZONE', 6 UNION ALL SELECT 'TIME WITH TIME ZONE', 7 UNION ALL SELECT 'INTERVAL', 8 UNION ALL SELECT 'BOOLEAN', 9 UNION ALL SELECT 'XML', 10 UNION ALL SELECT 'BYTEA', 11), -- Attaching current columns data curcols_data AS (SELECT base_type, array_agg(concat_ws(':', column_pos, data_type)) AS colpos_list FROM dbms_sql$cursor_column WHERE cursor_id = v_cursor_id GROUP BY base_type)
SELECT string_agg( coalesce(' CASE colpos' || chr(10) || (SELECT string_agg(format(' WHEN %s THEN %s', colpos, CASE dtype WHEN 'TIME WITHOUT TIME ZONE' -- List of types that require special conversion THEN format('to_char(col_%s, ''2001-01-01 hh24:mi:ss.us'')::TIMESTAMP', colpos) ELSE 'col_' || colpos END), chr(10)) FROM (SELECT substring(colpos, '^(\d+):?') AS colpos, substring(colpos, ':(.+)$') AS dtype FROM unnest(cdt.colpos_list) AS colpos) AS cols) || chr(10) || ' END', ' null'), ',' || chr(10) ORDER BY dto.dtype_order ASC) INTO v_coldtype_cases FROM dtypes_order dto LEFT JOIN curcols_data cdt ON (cdt.base_type = dto.base_type);
-- Generating list of consecutive column aliases (col_1, col_2, ...) SELECT string_agg(concat(repeat(' ', 29), 'col_', colnum), concat(',', chr(10))) INTO v_col_aliases FROM generate_series(1, v_cols_count) AS colnum;
-- Distributing REFRUCSOR data into `dbms_sql$recordset` table v_prepdynsql_block := concat_ws(chr(10), 'DO $dbms_sql$', '<<outer_block>>', 'DECLARE', ' v_cur_metadata JSONB;', ' v_refcur_rec RECORD;', format(' v_cursor_id INTEGER := %s;', v_cursor_id), format(' v_cols_count SMALLINT := %s;', v_cols_count), ' v_rows_fetched INTEGER := 0;', format(' v_is_scrollable BOOLEAN := %s;', CASE WHEN v_is_scrollable THEN 'true' ELSE 'false' END), format(' v_refcursor REFCURSOR := %s;', quote_literal(p_refcursor)), 'BEGIN', 'LOOP', '-- Fetching row into RECORD variable', 'IF (outer_block.v_rows_fetched = 0 AND', ' NOT outer_block.v_is_scrollable)', 'THEN', ' FETCH FIRST', ' FROM outer_block.v_refcursor', ' INTO outer_block.v_refcur_rec;', 'ELSE', ' FETCH NEXT', ' FROM outer_block.v_refcursor', ' INTO outer_block.v_refcur_rec;', 'END IF;', '', '-- Exit when no more rows to fetch', 'EXIT WHEN NOT FOUND;', 'outer_block.v_rows_fetched := outer_block.v_rows_fetched + 1;', '', '-- Inserting column value into "dbms_sql$recordset" table', 'EXECUTE', quote_literal(concat_ws(chr(10), 'INSERT INTO dbms_sql$recordset (cursor_id,', ' rownum,', ' column_pos,', ' chr_val,', ' num_val,', ' dprec_val,', ' money_val,', ' tstamp_val,', ' tstamptz_val,', ' timetz_val,', ' interval_val,', ' bool_val,', ' xml_val,', ' bin_val)', ' SELECT $1,', ' $2,', ' colpos,', v_coldtype_cases, -- Coltypes distribution CASEs ' FROM (SELECT generate_series(1, $3) AS colpos,', ' dynsql.*', concat(' FROM (SELECT ', (SELECT string_agg(concat('$', col_num), concat(',', chr(10), repeat(' ', 24))) FROM generate_series(4, v_cols_count + 3) AS col_num)), -- List of consecutive column placeholders ($4, $5, ...) format(' ) AS dynsql (%s)', trim(v_col_aliases)), -- List of consecutive column aliases ' ) AS dynsql2')), 'USING outer_block.v_cursor_id,', ' outer_block.v_rows_fetched,', ' outer_block.v_cols_count,', concat(v_col_names2, ';'), 'END LOOP;', '', '-- Getting cursor metadata (JSON) object', 'outer_block.v_cur_metadata :=', ' nullif(current_setting(format(''sct$dbms_sql.cursor_id_%s_metadata'',', ' outer_block.v_cursor_id), true), '''');', '', '-- Setting "rows_count" (JSON) cursor property', 'outer_block.v_cur_metadata := jsonb_set(outer_block.v_cur_metadata, ''{rows_count}'',', ' to_jsonb(outer_block.v_rows_fetched));', '', '-- Saving cursor metadata (JSON) object', 'PERFORM set_config(format(''sct$dbms_sql.cursor_id_%s_metadata'', outer_block.v_cursor_id),', ' outer_block.v_cur_metadata::TEXT, -- cursor metadata object', ' false);', 'END; $dbms_sql$');
EXECUTE v_prepdynsql_block; -- Will be saved into `dbms_sql$cursor` table if "v_debug_mode" is set to "ON" (1)
-- Closing source REFCURSOR CLOSE p_refcursor;
/* Logging of all dynamically generated scripts into `dbms_sql$cursor` if debug mode is "ON" */ IF (v_debug_mode) THEN UPDATE dbms_sql$cursor SET colstype_block = v_colstype_block, prepdynsql_block = v_prepdynsql_block WHERE cursor_id = v_cursor_id; END IF;
-- Setting `last_affected_cursor_id` variable PERFORM set_config('sct$dbms_sql.last_affected_cursor_id', v_cursor_id::TEXT, false);
-- Setting `last_row_count` variable PERFORM set_config('sct$dbms_sql.last_row_count', v_last_row_count::TEXT, false);
RETURN v_cursor_id; EXCEPTION -- The REFCURSOR is not opened or NULL WHEN invalid_cursor_state THEN RAISE USING MESSAGE := 'Invalid cursor.', DETAIL := 'Could not transform a not opened REFCURSOR into DBMS_SQL cursor number.', HINT := '-1001';
-- Duplicate column names in REFCURSOR WHEN duplicate_column THEN RAISE USING MESSAGE := 'Duplicate column name.', DETAIL := 'The same column name was listed in REFCURSOR more than once, or some columns do not have assigned names.', HINT := '-957';
-- Some syntax error occurred WHEN syntax_error THEN GET STACKED DIAGNOSTICS v_err_message = MESSAGE_TEXT, v_err_stack = PG_EXCEPTION_CONTEXT;
RAISE USING MESSAGE := 'Syntax error has occured while trying to transform REFCURSOR into a DBMS_SQL cursor number.', DETAIL := concat(v_err_message, '.'), HINT := concat('(Call Stack)', chr(10), v_err_stack); END;

DECLARE v_cur_metadata JSONB; v_curr_row INTEGER; v_rows_fetched INTEGER; v_coldtype_casts VARCHAR; v_colnames_cases VARCHAR; v_colnames_list VARCHAR; v_resrecset_sql TEXT; v_current_user_oid BIGINT; v_sql_function_code SMALLINT; v_res_refcursor REFCURSOR := format('sct-dbms_sql$cur_id_%s_recset', p_cursor_id); BEGIN -- Checking that cursor is opened (exit if it is not) IF (NOT aws_oracle_ext.dbms_sql$is_open(p_cursor_id)) THEN RAISE invalid_cursor_state; END IF;
-- Obtaining current user OID v_current_user_oid := aws_oracle_ext.get_user_oid_by_name(current_user);
-- Getting cursor metadata (JSON) object v_cur_metadata := nullif(current_setting(format('sct$dbms_sql.cursor_id_%s_metadata', p_cursor_id), true), '');
/* Checking that effective user OID (current_user) of the caller on this cursor is the same as that of the caller of the most recent parse operation on this cursor. Actual for cursors opened with security level 2 */ IF ((v_cur_metadata ->> 'security_level')::SMALLINT = 2 AND (v_cur_metadata ->> 'parse_user_oid')::BIGINT != v_current_user_oid) THEN RAISE invalid_role_specification; END IF;
-- Getting dynamic SQL function code v_sql_function_code := aws_oracle_ext.dbms_sql$last_sql_function_code(p_cursor_id);
/* Cursor rows count should not be empty (NULL), or of type other than SELECT (func_code 4) */ IF ((v_cur_metadata ->> 'rows_count') IS NULL OR v_sql_function_code != 4) THEN -- ORA-01001: invalid cursor RAISE null_value_not_allowed; END IF;
-- Getting num of rows already fetched from DBMS_SQL cursor v_rows_fetched := coalesce((v_cur_metadata ->> 'curr_row')::INTEGER, 0);
BEGIN -- Getting column info (list of names, data type casts, column position cases) SELECT string_agg(format('CAST("%s" AS %s)', col_name, CASE WHEN data_type IN ('CHARACTER', 'CHARACTER VARYING') THEN concat(data_type, '(' || chr_max_len || ')') WHEN (data_type ~* '^\s*TIME' OR data_type = 'INTERVAL') THEN format(CASE data_type WHEN 'TIMESTAMP WITHOUT TIME ZONE' THEN 'TIMESTAMP(%s) WITHOUT TIME ZONE' WHEN 'TIMESTAMP WITH TIME ZONE' THEN 'TIMESTAMP(%s) WITH TIME ZONE' WHEN 'TIME WITHOUT TIME ZONE' THEN 'TIME(%s) WITHOUT TIME ZONE' WHEN 'TIME WITH TIME ZONE' THEN 'TIME(%s) WITH TIME ZONE' ELSE 'INTERVAL(%s)' END, /* default precision for time fraction is 6 */ coalesce(tstamp_precision, interval_precision, 6)) ELSE data_type END), concat(',', chr(10), repeat(' ', 7))), string_agg(format(concat_ws(chr(10), '(array_agg(CASE column_pos', repeat(' ', 28) || 'WHEN %s THEN %s', repeat(' ', 26) || 'END))[%s]'), column_pos, CASE base_type WHEN 'TEXT' THEN 'chr_val' WHEN 'NUMERIC' THEN 'num_val' WHEN 'DOUBLE PRECISION' THEN 'dprec_val' WHEN 'MONEY' THEN 'money_val' WHEN 'TIMESTAMP WITHOUT TIME ZONE' THEN 'tstamp_val' WHEN 'TIMESTAMP WITH TIME ZONE' THEN 'tstamptz_val' WHEN 'TIME WITH TIME ZONE' THEN 'timetz_val' WHEN 'INTERVAL' THEN 'interval_val' WHEN 'BOOLEAN' THEN 'bool_val' WHEN 'XML' THEN 'xml_val' WHEN 'BYTEA' THEN 'bin_val' END, column_pos), concat(',', chr(10), repeat(' ', 15))), string_agg(format('"%s"', col_name), concat(',', chr(10), repeat(' ', 24))) INTO v_coldtype_casts, v_colnames_cases, v_colnames_list -- Comma-separated list of column names FROM dbms_sql$cursor_column WHERE cursor_id = p_cursor_id; EXCEPTION WHEN insufficient_privilege THEN -- Effective userid changed RAISE invalid_cursor_state; END;
-- Constructing query to extract data (as dataset) into REFCURSOR v_resrecset_sql := format(concat_ws(chr(10), 'SELECT ' || v_coldtype_casts, ' FROM (SELECT ' || v_colnames_cases, ' FROM dbms_sql$recordset', ' WHERE cursor_id = %s', ' AND rownum > %s', ' GROUP BY rownum', ' ) AS record_set (%s)'), p_cursor_id, v_rows_fetched, v_colnames_list);
-- Opening REFCURSOR for dymanic SQL query resulting data OPEN v_res_refcursor SCROLL FOR EXECUTE v_resrecset_sql;
-- Closing the DBMS_SQL cursor and nulling its context PERFORM aws_oracle_ext.dbms_sql$close_cursor(p_cursor_id);
-- Setting `last_row_count` variable PERFORM set_config('sct$dbms_sql.last_row_count', v_rows_fetched::TEXT, false);
RETURN v_res_refcursor; EXCEPTION -- Cursor is not opened or does not exist WHEN invalid_cursor_state THEN RAISE USING MESSAGE := 'DBMS_SQL access denied.', DETAIL := 'DBMS_SQL access was denied due to security concerns.', HINT := '-29471';
-- DBMS_SQL cursor rows count is NULL or func_code != 4 (SELECT) WHEN null_value_not_allowed THEN RAISE USING MESSAGE := 'Invalid cursor.', DETAIL := 'Dynamic SQL statement was not executed or is not a valid SELECT query.', HINT := '-1001';
-- Effective userid (OID) changed since cursor parse WHEN invalid_role_specification THEN RAISE USING MESSAGE := 'Effective userid is not the same as when cursor was parsed.', DETAIL := 'The effective userid on this call to DBMS_SQL is not the same as that at the time the cursor was parsed.', HINT := '-29470'; END;

DECLARE
    v_var_info TEXT[];
    v_chr_val TEXT;
    v_num_val NUMERIC;
    v_dprec_val DOUBLE PRECISION;
    v_money_val MONEY;
    v_tstamp_val TIMESTAMP WITHOUT TIME ZONE;
    v_tstamptz_val TIMESTAMP WITH TIME ZONE;
    v_timetz_val TIME WITH TIME ZONE;
    v_interval_val INTERVAL;
    v_bool_val BOOLEAN;
    v_xml_val XML;
    v_bin_val BYTEA;
    v_is_fixed SMALLINT;
    v_value_size INTEGER;
    v_out_var_size INTEGER;
    v_bind_var_data_t TEXT;
    v_bind_var_base_t TEXT;
    v_out_var_base_t TEXT;
    v_out_var_data_t TEXT;
    v_cur_metadata JSONB;
    v_current_user_oid BIGINT;
BEGIN
    -- Checking that cursor is opened (exit if it is not)
    IF (NOT aws_oracle_ext.dbms_sql$is_open(p_cursor_id)) THEN
        RAISE invalid_cursor_state;
    -- Variable name should not be empty or blank (null)
    ELSIF (nullif(trim(p_var_name), '') IS NULL) THEN
        RAISE no_data_found;
    END IF;

    -- Obtaining current user OID
    v_current_user_oid := aws_oracle_ext.get_user_oid_by_name(current_user);

    -- Getting cursor metadata (JSON) object
    v_cur_metadata := nullif(current_setting(format('sct$dbms_sql.cursor_id_%s_metadata', p_cursor_id), true), '');

    /* Checking that effective user OID (current_user) of the caller on this cursor is the same
       as that of the caller of the most recent parse operation on this cursor.
       Actual for cursors opened with security level 2 */
    IF ((v_cur_metadata ->> 'security_level')::SMALLINT = 2 AND
        (v_cur_metadata ->> 'parse_user_oid')::BIGINT != v_current_user_oid)
    THEN
        RAISE invalid_role_specification;
    END IF;

    v_out_var_data_t := upper(pg_typeof(p_var_value)::TEXT);

    -- Determining base data type of passed variable
    v_out_var_base_t := aws_oracle_ext.get_base_type_by_dtype(v_out_var_data_t);

    -- Extracting variable info (label in statement code and general name) from supplied string
    v_var_info := regexp_matches(p_var_name, '^((?:\s*\$\d+\s*(?:\|)?)+)*\s*\:?([[:alnum:]_]+)\s*$', 'gi');

    BEGIN
        /* Extracting var data from `dbms_sql$bind_variable`
           (by cursor id and variable name) */
        SELECT data_type,
               base_type,
               value_size,
               is_fixed,
               chr_val,
               num_val,
               dprec_val,
               money_val,
               tstamp_val,
               tstamptz_val,
               timetz_val,
               interval_val,
               bool_val,
               xml_val,
               bin_val
          INTO STRICT
               v_bind_var_data_t,
               v_bind_var_base_t,
               v_value_size,
               v_is_fixed,
               v_chr_val,
               v_num_val,
               v_dprec_val,
               v_money_val,
               v_tstamp_val,
               v_tstamptz_val,
               v_timetz_val,
               v_interval_val,
               v_bool_val,
               v_xml_val,
               v_bin_val
          FROM dbms_sql$bind_variable
         WHERE cursor_id = p_cursor_id
           AND var_name = v_var_info[2]; -- Variable name
    EXCEPTION
        WHEN insufficient_privilege THEN
            -- Effective userid changed
            RAISE invalid_cursor_state;
    END;

    -- Check if OUT argument base type matches declared bind variable base type
    IF ((substring(v_out_var_base_t, 1, 14) != substring(v_bind_var_base_t, 1, 14) AND -- TIMESTAMPs
         substring(v_out_var_base_t, 1, 5) != substring(v_bind_var_base_t, 1, 5) AND -- TIMEs
         (substring(v_out_var_data_t, 1, 5) != 'TIME ' AND
          substring(v_bind_var_data_t, 1, 10) != 'TIMESTAMP ') AND
         NOT (v_bind_var_base_t IN ('NUMERIC',
                                    'DOUBLE PRECISION') AND
              v_out_var_base_t IN ('NUMERIC',
                                   'DOUBLE PRECISION'))) OR
           (substring(v_bind_var_data_t, 1, 5) = 'TIME ' AND
            (substring(v_out_var_data_t, 1, 10) = 'TIMESTAMP ' OR
             v_out_var_data_t = 'DATE')))
    THEN
        RAISE datatype_mismatch;
    -- Variable should not be declared using `dbms_sql$bind_variable_char`
    ELSIF (v_is_fixed = 1) THEN
        RAISE datatype_mismatch;
    END IF;

    -- Copying value into the OUT argument
    CASE v_out_var_base_t
       WHEN 'TEXT' THEN
           IF (v_value_size != 0 AND v_value_size < char_length(v_chr_val)) THEN
               -- ORA-06502: PL/SQL: numeric or value error
               RAISE numeric_value_out_of_range;
           END IF;

           IF (v_out_var_data_t = 'CHARACTER') THEN
               v_out_var_size := coalesce(length(p_var_value::BYTEA, pg_client_encoding()), 0);

               IF (v_out_var_size != 0 AND v_out_var_size < char_length(v_chr_val)) THEN
                   /* ORA-06502: PL/SQL: numeric or value error.
                      Character string buffer too small */
                   RAISE string_data_length_mismatch;
               END IF;
           END IF;

           p_var_value := v_chr_val;
       WHEN 'NUMERIC' THEN
           IF (v_bind_var_base_t = 'NUMERIC') THEN
               p_var_value := v_num_val;
           ELSE
               p_var_value := v_dprec_val;
           END IF;
       WHEN 'DOUBLE PRECISION' THEN
           IF (v_bind_var_base_t = 'DOUBLE PRECISION') THEN
               p_var_value := v_dprec_val;
           ELSE
               p_var_value := v_num_val;
           END IF;
       WHEN 'MONEY' THEN
           p_var_value := v_money_val;
       WHEN 'TIMESTAMP WITHOUT TIME ZONE' THEN
           CASE v_bind_var_base_t
              WHEN 'TIMESTAMP WITHOUT TIME ZONE' THEN
                  p_var_value := v_tstamp_val;
              WHEN 'TIMESTAMP WITH TIME ZONE' THEN
                  p_var_value := v_tstamptz_val;
              ELSE
                  p_var_value := v_timetz_val;
           END CASE;
       WHEN 'TIMESTAMP WITH TIME ZONE' THEN
           p_var_value := CASE v_bind_var_base_t
                             WHEN 'TIMESTAMP WITHOUT TIME ZONE' THEN v_tstamp_val::TIMESTAMPTZ
                             ELSE v_tstamptz_val
                          END;
       WHEN 'TIME WITH TIME ZONE' THEN
           p_var_value := CASE v_bind_var_base_t
                             WHEN 'TIMESTAMP WITHOUT TIME ZONE' THEN v_tstamp_val::TIME::TIMETZ
                             WHEN 'TIMESTAMP WITH TIME ZONE' THEN v_tstamptz_val::TIMETZ
                             ELSE v_timetz_val
                          END;
       WHEN 'INTERVAL' THEN
           p_var_value := v_interval_val;
       WHEN 'BOOLEAN' THEN
           p_var_value := v_bool_val;
       WHEN 'XML' THEN
           p_var_value := v_xml_val;
       WHEN 'BYTEA' THEN
           p_var_value := CASE
                             WHEN v_value_size IS NULL THEN v_bin_val
                             ELSE substring(v_bin_val from 1 for v_value_size)
                          END;
       ELSE
           p_var_value := null;
    END CASE;
EXCEPTION
    -- Cursor is not opened or does not exist
    WHEN invalid_cursor_state THEN
        RAISE USING MESSAGE := 'DBMS_SQL access denied.',
                    DETAIL := 'DBMS_SQL access was denied due to security concerns.',
                    HINT := '-29471';

    -- Bind variable does not exist
    WHEN no_data_found THEN
        RAISE USING MESSAGE := 'Bind variable does not exist.',
                    DETAIL := 'Call for a bind variable that was not listed in the corresponding SQL statement.',
                    HINT := '-1006';

    /* OUT argument base type does not match bind variable declared base type.
       Variable was declared through `dbms_sql$bind_variable_char` */
    WHEN datatype_mismatch THEN
        RAISE USING MESSAGE := 'Type of OUT argument does not match declared type of bind variable.',
                    DETAIL := 'Attempting to get the value of a bind variable but the type of the given OUT argument is different from the type of bind variable that was defined previously.',
                    HINT := '-6562';

    -- Variable value length exceeds declared OUT value size
    WHEN numeric_value_out_of_range THEN
        RAISE USING MESSAGE := 'Variable value length exceeds declared OUT value size.',
                    DETAIL := format('Variable value length: %s, declared variable OUT value size: %s.',
                                     char_length(v_chr_val), v_value_size),
                    HINT := '-6502';

    -- Length of OUT argument is too small
    WHEN string_data_length_mismatch THEN
        RAISE USING MESSAGE := 'Character string buffer too small.',
                    DETAIL := 'Length of OUT argument is too small to store bind variable value.',
                    HINT := '-6502';

    -- Effective userid (OID) changed since cursor parse
    WHEN invalid_role_specification THEN
        RAISE USING MESSAGE := 'Effective userid is not the same as when cursor was parsed.',
                    DETAIL := 'The effective userid on this call to DBMS_SQL is not the same as that at the time the cursor was parsed.',
                    HINT := '-29470';
END;

DECLARE
    v_var_info TEXT[];
    v_chr_val TEXT;
    v_is_fixed SMALLINT;
    v_var_size INTEGER;
    v_value_size INTEGER;
    v_out_var_size INTEGER;
    v_out_var_base_t TEXT;
    v_out_var_data_t TEXT;
    v_cur_metadata JSONB;
    v_current_user_oid BIGINT;
BEGIN
    -- Checking that cursor is opened (exit if it is not)
    IF (NOT aws_oracle_ext.dbms_sql$is_open(p_cursor_id)) THEN
        RAISE invalid_cursor_state;
    -- Variable name should not be empty or blank (null)
    ELSIF (nullif(trim(p_var_name), '') IS NULL) THEN
        RAISE no_data_found;
    END IF;

    -- Obtaining current user OID
    v_current_user_oid := aws_oracle_ext.get_user_oid_by_name(current_user);

    -- Getting cursor metadata (JSON) object
    v_cur_metadata := nullif(current_setting(format('sct$dbms_sql.cursor_id_%s_metadata', p_cursor_id), true), '');

    /* Checking that effective user OID (current_user) of the caller on this cursor is the same
       as that of the caller of the most recent parse operation on this cursor.
       Actual for cursors opened with security level 2 */
    IF ((v_cur_metadata ->> 'security_level')::SMALLINT = 2 AND
        (v_cur_metadata ->> 'parse_user_oid')::BIGINT != v_current_user_oid)
    THEN
        RAISE invalid_role_specification;
    END IF;

    v_out_var_data_t := upper(pg_typeof(p_var_value)::TEXT);

    -- Determining base data type of passed variable
    v_out_var_base_t := aws_oracle_ext.get_base_type_by_dtype(v_out_var_data_t);

    -- Extracting variable info (label in statement code and general name) from supplied string
    v_var_info := regexp_matches(p_var_name, '^((?:\s*\$\d+\s*(?:\|)?)+)*\s*\:?([[:alnum:]_]+)\s*$', 'gi');

    BEGIN
        /* Extracting var data from "dbms_sql$bind_variable"
           (by cursor id and variable name) */
        SELECT var_size,
               value_size,
               is_fixed,
               chr_val
          INTO STRICT
               v_var_size,
               v_value_size,
               v_is_fixed,
               v_chr_val
          FROM dbms_sql$bind_variable
         WHERE cursor_id = p_cursor_id
           AND var_name = v_var_info[2]; -- Variable name
    EXCEPTION
        WHEN insufficient_privilege THEN
            -- Effective userid changed
            RAISE invalid_cursor_state;
    END;

    -- Variable should not be declared using `dbms_sql$bind_variable`
    IF (coalesce(v_is_fixed, 0) != 1) THEN
        RAISE most_specific_type_mismatch;
    END IF;

    -- Copying value into the OUT argument
    CASE v_out_var_base_t
       WHEN 'TEXT' THEN
           IF (v_value_size != 0 AND v_value_size < char_length(v_chr_val)) THEN
               -- ORA-06502: PL/SQL: numeric or value error
               RAISE numeric_value_out_of_range;
           END IF;

           IF (v_out_var_data_t = 'CHARACTER') THEN
               v_out_var_size := coalesce(length(p_var_value::BYTEA, pg_client_encoding()), 0);

               IF (v_out_var_size != 0 AND v_out_var_size < char_length(v_chr_val)) THEN
                   /* ORA-06502: PL/SQL: numeric or value error.
                      Character string buffer too small */
                   RAISE string_data_length_mismatch;
               END IF;
           END IF;

           p_var_value := v_chr_val;
       WHEN 'NUMERIC' THEN
           p_var_value := v_chr_val::NUMERIC;
       WHEN 'DOUBLE PRECISION' THEN
           p_var_value := v_chr_val::DOUBLE PRECISION;
       WHEN 'MONEY' THEN
           p_var_value := v_chr_val::MONEY;
       WHEN 'TIMESTAMP WITHOUT TIME ZONE' THEN
           CASE v_out_var_data_t
              WHEN 'DATE'
              THEN
                  v_chr_val := regexp_replace(v_chr_val, '\s+', '', 'gi');

                  IF (char_length(v_chr_val) < 8) THEN
                      RAISE invalid_datetime_format;
                  END IF;

                  p_var_value := to_date(v_chr_val, 'dd-MON-yy');
              WHEN 'TIME WITHOUT TIME ZONE'
              THEN
                  v_chr_val := regexp_replace(v_chr_val, '\s*[AP]M', '', 'i');

                  IF (char_length(v_chr_val) < 8) THEN
                      RAISE invalid_datetime_format;
                  END IF;

                  p_var_value := v_chr_val::TIME;
              ELSE -- TIMESTAMP WITHOUT TIME ZONE
                  v_chr_val := trim(regexp_replace(v_chr_val, '\s*[AP]M', '', 'i'));

                  IF (char_length(v_chr_val) < 17) THEN
                      RAISE invalid_datetime_format;
                  END IF;

                  p_var_value := v_chr_val::TIMESTAMP;
              END CASE;
       WHEN 'TIMESTAMP WITH TIME ZONE' THEN
           v_chr_val := trim(regexp_replace(v_chr_val, '\s*[AP]M', '', 'i'));

           IF (char_length(v_chr_val) < 17) THEN
               RAISE invalid_datetime_format;
           END IF;

           p_var_value := to_timestamp(v_chr_val, 'dd-MON-yy hh24:mi:ss.us');
       WHEN 'TIME WITH TIME ZONE' THEN
           IF (char_length(v_chr_val) < 8) THEN
               RAISE invalid_datetime_format;
           END IF;

           p_var_value := v_chr_val::TIMETZ;
       WHEN 'INTERVAL' THEN
           p_var_value := v_chr_val::INTERVAL;
       WHEN 'BOOLEAN' THEN
           p_var_value := v_chr_val::BOOLEAN;
       WHEN 'XML' THEN
           BEGIN
               p_var_value := v_chr_val::XML;
           EXCEPTION
               WHEN OTHERS THEN
               RAISE invalid_xml_document;
           END;
       WHEN 'BYTEA' THEN
           BEGIN
               p_var_value := decode(v_chr_val, 'hex');
           EXCEPTION
               WHEN OTHERS THEN
               RAISE invalid_binary_representation;
           END;
       ELSE
           p_var_value := null;
    END CASE;
EXCEPTION
    -- Cursor is not opened or does not exist
    WHEN invalid_cursor_state THEN
        RAISE USING MESSAGE := 'DBMS_SQL access denied.',
                    DETAIL := 'DBMS_SQL access was denied due to security concerns.',
                    HINT := '-29471';

    -- Bind variable does not exist
    WHEN no_data_found THEN
        RAISE USING MESSAGE := 'Bind variable does not exist.',
                    DETAIL := 'Call for a bind variable that was not listed in the corresponding SQL statement.',
                    HINT := '-1006';

    -- Length of OUT argument is too small
    WHEN string_data_length_mismatch THEN
        RAISE USING MESSAGE := 'Character string buffer too small.',
                    DETAIL := 'Length of OUT argument is too small to store bind variable value.',
                    HINT := '-6502';

    -- Variable value length exceeds declared OUT value size
    WHEN numeric_value_out_of_range THEN
        RAISE USING MESSAGE := 'Variable value length exceeds declared OUT value size.',
                    DETAIL := format('Variable value length: %s, declared variable OUT value size: %s.',
                                     char_length(v_chr_val), v_value_size),
                    HINT := '-6502';

    /* OUT argument base type does not match bind variable declared base type.
       Variable was declared through `dbms_sql$bind_variable` */
    WHEN most_specific_type_mismatch THEN
        RAISE USING MESSAGE := 'Type of OUT argument does not match declared type of bind variable.',
                    DETAIL := 'Attempting to get the value of a bind variable but the type of the given OUT argument is different from the type of bind variable that was defined previously.',
                    HINT := '-6562';

    -- Effective userid (OID) changed since cursor parse
    WHEN invalid_role_specification THEN
        RAISE USING MESSAGE := 'Effective userid is not the same as when cursor was parsed.',
                    DETAIL := 'The effective userid on this call to DBMS_SQL is not the same as that at the time the cursor was parsed.',
                    HINT := '-29470';

    -- Possible data type conversion error (char to numeric\money)
    WHEN invalid_text_representation THEN
        RAISE USING MESSAGE := 'Error during variable value conversion (from char to numeric\money).',
                    DETAIL := format('Value %s is not correct for conversion to numeric\money.',
                                     quote_literal(v_chr_val));

    -- Possible data type conversion error (char to datetime\interval)
    WHEN invalid_datetime_format THEN
        RAISE USING MESSAGE := 'Error during variable value conversion (from char to datetime\interval).',
                    DETAIL := format('Value %s is not correct for conversion to datetime\interval.',
                                     quote_literal(v_chr_val));

    -- Possible data type conversion error (char to binary data)
    WHEN invalid_binary_representation THEN
        RAISE USING MESSAGE := 'Error during variable value conversion (from char to binary data).',
                    DETAIL := format('Value %s is not correct for conversion to binary data.',
                                     quote_literal(v_chr_val));

    -- Possible data type conversion error (char to XML document)
    WHEN invalid_xml_document THEN
        RAISE USING MESSAGE := 'Error during variable value conversion (from char to XML document).',
                    DETAIL := format('Value %s is not correct for conversion to XML document.',
                                     quote_literal(v_chr_val));
END;

DECLARE
    v_server_pid TEXT;
    v_obj_handle TEXT;
    v_schema_name TEXT;
    v_routine_name TEXT;
    v_routine_type TEXT;
    v_routine_oid TEXT;
    i_param_type TEXT;
    v_call_stack TEXT[];
    v_call_info TEXT[];
    v_param_types TEXT;
    v_stack_header TEXT;
    v_search_path TEXT[];
    v_pg_call_stack TEXT;
    v_anonymous_block TEXT;
    v_call_stack_rec TEXT;
    v_full_search_path TEXT[];
    i_call_stack_rec RECORD;
BEGIN
    -- Determining call stack label depending on Oracle style param
    v_stack_header := CASE
                         WHEN p_ora_style THEN '------- PL/SQL Call Stack -------'
                         ELSE '------ PL/pgSQL Call Stack ------'
                      END;

    -- Determining anonymous block label depending on Oracle style param
    v_anonymous_block := CASE
                            WHEN p_ora_style THEN 'anonymous block'
                            ELSE 'inline_code_block'
                         END;

    -- Constructing call stack header
    v_call_stack := ARRAY[v_stack_header,
                          '    object       line  object',
                          '    handle     number  name'];

    -- Obtaining current search path
    v_search_path := regexp_split_to_array(replace(current_setting('search_path'),
                                                   '$user', current_user), '\s*,\s*');
    -- Getting server process ID
    v_server_pid = pg_backend_pid();

    -- Getting current call stack
    GET DIAGNOSTICS v_pg_call_stack = PG_CONTEXT;

    FOR i_call_stack_rec IN
    (SELECT t.*
       FROM regexp_split_to_table(v_pg_call_stack, chr(10))
       WITH ORDINALITY AS t(call_stack, line_num))
    LOOP
        CONTINUE WHEN i_call_stack_rec.line_num = 1 OR
                      i_call_stack_rec.call_stack !~ 'line \d+ at';

        -- Obtaining info regarding particular stack call
        v_call_info := regexp_matches(i_call_stack_rec.call_stack,
                                      '^[[:alnum:]_/]+\s+([a-z]+)\s+(.*)(?<=[^\s])\s+line\s+(\d+)', 'g');
        v_routine_oid := NULL;

        v_routine_type := v_call_info[1];

        -- Extracting param types list from routine signature
        v_param_types := substring(v_call_info[2], '\((.*)\)$');

        -- Extracting schema name from `schema.["]routine["]`
        v_schema_name := substring(v_call_info[2], '^(.+)\.');

        -- Extracting routine name from `schema.["]routine["]`(params)
        v_routine_name := regexp_replace(v_call_info[2], '^(.+)\.|\((.*)\)$', '', 'g');

        -- Emulating Oracle anonymous block label
        IF (p_ora_style) THEN
            v_routine_name := replace(v_routine_name, 'inline_code_block', 'anonymous block');
        END IF;

        -- Determining whether call routine is a function or a procedure
        IF (v_routine_type = 'function' AND v_routine_name != 'anonymous block')
        THEN
            -- Putting stack record schema name on top of search path
            v_full_search_path := CASE
                                     WHEN v_schema_name IS NULL THEN v_search_path
                                     ELSE array_prepend(v_schema_name, v_search_path)
                                  END;
            SELECT oid,
                   CASE prokind
                      WHEN 'p' THEN 'procedure'
                      ELSE 'function'
                   END,
                   pronamespace::REGNAMESPACE::TEXT
              INTO v_routine_oid,
                   v_routine_type,
                   v_schema_name
              FROM pg_catalog.pg_proc
             WHERE proname = substring(v_routine_name, '"?([^"]+)') -- routine name
               AND pronamespace::REGNAMESPACE::TEXT = ANY(v_full_search_path) -- schema names list
               AND proargtypes = coalesce(array_to_string(string_to_array(v_param_types, ',')::REGTYPE[]::OID[],
                                          ' '), '')::OIDVECTOR -- param OIDs list
             ORDER BY array_position(v_full_search_path, pronamespace::REGNAMESPACE::TEXT) ASC
             LIMIT 1;
        END IF;

        -- Constructing object handle (server_PID.object_OID)
        v_obj_handle := concat(v_server_pid, '.' || v_routine_oid);

        -- Constructing call stack record
        v_call_stack_rec :=
            concat(rpad(repeat(' ', (14 - char_length(v_obj_handle)) / 2) || v_obj_handle, 14), -- object handle
                   ' ', -- delimiter
                   lpad(v_call_info[3], 6, ' '), -- line number
                   '  ', -- delimiter
                   CASE
                      WHEN v_routine_name !~ '^(anonymous\s|inline_code_)block$'
                      THEN v_routine_type || ' ' -- routine type
                   END,
                   CASE
                      WHEN v_schema_name ~ '^"' THEN v_schema_name
                      ELSE CASE
                              WHEN p_ora_style THEN upper(v_schema_name)
                              ELSE v_schema_name
                           END
                   END || '.', -- schema name
                   CASE
                      WHEN v_routine_name ~ '^"' OR
                           v_routine_name ~ '^(anonymous\s|inline_code_)block$'
                      THEN v_routine_name
                      ELSE CASE
                              WHEN p_ora_style THEN upper(v_routine_name)
                              ELSE v_routine_name
                           END
                   END, -- routine name
                   CASE
                      WHEN v_routine_name !~ '^(anonymous\s|inline_code_)block$'
                      THEN CASE
                              WHEN NOT p_ora_style THEN concat('(', v_param_types, ')')
                              ELSE upper('(' || nullif(v_param_types, '') || ')')
                           END
                   END);

        v_call_stack := array_append(v_call_stack, v_call_stack_rec);
    END LOOP;

    -- SQL SELECT dbms_utility$format_call_stack()
    IF (array_length(v_call_stack, 1) = 3) THEN
        v_call_stack :=
            array_append(v_call_stack,
                         rpad(repeat(' ', (14 - char_length(v_server_pid)) / 2) || v_server_pid, 14)
                         || '      1  '
                         || v_anonymous_block);
    END IF;

    RETURN array_to_string(v_call_stack, chr(10));
END;

declare
	lCtx aws_oracle_ext.dbms_xmlgen$tContext;
	lnCtxHdl int := trunc(ctx);
begin
    lCtx := aws_oracle_ext.dbms_xmlgen$getctxobj(ctxHdl => lnCtxHdl);

    if not lCtx is null then
        perform aws_oracle_ext.dbms_xmlgen$setctxvalue(ctxHdl => lCtx.ctxIdx,
                                                       ctxCursor => null,
                                                       ctxQuery => null,
                                                       ctxAttributes => null,
                                                       ctxNumRowsProcessed => null
                                                      );
    end if;
end;

select *
           from json_populate_record(null::dbms_xmlgen$TctxAttributes,
                                     coalesce(ctxAttr, dbms_xmlgen$contextAttr2text())::json
                                    )

declare
	escChars text[5][2] := array[array['&quot;', '"'], array['&apos;', ''''], array['&lt;', '<'], array['&gt;', '>'], array['&amp;', '&']];
    i int;
    lvResult text;
begin
	if flag >= aws_oracle_ext.dbms_xmlgen$entity_decode() then
		begin
			lvResult := xmlData;
			for i in 1 .. 5 loop
				lvResult := replace(lvResult, escChars[i][1], escChars[i][2]);
			end loop;
   	    exception when others then
			lvResult := null;
		    raise;
		end;
	elsif flag = aws_oracle_ext.dbms_xmlgen$entity_encode() then
		lvResult := xmlserialize(content (xpath('.', xmlelement(name dummy, xmlData), array[array['xsi', 'http://www.w3.org/2001/XMLSchema-instance']]))[1] as text);
	else
		raise exception 'XML processing error. Nagative value [%] was readed', flag using hint = '22063';
	end if;
    return lvResult;
end;

begin
	if ctxHdl < 0  then
		raise exception 'XML processing error. Nagative value [%] was readed', ctxHdl using hint = '22063';
	end if;

	return aws_oracle_ext.dbms_xmlgen$context2obj(ctxHdl => trunc(ctxHdl));

end;

declare
	lCtx aws_oracle_ext.dbms_xmlgen$tContext;
	lnCtxHdl int := trunc(ctx);
begin
	lCtx := aws_oracle_ext.dbms_xmlgen$getctxobj(ctxHdl => lnCtxHdl);
	return coalesce(lCtx.ctxNumRowsProcessed, 0);
end;

declare
    lCtx aws_oracle_ext.dbms_xmlgen$tContext;
    lnCtxHdl int := trunc(ctx);
    lvReturn text;
    lrResult record;
begin
	lCtx := aws_oracle_ext.dbms_xmlgen$getctxobj(ctxHdl => lnCtxHdl);

	if lCtx is null then
	    raise exception 'Invalid context or doesn''t exists' using hint = '19019';
	else
		lrResult := aws_oracle_ext.dbms_xmlgen$qc2xml(pvSQL => lCtx.ctxQuery,
	                                                  pCr => lCtx.ctxCursor,
	                                                  pnMaxRows => (lCtx.ctxAttributes).ctxMaxRows,
	                                                  pvRowsetTag => (lCtx.ctxAttributes).ctxRowsettag,
	                                                  pvRowTag => (lCtx.ctxAttributes).ctxRowTag,
	                                                  pnSkipRows => (lCtx.ctxAttributes).ctxSkipRows
	                                                 );

	    perform aws_oracle_ext.dbms_xmlgen$setctxvalue(ctxHdl => lCtx.ctxIdx,
	                                                   ctxCursor => lCtx.ctxCursor,
	                                                   ctxQuery => lCtx.ctxQuery,
	                                                   ctxAttributes => lCtx.ctxAttributes,
	                                                   ctxNumRowsProcessed => lrResult.pnRowCount,
	                                                   forceSetValues => true
	                                                  );

	    lvReturn := xmlserialize(document lrResult.pxXML as text
                                );
	end if;

	return lvReturn;
end;

begin
    return xmlserialize(document (aws_oracle_ext.dbms_xmlgen$qc2xml(pvSQL => sqlQuery,
				                                                    pCr => null,
				                                                    pvRowsetTag => aws_oracle_ext.dbms_xmlgen$default_rowsettag(),
				                                                    pvRowTag => aws_oracle_ext.dbms_xmlgen$default_rowtag()
				                                                   )).pxXML as text
                       );
end;

begin
	return aws_oracle_ext.dbms_xmlgen$setctxvalue(ctxHdl => null,
			                                      ctxCursor => queryString,
			                                      ctxQuery => null,
			                                      ctxAttributes => null,
			                                      ctxNumRowsProcessed => null
			                                     );
end;

declare
begin
	return aws_oracle_ext.dbms_xmlgen$setctxvalue(ctxHdl => null,
			                                      ctxCursor => null,
			                                      ctxQuery => queryString,
			                                      ctxAttributes => null,
			                                      ctxNumRowsProcessed => null
			                                     );
end;

declare

  crMain refcursor;
  lxResult xml := null;
  lxXMLArr xml[];

  lvSQL text := pvSQL;
  lCr refcursor := pCr;

  lbAddXmlRoot boolean := coalesce(pbAddXmlRoot, true);
  lbConvertSpecialChars boolean := pbConvertSpecialChars;
  lnMaxRows int := trunc(pnMaxRows);
  lnNullHandling numeric := pnNullHandling;
  lvRowsetTag text := nullif(pvRowsetTag, '');
  lvRowTag text := nullif(pvRowTag, '');
  lnSkipRows int := trunc(pnSkipRows);
  lbUseNullattributeIndicator bool := pbUseNullattributeIndicator;
  lbUseItemTagsForColl bool := pbUseItemTagsForColl;

  lvQuery text;
  lvQueryROWSETTAG text :=  $query$ select xmlelement(name %1$s, xmlagg(row))
							          from unnest($1) as q(row)
       				        $query$;

  lvQueryROWTAG text := $query$ select xmlelement(name %2$s,
                                                  xmlagg(xmlelement(name %1$s, row))
				                                 )
							      from unnest($1) as q(row)
       				    $query$;

begin

   if lvRowSetTag is not null and lvRowTag is not null then

   	 lvQuery := format(lvQueryROWTAG, lvRowTag, lvRowSetTag);

   elsif lvRowSetTag is null and lvRowTag is not null or
         lvRowSetTag is not null and lvRowTag is null then

     lvQuery := format(lvQueryROWSETTAG, coalesce(lvRowSetTag, lvRowTag));

   elseif lvRowTag is null and lvRowSetTag is null then

     lxResult := xmlparse(content '<ERROR>ext.XMLSQLException: The row-set tag and the row tag are both omitted.</ERROR>');

   else

   	 lxResult := xmlparse(content '<ERROR>ext.XMLSQLException: Undeterminated error.</ERROR>');
   end if; -- if lvRowSetTag is not null and lvRowTag is not null thenn


   if lxResult is null then
     if nullif(lvSQL, '') is not null or
        nullif(lCr::text, '') is not null then

	   select array_agg(xmlrow)
	     into lxXMLArr
	     from aws_oracle_ext.dbms_xmlgen$qc2xmltab(pvSQL => lvSQL,
                                                   pCr => lCr,
                                                   pnLimit => lnMaxRows,
                                                   pnOffset => lnSkipRows
                                                  );
	 else
	   raise exception 'Null Exception.' using hint = '29532';
	 end if;

     begin
         open crMain for execute lvQuery using lxXMLArr;
         fetch crMain into lxResult;
         close crMain;
     end;

   end if; -- if lvResult is null then

   if lbAddXmlRoot then
   	lxResult := xmlroot(lxResult, version '1.0', standalone yes);
   end if;

   pxXML := lxResult;
   pnRowCount := array_length(lxXMLArr, 1);
end;

declare
	CtxConvertSpecialChars constant text = 'ctxConvertSpecialChars';
	lCtx aws_oracle_ext.dbms_xmlgen$tContext;
	lnCtxHdl int := trunc(ctx);
begin
	lCtx := aws_oracle_ext.dbms_xmlgen$getctxobj(ctxHdl => lnCtxHdl);

    if not lCtx is null then
	lCtx.ctxAttributes := aws_oracle_ext.dbms_xmlgen$setCtxAttrValue(ctxAttributes => lCtx.ctxAttributes,
                                                                     forceParamList => array[case when replace is null then CtxConvertSpecialChars else null end],
                                                                     ctxConvertSpecialChars => replace
                                                                    );

    perform aws_oracle_ext.dbms_xmlgen$setctxvalue(ctxHdl => lCtx.ctxIdx,
                                                   ctxCursor => lCtx.ctxCursor,
                                                   ctxQuery => lCtx.ctxQuery,
                                                   ctxAttributes => lCtx.ctxAttributes,
                                                   ctxNumRowsProcessed => lCtx.ctxNumRowsProcessed,
                                                   forceSetValues => true
                                                  );
    end if;
end;

declare
	lforceParamList text[];
begin
	if ctxMaxRows < 0  then
		raise exception 'XML processing error. Nagative value [%] was readed', ctxMaxRows using hint = '22063';
	elsif ctxSkipRows < 0 then
		raise exception 'XML processing error. Nagative value [%] was readed', ctxSkipRows using hint = '22063';
	elsif ctxNullHandling < 0 then
		raise exception 'XML processing error. Nagative value [%] was readed', ctxNullHandling using hint = '22063';
	end if;

	lforceParamList := upper(forceParamList::text)::text[];

	ctxAttributes := (case when upper('ctxConvertSpecialChars') = any(lforceParamList) then ctxConvertSpecialChars else coalesce(ctxConvertSpecialChars, ctxAttributes.ctxConvertSpecialChars) end,
	                  case when upper('ctxMaxRows') = any(lforceParamList) then ctxMaxRows else coalesce(ctxMaxRows, ctxAttributes.ctxMaxRows) end,
                      case when upper('ctxNullHandling') = any(lforceParamList) then ctxNullHandling else coalesce(ctxNullHandling, ctxAttributes.ctxNullHandling) end,
	                  case when upper('ctxRowsetTag') = any(lforceParamList) then ctxRowsetTag else coalesce(ctxRowsetTag, ctxAttributes.ctxRowsetTag) end,
	                  case when upper('ctxRowTag') = any(lforceParamList) then ctxRowTag else coalesce(ctxRowTag, ctxAttributes.ctxRowTag) end,
	                  case when upper('ctxSkipRows') = any(lforceParamList) then ctxSkipRows else coalesce(ctxSkipRows, ctxAttributes.ctxSkipRows) end,
	                  case when upper('ctxUseNullattributeIndicator') = any(lforceParamList) then ctxUseNullattributeIndicator else coalesce(ctxUseNullattributeIndicator, ctxAttributes.ctxUseNullattributeIndicator) end,
	                  case when upper('ctxUseItemTagsForColl') = any(lforceParamList) then ctxUseItemTagsForColl else coalesce(ctxUseItemTagsForColl, ctxAttributes.ctxUseItemTagsForColl) end
                     )::dbms_xmlgen$TctxAttributes;
	lforceParamList := upper(forceParamList::text)::text[];

	ctxAttributes := (case when upper('ctxConvertSpecialChars') = any(lforceParamList) then ctxConvertSpecialChars else coalesce(ctxConvertSpecialChars, ctxAttributes.ctxConvertSpecialChars) end,
	                  case when upper('ctxMaxRows') = any(lforceParamList) then ctxMaxRows else coalesce(ctxMaxRows, ctxAttributes.ctxMaxRows) end,
                      case when upper('ctxNullHandling') = any(lforceParamList) then ctxNullHandling else coalesce(ctxNullHandling, ctxAttributes.ctxNullHandling) end,
	                  case when upper('ctxRowsetTag') = any(lforceParamList) then ctxRowsetTag else coalesce(ctxRowsetTag, ctxAttributes.ctxRowsetTag) end,
	                  case when upper('ctxRowTag') = any(lforceParamList) then ctxRowTag else coalesce(ctxRowTag, ctxAttributes.ctxRowTag) end,
	                  case when upper('ctxSkipRows') = any(lforceParamList) then ctxSkipRows else coalesce(ctxSkipRows, ctxAttributes.ctxSkipRows) end,
	                  case when upper('ctxUseNullattributeIndicator') = any(lforceParamList) then ctxUseNullattributeIndicator else coalesce(ctxUseNullattributeIndicator, ctxAttributes.ctxUseNullattributeIndicator) end,
	                  case when upper('ctxUseItemTagsForColl') = any(lforceParamList) then ctxUseItemTagsForColl else coalesce(ctxUseItemTagsForColl, ctxAttributes.ctxUseItemTagsForColl) end
                     )::dbms_xmlgen$TctxAttributes;
end;

declare
    CONTEXT_SEQUENCE constant text := 'dbms_xmlgen$context$sequence';
    lrCtxQuery record;
    lrCtx aws_oracle_ext.dbms_xmlgen$TContext;
    lvCtx text;
    lnIdx numeric := ctxHdl;
    lvCtxIdx varchar;
    lnCurrval int := 0;
begin
	if ctxHdl is null then
		lnCurrval := coalesce(nullif(aws_oracle_ext.get_extpack_variable(variable_name => CONTEXT_SEQUENCE, missing_ok => true), ''), '-1')::integer;
        begin
            lnCurrval := lnCurrval + 1;
            perform aws_oracle_ext.set_extpack_variable(variable_name => CONTEXT_SEQUENCE, variable_value => lnCurrval::text);
            select Idx, ctxIdx, aws_oracle_ext.dbms_xmlgen$context2text(Idx, ctxCursor, ctxQuery) ctx
              into lrCtxQuery
              from aws_oracle_ext.dbms_xmlgen$ctxHdl2uidRec(lnCurrval) as (Idx numeric, ctxIdx character varying);

            lnIdx := lrCtxQuery.Idx;
            lvCtxIdx := lrCtxQuery.ctxIdx;
            lvCtx := lrCtxQuery.ctx;
        end;
	elsif ctxHdl is not null and
	      ctxCursor is not null or
          ctxQuery is not null or
          ctxAttributes is not null or
          ctxNumRowsProcessed is not null then

	   if forceSetValues then
	       lrCtx.ctxIdx := ctxHdl;
		   lrCtx.ctxCursor := ctxCursor;
		   lrCtx.ctxQuery := ctxQuery;
		   lrCtx.ctxAttributes := ctxAttributes;
		   lrCtx.ctxNumRowsProcessed := ctxNumRowsProcessed;
	   else
	   	   lrCtx := aws_oracle_ext.dbms_xmlgen$getctxobj(ctxHdl => ctxHdl);

		   if not lrCtx is null then
		   	   lrCtx.ctxCursor := coalesce(ctxCursor, lrCtx.ctxCursor);
			   lrCtx.ctxQuery := coalesce(ctxQuery, lrCtx.ctxQuery);
			   lrCtx.ctxAttributes := coalesce(ctxAttributes, lrCtx.ctxAttributes);
			   lrCtx.ctxNumRowsProcessed := coalesce(ctxNumRowsProcessed, lrCtx.ctxNumRowsProcessed);
		   end if; -- if not lrCtx is null then
	   end if; --  if forceSetValues then

	   lvCtxIdx := aws_oracle_ext.dbms_xmlgen$ctxIdx(ctxHdl);
	   lvCtx := aws_oracle_ext.dbms_xmlgen$context2text(lrCtx);
	else
		lvCtxIdx := aws_oracle_ext.dbms_xmlgen$ctxIdx(ctxHdl);
		lvCtx := null;
	end if;

    perform aws_oracle_ext.set_extpack_variable(variable_name => lvCtxIdx, variable_value => lvCtx);

    return lnIdx;
end;

