                                            file_name      => fname, /* File name */
                                            error_on_empty => true);

begin  

  return aws_oracle_ext.get_extpack_variable(variable_name => 'TIME_ZONE', missing_ok => false); 

  exception 
     when internal_error then 
	   return coalesce(CURRENT_SETTING('TIMEZONE', TRUE), 'UTC');   
