BEGIN

/* DELETE removes all elements from a collection */
    RETURN '{}';

END;


DECLARE
    ind INTEGER;
    vArray parray%type;
BEGIN
/* DELETE(m,n) removes all elements in the range m..n from collection */

    IF (m > n) OR (n IS NULL) OR (m iS NULL) THEN

        vArray := parray;

    ELSE

        ind := ARRAY_LENGTH(parray, 1);
        vArray := parray[1:m - 1];

        FOR i IN 1..n - m + 1 LOOP

            vArray := ARRAY_APPEND(vArray, NULL);

        END LOOP;

        vArray := vArray || parray[n + 1:ind];

    END IF;

    RETURN vArray;

END;

DECLARE
    ind INTEGER;
BEGIN

/* DELETE(n) removes the nth element from collection */
    ind := ARRAY_LENGTH(parray, 1);

    RETURN
    CASE
        WHEN (n > ind) OR (n < 1) OR (n IS NULL) THEN
            parray
        ELSE
            ARRAY_APPEND(parray[1:n - 1], NULL)||parray[n + 1:ind]
    END;

END;

DECLARE result text; proname name; expr_hex bytea; BEGIN IF p_expr IS NULL THEN RETURN 'NULL'; END IF;
EXECUTE 'SELECT p.proname FROM pg_catalog.pg_proc p LEFT JOIN pg_catalog.pg_namespace n ON n.oid = p.pronamespace WHERE pg_catalog.pg_function_is_visible(p.oid) AND n.nspname = ''pg_catalog'' AND p.proname LIKE ''%send'' AND pg_catalog.pg_get_function_arguments(p.oid) = $1' INTO proname USING pg_typeof(p_expr)::text;
EXECUTE 'SELECT ' || proname || '($1)' INTO expr_hex USING p_expr;
SELECT ARRAY_TO_STRING(ARRAY_AGG(CONCAT(CASE WHEN p_fmt IN (8,1008) --OCT THEN aws_oracle_ext.to_oct(i)::text WHEN p_fmt IN (16,1016) --HEX THEN CASE WHEN SUBSTRING(v,1,1) = '0' THEN SUBSTRING(v,2) ELSE v END WHEN p_fmt IN (10,1010) --DEC THEN i::text WHEN p_fmt IN (17,1017) OR p_fmt < 0 --ASCII/HEX THEN CASE WHEN i BETWEEN 1 AND 31 THEN '^' || CHR(i+64) WHEN i BETWEEN 32 AND 126 THEN CHR(i) ELSE v END ELSE i::text --DEC END ,',') ) ,'') FROM (SELECT v, i, row_number() OVER (ORDER BY rn DESC) AS rn FROM (SELECT v, aws_oracle_ext.hex_to_int(v) AS i, row_number() OVER () AS rn FROM REGEXP_SPLIT_TO_TABLE(LTRIM(expr_hex::text, '\x') ,E'(?=(..)+$)') AS v) t ) t WHERE rn BETWEEN COALESCE(p_pos,1) AND COALESCE(p_pos,1) + COALESCE(p_len,rn) - 1 INTO result;
SELECT 'Typ=' || pg_typeof(p_expr)::oid || ' Len=' || pg_column_size(p_expr) || CASE WHEN p_fmt > 1000 THEN ' CharacterSet=' || (SELECT character_set_name FROM information_schema.character_sets) ELSE '' END || ': ' || RTRIM(result,',') INTO result;
RETURN result; END

SELECT EXISTS 
(
    SELECT
    FROM UNNEST(pcollection) WITH ORDINALITY AS t(v,o)
    WHERE o = n
);

BEGIN

    FOR idx IN 1..n::NUMERIC(10) LOOP

      parray := ARRAY_APPEND(parray, parray[ii::NUMERIC(10)]);

    END LOOP;

    RETURN parray;

END;

DECLARE
  l_context_type text;
  l_key text;
  l_interval text;
  l_signature text;
  l_value character varying;

begin
  perform aws_oracle_ext.init_context_struct();
  l_key := aws_oracle_ext.get_context_param('key');
  l_context_type := aws_oracle_ext.get_context_type(a_context_namespace);


  case upper(l_context_type) 
  	when 'ACCESSED LOCALLY' then 
  		begin
			select cl.attribute_value, cl.signature 
			  into strict l_value, l_signature
			from context_local cl
			where cl.context_namespace = $1
			  and cl.attribute_name = $2
		  	  and (cl.user_name = $3 or cl.user_name is null)
		  	  and (cl.cliend_id = $4 or coalesce(cl.cliend_id, '') = '')
		    limit 1;	  		
	  	end;
  	when 'ACCESSED GLOBALLY' then	  
  		begin		
			delete from aws_oracle_context.context_global 
			where backend_start <= now()::timestamp - (coalesce(aws_oracle_ext.get_context_param('auto_clean_interval'), '1 day'))::interval;

			select w.attribute_value, w.signature 
			into strict l_value, l_signature 
			from (
				select 
					cg.attribute_value, 
					cg.signature,
					row_number() over (order by cg.context_namespace, cg.attribute_name, cg.cliend_id) as row
				from aws_oracle_context.context_global cg 
				where cg.context_namespace = $1
				  and cg.attribute_name = $2
				  and (cg.user_name = $3 or cg.user_name is null)
				  and (cg.cliend_id = $4 or coalesce(cg.cliend_id, '') = '')
				) w  
		    where row = 1;
	  	end;  
  	else 
		-- INITIALIZED EXTERNALLY, INITIALIZED GLOBALLY  	
  		raise exception 'unsupported context type: %', l_context_type;
  end case;

  if l_signature <> crypt(l_value || ':' || l_key, l_signature) then  
	raise exception  'The signature for %.% is broken', $1, $2;
  end if;	  	

  return l_value;
  exception 
 	when no_data_found then return null; 
end;

declare 
  l_res jsonb;
begin
  execute 'show application_name' into strict l_res;
  return l_res;

  exception when others then 
	return jsonb_build_object('action'::text, 'init'::text);
end;

