    vResult VARCHAR DEFAULT NULL;
BEGIN

    IF pVal IS NOT NULL AND pVal <> '' THEN

        vResult := aws_oracle_ext.to_char_formatter
        (
            pVal => pVal
        );
    END IF;

    RETURN vResult;

END;

