DECLARE
   v_err_message TEXT;
   v_encoded_url TEXT;
   v_temp_char VARCHAR;
   v_char_reqexp VARCHAR;
   v_url_charset VARCHAR;
   v_ascii_val INTEGER;
   v_temp_charcode BYTEA;
BEGIN
   /* Requires functions to be installed in "aws_oracle_ext" schema:
      - get_charset_name.
    */

   -- Determining the set of unescapable characters
   v_char_reqexp := CASE
                       WHEN p_escape_reserved THEN '[0-9a-zA-Z\-_.!~*`()]+'
                       ELSE '[0-9a-zA-Z\-_.!~*`();/?:@&=+$,\[\]]+'
                    END;

   -- Determining the charset for character encoding
   v_url_charset := CASE
                       WHEN p_url_charset IS NULL THEN pg_client_encoding()
                       ELSE aws_oracle_ext.get_charset_name(p_url_charset)
                    END;

   -- Iterating over all characters in the string
   FOR i_charpos IN 1..char_length(coalesce(p_url, ''))
   LOOP
      v_temp_char := substr(p_url, i_charpos, 1);

      -- Skipping unescapable characters and convert others
      IF (v_temp_char ~ v_char_reqexp) THEN
         v_encoded_url := concat(v_encoded_url, v_temp_char);
      ELSE
         -- Encoding character to selected charset
         v_temp_charcode := convert_to(v_temp_char, v_url_charset);

         -- Casting each character byte to hex and escaping it with "%"
         FOR i_charcode IN 1..octet_length(v_temp_charcode)
         LOOP
            v_temp_char := upper(encode(substring(v_temp_charcode, i_charcode, 1), 'hex'));
            v_encoded_url := concat(v_encoded_url, '%', v_temp_char);
         END LOOP;
      END IF;
   END LOOP;

   RETURN v_encoded_url;
EXCEPTION
   WHEN untranslatable_character THEN
      GET STACKED DIAGNOSTICS v_err_message = MESSAGE_TEXT;

      RAISE USING MESSAGE := format('Failed to encode character "%s" to %s charset (Oracle %s).',
                                    v_temp_char,
                                    upper(v_url_charset),
                                    upper(p_url_charset)),
                  DETAIL := concat(v_err_message, '.'),
                  HINT := format('Try to use another charset (not %s) in order to encode mentioned character (symbol).',
                                 upper(p_url_charset));
END;

DECLARE
   v_err_message TEXT;
   v_char_code TEXT;
   v_char_byte BYTEA;
   v_url_charset VARCHAR;
   v_decoded_url BYTEA := '';
BEGIN
   /* Requires functions to be installed in "aws_oracle_ext" schema:
      - get_charset_name.
    */

   -- Determining the charset for character decoding
   v_url_charset := CASE
                       WHEN p_url_charset IS NULL THEN pg_client_encoding()
                       ELSE aws_oracle_ext.get_charset_name(p_url_charset)
                    END;

   -- Iterating over all significant characters in the string
   FOR v_char_code IN
   (SELECT unnest(regexp_matches(p_url, '(%..|.)', 'g')) url_char)
   LOOP
      -- Unescaping "%XX" characters
      v_char_byte := CASE
                        WHEN v_char_code !~ '%' THEN v_char_code::BYTEA
                        ELSE decode(substring(v_char_code, 2, 2), 'hex')
                     END;

      -- Concatenating into resulting byte string
      v_decoded_url := v_decoded_url || v_char_byte;
   END LOOP;

   RETURN convert_from(v_decoded_url, v_url_charset);
EXCEPTION
   WHEN character_not_in_repertoire THEN
      GET STACKED DIAGNOSTICS v_err_message = MESSAGE_TEXT;

      RAISE USING MESSAGE := format('Failed to decode url byte string "%s" to %s charset (Oracle %s).',
                                    v_decoded_url::TEXT,
                                    upper(v_url_charset),
                                    upper(p_url_charset)),
                  DETAIL := concat(v_err_message, '.'),
                  HINT := format('Try to use another charset (not %s) or correct initial url string.',
                                 upper(p_url_charset));
END;

DECLARE
    vstmt TEXT;
BEGIN

	vstmt := $1;

	SELECT pc.statement 
	INTO vstmt
	FROM pg_cursors pc
	WHERE pc.name = vstmt;

	OPEN $2 FOR EXECUTE vstmt;

	CLOSE $1;

END;

declare
    ILIMIT constant integer := 1024;
    PDELIMETER constant varchar(1) := '/';
    PROOT constant varchar(2) := '//';
    XVALUE constant varchar(7) := '/text()';
    XSCHEMA constant varchar(3) := 'xsi';
    XURI constant varchar(41) := 'http://www.w3.org/2001/XMLSchema-instance';

    mainData aws_oracle_ext.xmltype$txml2json[];
    dummyData aws_oracle_ext.xmltype$txml2json[];
    lnMx int;
    lnMn int;
begin

	if pXML is null or pTargetJObj is null then
	    raise exception 'XMLTYPE.Null Exception.' using hint = '29532';
	end if;

	with recursive rStructure (iNum, rNum, pNum, recFullKey, recKey, recValue, recValType)
	               as (select 0 as iNum,
		                      row_number() over () as rNum,
		                      0::bigint as pNum,
		                      t.key as recFullKey,
		                      t.key as recKey,
		                      t.value as recValue,
		                      json_typeof(t.value) as recValType
		                 from json_each(pTargetJObj) t
		                union all
		               select r.iNum + 1 as iNum,
		                       row_number() over () as rNum,
		                       r.rNum as pNum,
		                       r.recFullKey||PDELIMETER||t.key as recFullKey,
		                       t.key as recKey,
		                       t.value as recValue,
		                       json_typeof(t.value) as recValType
		                  from rStructure r left join json_each(r.recValue) t on r.recValue is not null
		                 where r.recValType = json_typeof(pTargetJObj)
		                   and r.iNum < ILIMIT
		               ),
				   rMatchData (iNum, rNum, pNum, recFullKey, recKey, recValue, recValType)
				   as (select r.iNum as iNum,
			              	  r.rNum as rNum,
			              	  r.pNum as pNum,
			              	  r.recFullKey as recFullKey,
			              	  r.recKey as recKey,
			              	  to_json(case when r.recValType != json_typeof(pTargetJObj) then
								              coalesce((xpath(PROOT||r.recFullKey||XVALUE, pXML, array[array[XSCHEMA, XURI]]))[1]::text,
								                       (xpath(PROOT||r.recFullKey, pXML, array[array[XSCHEMA, XURI]]))[1]::text
						                     		  )
					                       else null end
					                  ) as recValue,
			                  r.recValType as recValType
			             from rStructure r
			            where r.recFullKey is not null
			          )
    select array_agg(row(d.iNum, d.rNum, d.pNum, d.recKey, d.recValue)::aws_oracle_ext.xmltype$txml2json)
      into mainData
      from rMatchData d;

    select max(iNum), min(iNum)
      into lnMx, lnMn
      from unnest(mainData);

    select array_agg(row(d.iNum, null, d.pNum, null, d.recValue)::aws_oracle_ext.xmltype$txml2json)
      into dummyData
      from (select d1.iNum, d1.pNum, json_object_agg(d1.recKey, d1.recValue) recValue
              from unnest(mainData) d1
             where d1.iNum = lnMx
             group by d1.iNum, d1.pNum
           ) d;

    for i in reverse lnMx -1 .. lnMn loop
		 select array_agg(row(d.iNum, null, d.pNum, null, d.recValue)::aws_oracle_ext.xmltype$txml2json)
		   into dummyData
		   from (select d1.iNum, d1.pNum, json_object_agg(d1.recKey, coalesce(d1.recValue, g1.recValue)) recValue
				   from unnest(mainData) d1 left join unnest(dummyData) g1 on d1.iNum = g1.iNum -1 and d1.rNum = g1.pNum
				  where d1.iNum = i
				  group by d1.iNum, d1.pNum
		        ) d;
	end loop;

    return dummyData[1].recValue;
end;

BEGIN
    REFRESH MATERIALIZED VIEW aws_oracle_ext.sys_dba_directories;

    RETURN NULL;
END

procedure.complex-attribute:
BEGIN
    -- Checking input arguments
    IF (dest_lob IS NULL OR
        src_lob IS NULL)
    THEN
        -- Invalid LOB locator specified
        RAISE null_value_not_allowed;
    END IF;

    dest_lob := dest_lob || src_lob;
EXCEPTION
    WHEN null_value_not_allowed THEN
        RAISE USING MESSAGE := 'Invalid LOB locator specified.',
                    DETAIL := 'LOB locator variable is not initialized.',
                    HINT := '-22275';
END;

DECLARE
    v_dest_lob TEXT;
BEGIN
    v_dest_lob := dest_lob;

    CALL aws_oracle_ext.dbms_lob$append(dest_lob => v_dest_lob,
                                        src_lob => src_lob);
    dest_lob := v_dest_lob;
END;

BEGIN
    -- Checking input arguments
    IF (dest_lob IS NULL OR
        src_lob IS NULL)
    THEN
        -- Invalid LOB locator specified
        RAISE null_value_not_allowed;
    END IF;

    dest_lob := dest_lob || src_lob;
EXCEPTION
    WHEN null_value_not_allowed THEN
        RAISE USING MESSAGE := 'Invalid LOB locator specified.',
                    DETAIL := 'LOB locator variable is not initialized.',
                    HINT := '-22275';
END;

DECLARE v_dest_before BYTEA; v_src_part BYTEA; v_dest_after BYTEA; v_amount NUMERIC; BEGIN -- Checking input arguments IF (dest_lob IS NULL OR src_lob IS NULL) THEN -- Invalid LOB locator specified RAISE null_value_not_allowed; ELSIF (amount IS NULL OR dest_offset IS NULL OR src_offset IS NULL) THEN -- Numeric or value error RAISE zero_length_character_string; ELSIF (amount < 1 OR dest_offset < 1 OR src_offset < 1) THEN -- Argument is out of range RAISE check_violation; END IF;
-- NUMERIC -> INTEGER DBMS_LOB.LOBMAXSIZE workaround v_amount := CASE amount WHEN aws_oracle_ext.dbms_lob$lobmaxsize() THEN 2147483647 -- Maximum positive value for data type INTEGER ELSE amount END;
-- Extracting left part of the object v_dest_before := substr(dest_lob, 1, floor(dest_offset)::INTEGER - 1);
-- Extracting the substring from the source object v_src_part := substr(src_lob, floor(src_offset)::INTEGER, floor(v_amount)::INTEGER);
-- Extracting right part of the object v_dest_after := substr(dest_lob, octet_length(v_src_part) + octet_length(v_dest_before) + 1);
-- Constructing the resulting object dest_lob := v_dest_before || v_src_part || v_dest_after; EXCEPTION WHEN null_value_not_allowed THEN RAISE USING MESSAGE := 'Invalid LOB locator specified.', DETAIL := 'LOB locator variable is not initialized.', HINT := '-22275'; WHEN zero_length_character_string THEN RAISE USING MESSAGE := 'Numeric or value error.', DETAIL := 'Amount of bytes to copy, source and destination offset could not be NULL.', HINT := '-6502'; WHEN check_violation THEN RAISE USING MESSAGE := format('Argument %s is invalid or out of range.', CASE WHEN amount < 1 THEN 3 WHEN dest_offset < 1 THEN 4 WHEN src_offset < 1 THEN 5 END), DETAIL := 'Amount of bytes to copy, source and destination offset must be 1 or above.', HINT := '-21560'; END;

DECLARE
    v_dest_lob TEXT;
BEGIN
    v_dest_lob := dest_lob;

    CALL aws_oracle_ext.dbms_lob$copy(dest_lob => v_dest_lob,
                                      src_lob => src_lob,
                                      amount => amount,
                                      dest_offset => dest_offset,
                                      src_offset => src_offset);
    dest_lob := v_dest_lob;
END;

DECLARE v_dest_before TEXT; v_src_part TEXT; v_dest_after TEXT; v_amount NUMERIC; BEGIN -- Checking input arguments IF (dest_lob IS NULL OR src_lob IS NULL) THEN -- Invalid LOB locator specified RAISE null_value_not_allowed; ELSIF (amount IS NULL OR dest_offset IS NULL OR src_offset IS NULL) THEN -- Numeric or value error RAISE zero_length_character_string; ELSIF (amount < 1 OR dest_offset < 1 OR src_offset < 1) THEN -- Argument is out of range RAISE check_violation; END IF;
-- NUMERIC -> INTEGER DBMS_LOB.LOBMAXSIZE workaround v_amount := CASE amount WHEN aws_oracle_ext.dbms_lob$lobmaxsize() THEN 2147483647 -- Maximum positive value for data type INTEGER ELSE amount END;
-- Extracting left part of the object v_dest_before := substr(dest_lob, 1, floor(dest_offset)::INTEGER - 1);
-- Extracting the substring from the source object v_src_part := substr(src_lob, floor(src_offset)::INTEGER, floor(v_amount)::INTEGER);
-- Extracting right part of the object v_dest_after := substr(dest_lob, char_length(v_src_part) + char_length(v_dest_before) + 1);
-- Constructing the resulting object dest_lob := v_dest_before || v_src_part || v_dest_after; EXCEPTION WHEN null_value_not_allowed THEN RAISE USING MESSAGE := 'Invalid LOB locator specified.', DETAIL := 'LOB locator variable is not initialized.', HINT := '-22275'; WHEN zero_length_character_string THEN RAISE USING MESSAGE := 'Numeric or value error.', DETAIL := 'Amount of characters to copy, source and destination offset could not be NULL.', HINT := '-6502'; WHEN check_violation THEN RAISE USING MESSAGE := format('Argument %s is invalid or out of range.', CASE WHEN amount < 1 THEN 3 WHEN dest_offset < 1 THEN 4 WHEN src_offset < 1 THEN 5 END), DETAIL := 'Amount of characters to copy, source and destination offset must be 1 or above.', HINT := '-21560'; END;

BEGIN
    lob_loc := '\x'::BYTEA;
END;

BEGIN
    lob_loc := NULL::VARCHAR;
END;

BEGIN
    lob_loc := ''::TEXT;
END;

DECLARE
    v_amount NUMERIC;
BEGIN
    v_amount := amount;

    CALL aws_oracle_ext.dbms_lob$erase(lob_loc => lob_loc,
                                       amount => v_amount,
                                       "offset" => "offset");
    amount := v_amount;
END;

