DECLARE v_amount INTEGER; v_offset INTEGER; v_lob_len INTEGER; BEGIN -- Checking input arguments IF (lob_loc IS NULL) THEN -- Invalid LOB locator specified RAISE null_value_not_allowed; ELSIF (amount IS NULL OR "offset" IS NULL) THEN -- Numeric or value error RAISE zero_length_character_string; ELSIF (amount < 1 OR "offset" < 1) THEN -- Argument is out of range RAISE check_violation; END IF;
-- Determining object length (bytes) v_lob_len := octet_length(lob_loc);
-- Extracting integer part of offset v_offset := floor("offset")::INTEGER;
-- Make sense only if offset lies within the object IF (v_lob_len >= v_offset) THEN -- Determining actual amount of bytes to erase amount := least(v_lob_len - v_offset + 1, amount);
-- NUMERIC -> INTEGER DBMS_LOB.LOBMAXSIZE workaround v_amount := CASE amount WHEN aws_oracle_ext.dbms_lob$lobmaxsize() THEN 2147483647 -- Maximum positive value for data type INTEGER ELSE floor(amount) -- Extracting integer part of amount END;
-- Erasing part of the object at the specified offset lob_loc := overlay(lob_loc placing ('\x' || repeat('00', v_amount))::BYTEA -- replacing gap with zero-bytes from v_offset for v_amount); END IF; EXCEPTION WHEN null_value_not_allowed THEN RAISE USING MESSAGE := 'Invalid LOB locator specified.', DETAIL := 'LOB locator variable is not initialized.', HINT := '-22275'; WHEN zero_length_character_string THEN RAISE USING MESSAGE := 'Numeric or value error.', DETAIL := 'Amount of bytes to erase and offset could not be NULL.', HINT := '-6502'; WHEN check_violation THEN RAISE USING MESSAGE := format('Argument %s is invalid or out of range.', CASE WHEN amount < 1 THEN 2 WHEN "offset" < 1 THEN 3 END), DETAIL := 'Amount of bytes to erase and offset must be 1 or above.', HINT := '-21560'; END;

