DECLARE
    v_offset INTEGER;
    v_lob_len INTEGER;
BEGIN
    -- Extracting integer part of amount
    amount := floor(amount);

    -- Checking input arguments
    IF (lob_loc IS NULL)
    THEN
        -- Invalid LOB locator specified
        RAISE null_value_not_allowed;
    ELSIF (amount IS NULL OR
           "offset" IS NULL)
    THEN
        -- Numeric or value error
        RAISE zero_length_character_string;
    ELSIF (amount NOT BETWEEN 1 AND 32767 OR
           "offset" < 1)
    THEN
        -- Argument is out of range
        RAISE check_violation;
    END IF;

    -- Determining object length (characters)
    v_lob_len := char_length(lob_loc);

    -- Extracting integer part of offset
    v_offset := floor("offset")::INTEGER;

    -- Determining actual amount of characters to read
    amount := least(v_lob_len - v_offset + 1, amount);

    -- Make sense only if offset lies within the object
    IF (v_lob_len >= v_offset)
    THEN
        -- Reading a specified part of an object
        buffer := substr(lob_loc, v_offset, amount::INTEGER);
    ELSE
        -- Nothing to read
        RAISE no_data_found;
    END IF;
EXCEPTION
    WHEN null_value_not_allowed THEN
        RAISE USING MESSAGE := 'Invalid LOB locator specified.',
                    DETAIL := 'LOB locator variable is not initialized.',
                    HINT := '-22275';
    WHEN zero_length_character_string THEN
        RAISE USING MESSAGE := 'Numeric or value error.',
                    DETAIL := 'Amount of characters to read and offset could not be NULL.',
                    HINT := '-6502';
    WHEN check_violation THEN
        RAISE USING MESSAGE := format('Argument %s is invalid or out of range.',
                                      CASE
                                         WHEN (amount NOT BETWEEN 1 AND 32767) THEN 2
                                         WHEN "offset" < 1 THEN 3
                                      END),
                    DETAIL := 'Amount of characters to read and offset must be 1 or above.',
                    HINT := '-21560';
END;

DECLARE
    v_lob_len INTEGER;
BEGIN
    -- Determining object length (characters)
    v_lob_len := octet_length(lob_loc);

    -- Checking input arguments
    IF (lob_loc IS NULL)
    THEN
        -- Invalid LOB locator specified
        RAISE null_value_not_allowed;
    ELSIF (newlen > v_lob_len AND
           v_lob_len >= 0)
    THEN
        -- Trim length is greater than LOB length
        RAISE check_violation;
    END IF;

    lob_loc := substr(lob_loc, 1, floor(newlen)::INTEGER);
EXCEPTION
    WHEN null_value_not_allowed THEN
        RAISE USING MESSAGE := 'Invalid LOB locator specified.',
                    DETAIL := 'LOB locator variable is not initialized.',
                    HINT := '-22275';
    WHEN check_violation THEN
        RAISE USING MESSAGE := 'Trim length is greater than LOB length.',
                    DETAIL := 'Specified trim length is greater than current LOB value''s length.',
                    HINT := '-22926';
END;

DECLARE
    v_lob_loc TEXT;
BEGIN
    v_lob_loc := lob_loc;

    CALL aws_oracle_ext.dbms_lob$trim(lob_loc => v_lob_loc,
                                      newlen => newlen);
    lob_loc := v_lob_loc;
END;

DECLARE
    v_lob_len INTEGER;
BEGIN
    -- Determining object length (characters)
    v_lob_len := char_length(lob_loc);

    -- Checking input arguments
    IF (lob_loc IS NULL)
    THEN
        -- Invalid LOB locator specified
        RAISE null_value_not_allowed;
    ELSIF (newlen > v_lob_len AND
           v_lob_len >= 0)
    THEN
        -- Trim length is greater than LOB length
        RAISE check_violation;
    END IF;

    lob_loc := substr(lob_loc, 1, floor(newlen)::INTEGER);
EXCEPTION
    WHEN null_value_not_allowed THEN
        RAISE USING MESSAGE := 'Invalid LOB locator specified.',
                    DETAIL := 'LOB locator variable is not initialized.',
                    HINT := '-22275';
    WHEN check_violation THEN
        RAISE USING MESSAGE := 'Trim length is greater than LOB length.',
                    DETAIL := 'Specified trim length is greater than current LOB value''s length.',
                    HINT := '-22926';
END;

DECLARE v_amount INTEGER; v_buff_len INTEGER; BEGIN -- Determining buffer length (bytes) v_buff_len := octet_length(buffer);
-- Checking input arguments IF (lob_loc IS NULL) THEN -- Invalid LOB locator specified RAISE null_value_not_allowed; ELSIF (amount IS NULL OR "offset" IS NULL OR buffer IS NULL OR v_buff_len = 0) THEN -- Numeric or value error RAISE zero_length_character_string; ELSIF (amount < 1 OR "offset" < 1 OR amount > v_buff_len) THEN -- Argument is out of range RAISE check_violation; END IF;
-- NUMERIC -> INTEGER DBMS_LOB.LOBMAXSIZE workaround v_amount := CASE amount WHEN aws_oracle_ext.dbms_lob$lobmaxsize() THEN 2147483647 -- Maximum positive value for data type INTEGER ELSE amount END;
-- Writing part of the buffer over the source object lob_loc := overlay(lob_loc placing substr(buffer, 1, v_amount) from floor("offset")::INTEGER for v_amount); EXCEPTION WHEN null_value_not_allowed THEN RAISE USING MESSAGE := 'Invalid LOB locator specified.', DETAIL := 'LOB locator variable is not initialized.', HINT := '-22275'; WHEN zero_length_character_string THEN RAISE USING MESSAGE := 'Numeric or value error.', DETAIL := 'Binary data buffer could not be NULL or empty.', HINT := '-6502'; WHEN check_violation THEN RAISE USING MESSAGE := format('Argument %s is invalid or out of range.', CASE WHEN (amount < 1 OR amount > v_buff_len) THEN 2 WHEN "offset" < 1 THEN 3 END), DETAIL := 'Amount of bytes to write and offset must be 1 or above.', HINT := '-21560'; END;

DECLARE
    v_lob_loc TEXT;
BEGIN
    v_lob_loc := lob_loc;

    CALL aws_oracle_ext.dbms_lob$write(lob_loc => v_lob_loc,
                                       amount => amount,
                                       "offset" => "offset",
                                       buffer => buffer);
    lob_loc := v_lob_loc;
END;

DECLARE v_amount INTEGER; v_buff_len INTEGER; BEGIN -- Determining buffer length (characters) v_buff_len := char_length(buffer);
-- Checking input arguments IF (lob_loc IS NULL) THEN -- Invalid LOB locator specified RAISE null_value_not_allowed; ELSIF (amount IS NULL OR "offset" IS NULL OR buffer IS NULL OR v_buff_len = 0) THEN -- Numeric or value error RAISE zero_length_character_string; ELSIF (amount < 1 OR "offset" < 1 OR amount > v_buff_len) THEN -- Argument is out of range RAISE check_violation; END IF;
-- NUMERIC -> INTEGER DBMS_LOB.LOBMAXSIZE workaround v_amount := CASE amount WHEN aws_oracle_ext.dbms_lob$lobmaxsize() THEN 2147483647 -- Maximum positive value for data type INTEGER ELSE amount END;
-- Writing part of the buffer over the source object lob_loc := overlay(lob_loc placing substr(buffer, 1, v_amount) from floor("offset")::INTEGER for v_amount); EXCEPTION WHEN null_value_not_allowed THEN RAISE USING MESSAGE := 'Invalid LOB locator specified.', DETAIL := 'LOB locator variable is not initialized.', HINT := '-22275'; WHEN zero_length_character_string THEN RAISE USING MESSAGE := 'Numeric or value error.', DETAIL := 'Character data buffer could not be NULL or empty.', HINT := '-6502'; WHEN check_violation THEN RAISE USING MESSAGE := format('Argument %s is invalid or out of range.', CASE WHEN (amount < 1 OR amount > v_buff_len) THEN 2 WHEN "offset" < 1 THEN 3 END), DETAIL := 'Amount of characters to write and offset must be 1 or above.', HINT := '-21560'; END;

DECLARE
    v_amount INTEGER;
    v_buff_len INTEGER;
BEGIN
    -- Determining buffer length (bytes)
    v_buff_len := octet_length(buffer);

    -- Checking input argument
    IF (lob_loc IS NULL)
    THEN
        -- Invalid LOB locator specified
        RAISE null_value_not_allowed;
    ELSIF (buffer IS NULL OR
           v_buff_len = 0)
    THEN
        -- Numeric or value error
        RAISE zero_length_character_string;
    ELSIF (amount < 1 OR
           amount > v_buff_len)
    THEN
        -- Argument is out of range
        RAISE check_violation;
    END IF;

    -- NUMERIC -> INTEGER DBMS_LOB.LOBMAXSIZE workaround
    v_amount := CASE amount
                   WHEN aws_oracle_ext.dbms_lob$lobmaxsize()
                   THEN 2147483647 -- Maximum positive value for data type INTEGER
                   ELSE amount
                END;

    lob_loc := lob_loc || substr(buffer, 1, v_amount);
EXCEPTION
    WHEN null_value_not_allowed THEN
        RAISE USING MESSAGE := 'Invalid LOB locator specified.',
                    DETAIL := 'LOB locator variable is not initialized.',
                    HINT := '-22275';
    WHEN zero_length_character_string THEN
        RAISE USING MESSAGE := 'Numeric or value error.',
                    DETAIL := 'Binary data buffer could not be NULL or empty.',
                    HINT := '-6502';
    WHEN check_violation THEN
        RAISE USING MESSAGE := 'Argument 2 is invalid or out of range.',
                    DETAIL := 'Amount of bytes to write must be 1 or above.',
                    HINT := '-21560';
END;

DECLARE
    v_lob_loc TEXT;
BEGIN
    v_lob_loc := lob_loc;

    CALL aws_oracle_ext.dbms_lob$writeappend(lob_loc => v_lob_loc,
                                             amount => amount,
                                             buffer => buffer);
    lob_loc := v_lob_loc;
END;

DECLARE
    v_amount INTEGER;
    v_buff_len INTEGER;
BEGIN
    -- Determining buffer length (characters)
    v_buff_len := char_length(buffer);

    -- Checking input argument
    IF (lob_loc IS NULL)
    THEN
        -- Invalid LOB locator specified
        RAISE null_value_not_allowed;
    ELSIF (buffer IS NULL OR
           v_buff_len = 0)
    THEN
        -- Numeric or value error
        RAISE zero_length_character_string;
    ELSIF (amount < 1 OR
           amount > v_buff_len)
    THEN
        -- Argument is out of range
        RAISE check_violation;
    END IF;

    -- NUMERIC -> INTEGER DBMS_LOB.LOBMAXSIZE workaround
    v_amount := CASE amount
                   WHEN aws_oracle_ext.dbms_lob$lobmaxsize()
                   THEN 2147483647 -- Maximum positive value for data type INTEGER
                   ELSE amount
                END;

    lob_loc := lob_loc || substr(buffer, 1, v_amount);
EXCEPTION
    WHEN null_value_not_allowed THEN
        RAISE USING MESSAGE := 'Invalid LOB locator specified.',
                    DETAIL := 'LOB locator variable is not initialized.',
                    HINT := '-22275';
    WHEN zero_length_character_string THEN
        RAISE USING MESSAGE := 'Numeric or value error.',
                    DETAIL := 'Character data buffer could not be NULL or empty.',
                    HINT := '-6502';
    WHEN check_violation THEN
        RAISE USING MESSAGE := 'Argument 2 is invalid or out of range.',
                    DETAIL := 'Amount of characters to write must be 1 or above.',
                    HINT := '-21560';
END;

