    v_line TEXT;
    v_status NUMERIC;
BEGIN
    CALL aws_oracle_ext.dbms_output$get_line(line => v_line,
                                             status => v_status);
    -- Character string value (line).
    line := v_line;

    -- Output buffer emptiness flag.
    status := v_status;
END;

