DECLARE v_file_data TEXT; v_bucket_name TEXT; v_bucket_path TEXT; v_aws_region VARCHAR(50); v_err_message TEXT; BEGIN IF (file_name ~ '^\s*$') THEN -- ORA-22288: file or LOB operation FILEOPEN failed RAISE USING MESSAGE := 'File or LOB operation failed.', DETAIL := 'Make sure the file name is correct and that the file actually exists.', HINT := '-22288'; ELSIF (file_name IS NULL) THEN -- ORA-06502: PL/SQL: numeric or value error RAISE USING MESSAGE := 'Numeric or value error.', DETAIL := 'The file name is null. Correct the file name and make sure that the file actually exists.', HINT := '-6502'; END IF;
SELECT s3_bucket_name, s3_bucket_path, aws_region INTO STRICT v_bucket_name, v_bucket_path, v_aws_region FROM aws_oracle_ext.sys_dba_directories drs WHERE drs.directory_name = put_text_file_on_s3.directory_name;
-- Temp table for exporting the text file data from CREATE TEMP TABLE aws_s3_file_data$f8e6cd5394ee3e ON COMMIT DROP AS SELECT text_data AS file_data;
-- Inserting the file into a given table and uploading it to the S3 bucket PERFORM 1 FROM aws_s3.query_export_to_s3(query => 'SELECT file_data FROM aws_s3_file_data$f8e6cd5394ee3e', bucket => v_bucket_name, file_path => concat_ws('/', v_bucket_path, file_name), region => v_aws_region, options => 'NULL '''''); EXCEPTION WHEN no_data_found THEN -- ORA-29280: invalid directory object RAISE USING MESSAGE := 'Invalid directory object.', DETAIL := 'The directory object passed in does not exist in the database. Check that the name is correct and create a directory object if necessary.', HINT := '-29280'; WHEN OTHERS THEN GET STACKED DIAGNOSTICS v_err_message = MESSAGE_TEXT;
IF (v_err_message ~* 'credentials stored with the database') THEN -- IAM role containing the s3Import feature error RAISE USING MESSAGE := 'The IAM role containing the s3Export feature is not associated with the DB instance.', DETAIL := 'The credentials stored with the database instance can''t be accessed.', HINT := 'Make sure that the desired Amazon Resource Name (ARN) is associated with the feature-name: s3Export.'; ELSE RAISE; END IF; END;


declare
  l_stack text;
  l_object text;
  l_line integer;
begin
 get diagnostics l_stack = pg_context;

  select line[1], line[2]
    into l_object, l_line
    from regexp_matches(l_stack,'PL/pgSQL function (.*?)line (.*?) ', 'g') as stack_lines(line)
   limit 1 offset 1;

  if l_object is null then
  select line[1], line[2]
    into l_object, l_line
    from regexp_matches( l_stack, 'SQL function (\S+) statement (\d+)', 'g') as stack_lines(line)
    limit 1;
  end if;
  raise exception 
    using message = format('%I at line %s, AI %s [%s]: %s', l_object, l_line, p_ai_code, p_severity, p_message);
end;

trigger.complex-attribute:
FOR EACH ROW
when (New.CUSTOMER_ID is null)
EXECUTE PROCEDURE C##CO.customers$tr_fn_ind_on_null();

FOR EACH ROW
when (New.INVENTORY_ID is null)
EXECUTE PROCEDURE C##CO.inventory$tr_fn_ind_on_null();

FOR EACH ROW
when (New.ORDER_ID is null)
EXECUTE PROCEDURE C##CO.orders$tr_fn_ind_on_null();

FOR EACH ROW
when (New.PRODUCT_ID is null)
EXECUTE PROCEDURE C##CO.products$tr_fn_ind_on_null();

FOR EACH ROW
when (New.SHIPMENT_ID is null)
EXECUTE PROCEDURE C##CO.shipments$tr_fn_ind_on_null();

FOR EACH ROW
when (New.STORE_ID is null)
EXECUTE PROCEDURE C##CO.stores$tr_fn_ind_on_null();

