   New.inventory_id := nextval('seq_inventory$def_on_null');
   RETURN NEW;
END;

BEGIN
   New.order_id := nextval('seq_orders$def_on_null');
   RETURN NEW;
END;

BEGIN
   New.product_id := nextval('seq_products$def_on_null');
   RETURN NEW;
